									<div style={{ display: 'flex', alignItems: 'center', gap: 18 }}>
										<div style={{ width: 96, height: 96, borderRadius: 12, overflow: 'hidden', background: '#fff', display: 'flex', alignItems: 'center', justifyContent: 'center', position: 'relative' }}>
											{residentImageSrc ? (
