              }}>{cat.label}</span>,
              children: (
                <>
                  <List
                    dataSource={filtered}
                    loading={loading}
                    locale={{
                      emptyText: <Empty
                        image={<BellOutlined style={{ fontSize: 48, color: '#d9d9d9' }} />}
                        description={<span style={{ color: '#888' }}>No notifications</span>}
                      />
                    }}
                    renderItem={renderItem}
                    itemLayout="horizontal"
                    style={{ marginTop: 8, minHeight: 120 }}
