export default NotificationDropdown;

import React, { useState, useEffect } from 'react';
import './Profile.css';
import { Button, Form, Input, message, Upload, Alert, Modal } from 'antd';
import { UserOutlined, MailOutlined, HomeOutlined, PhoneOutlined, UploadOutlined } from '@ant-design/icons';
import AvatarImage from './AvatarImage';
import { getAbsoluteApiUrl } from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import { axiosInstance } from '../services/api';
import dayjs from 'dayjs';

interface ProfileProps {
  profile: any | null;
  onProfileUpdate: (profile: any) => void;
}

const Profile: React.FC<ProfileProps> = ({ profile, onProfileUpdate }) => {
  const [userForm] = Form.useForm();
  const [residentForm] = Form.useForm();
  const [avatarPreview, setAvatarPreview] = useState<string | null>(null);
  const [editMode, setEditMode] = useState(false);
  const [requesting, setRequesting] = useState(false);
  const [residentMissing, setResidentMissing] = useState(false);
  const [pwdModalVisible, setPwdModalVisible] = useState(false);
  const [pwdLoading, setPwdLoading] = useState(false);
  const [changeForm] = Form.useForm();
  const { setUser } = useAuth();

  const autoCreateResident = async () => {
    try {
      // derive basic fields from userForm (if available)
      const values = userForm.getFieldsValue();
      const parts = (values.fullName || values.username || '').toString().trim().split(' ');
      const firstName = parts.length ? parts[0] : (values.username || values.email || 'Resident');
      const lastName = parts.length > 1 ? parts.slice(1).join(' ') : '';
      const payload: any = {
        firstName,
        lastName,
        barangayID: values.barangayID || '',
        username: values.username || '',
        email: values.email || ''
      };
      const resp = await axiosInstance.put('/resident/personal-info', payload);
      residentForm.setFieldsValue(resp.data || {});
      setResidentMissing(false);
      message.success('Resident profile created');
    } catch (err) {
      console.error('Failed to auto-create resident:', err);
      message.error('Failed to create resident info. Please fill the form manually.');
    }
  };

  useEffect(() => {
    axiosInstance.get('/resident/profile')
      .then(res => {
        userForm.setFieldsValue(res.data || {});
        if (res.data?.profileImage) {
          const url = res.data.profileImage.startsWith('http') ? res.data.profileImage : getAbsoluteApiUrl(res.data.profileImage);
          setAvatarPreview(url);
        }
      })
      .catch(() => {});

    axiosInstance.get('/resident/personal-info')
      .then(res => {
        residentForm.setFieldsValue(res.data || {});
        if (res.data?.profileImage) {
          const url = res.data.profileImage.startsWith('http') ? res.data.profileImage : getAbsoluteApiUrl(res.data.profileImage);
          setAvatarPreview(url);
        }
      })
      .catch((err) => {
        if (err?.response && err.response.status === 404) {
          setResidentMissing(true);
          residentForm.setFieldsValue({});
        }
      });
  }, [userForm, residentForm]);

  const handleUserSave = async () => {
    try {
      const values = await userForm.validateFields();
      const resp = await axiosInstance.put('/resident/profile', values);
      // If server returned an updated user profile, update auth context
      const returned = resp?.data || null;
      if (returned) {
        // prefer returned.user or returned.profile, otherwise use returned itself
        const returnedUser = returned.user || returned.profile || (returned?.userProfile ? returned.userProfile : (returned?.username ? returned : null));
        if (returnedUser && typeof setUser === 'function') setUser(returnedUser);
      }
      onProfileUpdate(values);
      message.success('User info updated');
      setEditMode(false);
    } catch (err) {
      message.error('Failed to update user info');
    }
  };

  const handleResidentSave = async () => {
    try {
      const values = await residentForm.validateFields();
      await axiosInstance.put('/resident/personal-info', values);
      message.success('Resident info updated');
    } catch (err) {
      message.error('Failed to update resident info');
    }
  };

  const handleAvatarChange = async (file: File | null) => {
    if (!file) return;
    const reader = new FileReader();
    reader.onload = e => setAvatarPreview(e.target?.result as string);
    reader.readAsDataURL(file);

    const form = new FormData();
    form.append('avatar', file);
    try {
      const resp = await axiosInstance.post('/resident/personal-info/avatar', form, { headers: { 'Content-Type': 'multipart/form-data' } });
      const updated = resp.data?.resident;
      const returnedUser = resp.data?.user;
      if (updated) {
        residentForm.setFieldsValue(updated || {});
        if (updated.profileImage) {
          const url = updated.profileImage.startsWith('http') ? updated.profileImage : `${getAbsoluteApiUrl(updated.profileImage)}?t=${Date.now()}`;
          setAvatarPreview(url);
        }
        setResidentMissing(false);
      }
      if (returnedUser) {
        try {
          // update auth context immediately so UI reflects new avatar
          if (typeof setUser === 'function') setUser(returnedUser);
          else localStorage.setItem('userProfile', JSON.stringify(returnedUser));
          try {
            const ev = new CustomEvent('userProfileUpdated', { detail: returnedUser });
            window.dispatchEvent(ev);
          } catch (err) {}
        } catch (err) {}
      }
      message.success('Profile image updated');
    } catch (err) {
      message.error('Failed to upload avatar');
    }
  };

  // ...existing code...

  const handleRequestStaff = async () => {
    setRequesting(true);
    try {
      const resp = await axiosInstance.post('/resident/request-staff-access');
      message.success(resp.data?.message || 'Request sent');
    } catch (err) {
      message.error('Failed to send request');
    }
    setRequesting(false);
  };

  return (
    <div className="profile-page">
      <div className="profile-header" style={{ background: '#fff', borderRadius: 8, padding: 24, marginBottom: 24 }}>
        <div style={{ display: 'flex', gap: 16, alignItems: 'center' }}>
          <div className="profile-avatar" style={{ overflow: 'hidden', background: '#f0f0f0' }}>
            {avatarPreview ? (
              <img src={avatarPreview} alt="avatar" style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
            ) : (
              <AvatarImage user={(() => {
                let displayUser = profile;
                if (!displayUser) {
                  try {
                    const stored = localStorage.getItem('userProfile');
                    if (stored) displayUser = JSON.parse(stored);
                  } catch (err) {}
                }
                return displayUser;
              })()} size={72} />
            )}
          </div>
          <div style={{ flex: 1 }}>
            <Form className="profile-info" form={userForm} layout="vertical" disabled={!editMode}>
              <Form.Item name="username" label="Username">
                <Input prefix={<UserOutlined />} />
              </Form.Item>
              <Form.Item name="email" label="Email">
                <Input prefix={<MailOutlined />} />
              </Form.Item>
              <Form.Item name="address" label="Address">
                <Input prefix={<HomeOutlined />} />
              </Form.Item>
              <Form.Item name="contactNumber" label="Contact Number">
                <Input prefix={<PhoneOutlined />} />
              </Form.Item>
            </Form>
            <div style={{ display: 'flex', gap: 8, marginTop: 8, alignItems: 'center' }}>
              {editMode ? <Button type="primary" onClick={handleUserSave}>Save</Button> : null}
              <Button onClick={() => setEditMode(!editMode)} type={editMode ? 'default' : 'primary'}>{editMode ? 'Cancel' : 'Edit'}</Button>
              <Button style={{ marginLeft: 8 }} onClick={() => setPwdModalVisible(true)}>Change Password</Button>

              {/* Prominent Upload button for avatar */}
              <Upload
                showUploadList={false}
                accept="image/*"
                customRequest={async ({ file, onSuccess, onError }) => {
                  try {
                    await handleAvatarChange(file as File);
                    if (typeof onSuccess === 'function') onSuccess('ok');
                  } catch (err) {
                    if (typeof onError === 'function') onError(err as any);
                  }
                }}
              >
                <Button icon={<UploadOutlined />} style={{ marginLeft: 8 }}>Upload Avatar</Button>
              </Upload>

              <Button type="dashed" loading={requesting} onClick={handleRequestStaff} style={{ marginLeft: 'auto' }}>Request Staff Access</Button>
            </div>
          </div>
        </div>
      </div>

      <Modal
        title="Change Password"
        open={pwdModalVisible}
        onCancel={() => { setPwdModalVisible(false); changeForm.resetFields(); }}
        footer={null}
      >
        <Form form={changeForm} layout="vertical" onFinish={async (vals) => {
          const { currentPassword, newPassword, confirmPassword } = vals;
          if (newPassword !== confirmPassword) {
            message.error('New password and confirmation do not match');
            return;
          }
          setPwdLoading(true);
            try {
            const resp = await axiosInstance.post('/change-password', { currentPassword, newPassword });
            message.success('Changes have been saved');
            setPwdModalVisible(false);
            changeForm.resetFields();
          } catch (err: any) {
            const text = err?.response?.data?.message || 'Failed to change password';
            message.error(text);
          } finally {
            setPwdLoading(false);
          }
        }}>
          <Form.Item name="currentPassword" label="Current password" rules={[{ required: true, message: 'Enter your current password' }]}>
            <Input.Password />
          </Form.Item>
          <Form.Item name="newPassword" label="New password" rules={[{ required: true, message: 'Enter a new password' }, { min: 6, message: 'Password must be at least 6 characters' }]}>
            <Input.Password />
          </Form.Item>
          <Form.Item name="confirmPassword" label="Confirm new password" rules={[{ required: true, message: 'Confirm your new password' }]}>
            <Input.Password />
          </Form.Item>
          <div style={{ display: 'flex', gap: 8, justifyContent: 'flex-end' }}>
            <Button onClick={() => { setPwdModalVisible(false); changeForm.resetFields(); }}>Cancel</Button>
            <Button type="primary" loading={pwdLoading} onClick={() => changeForm.submit()}>Save</Button>
          </div>
        </Form>
      </Modal>

      <div style={{ background: '#fff', borderRadius: 8, padding: 24 }} className="profile-resident">
        <h3 style={{ marginTop: 0 }}>Resident Info</h3>
        {residentMissing && (
          <Alert
            message="Resident profile missing"
            description={
              <div>
                <p>We couldn't find your resident personal info. Please create it so you can upload your profile picture and use resident features.</p>
                <Button type="primary" onClick={autoCreateResident}>Create Resident Info</Button>
              </div>
            }
            type="warning"
            showIcon
            style={{ marginBottom: 12 }}
          />
        )}
        <Form className="profile-info" form={residentForm} layout="vertical">
          <Form.Item name="firstName" label="First Name">
            <Input />
          </Form.Item>
          <Form.Item name="lastName" label="Last Name">
            <Input />
          </Form.Item>
          <Form.Item name="birthDate" label="Birthdate">
            <Input type="date" onChange={e => {
              const v = e.target.value; if (v) {
                const age = dayjs().diff(dayjs(v), 'year');
                residentForm.setFieldsValue({ age });
              }
            }} />
          </Form.Item>
          <Form.Item name="age" label="Age">
            <Input type="number" />
          </Form.Item>
          <Form.Item name="address" label="Address">
            <Input />
          </Form.Item>
          <div style={{ display: 'flex', gap: 8 }}>
            <Button type="primary" onClick={handleResidentSave}>Save Resident Info</Button>
          </div>
        </Form>
      </div>
    </div>
  );
};

export default Profile;

import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { TextField, Button, Box, Link, Paper, Container, InputAdornment, Alert, MenuItem } from '@mui/material';
import { Email, Lock, Home, Phone, Badge, Person, Visibility, VisibilityOff } from '@mui/icons-material';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import { axiosInstance } from '../services/api';

// Server requires: min 6 chars, at least one number, one uppercase letter, and one special character
const PASSWORD_REGEX = /^(?=.*[A-Z])(?=.*\d)(?=.*[^A-Za-z0-9]).{6,}$/;

const validationSchema = Yup.object({
  username: Yup.string()
    .matches(/^[a-zA-Z0-9]{4,20}$/, 'Username must be 4-20 characters and contain only letters and numbers')
    .required('Username is required'),
  email: Yup.string().email('Invalid email address').required('Email is required'),
  password: Yup.string()
    .matches(PASSWORD_REGEX, 'Password must be at least 6 characters long and contain at least one number, one uppercase letter, and one special character')
    .required('Password is required'),
  confirmPassword: Yup.string()
    .oneOf([Yup.ref('password'), undefined], 'Passwords must match')
    .required('Confirm Password is required'),
  address: Yup.string().required('Address is required'),
  contactNumber: Yup.string().required('Contact Number is required'),
  barangayID: Yup.string().required('Barangay ID is required'),
});

const RegisterForm = () => {
  const navigate = useNavigate();
  const [error, setError] = React.useState('');
  const [success, setSuccess] = React.useState('');
  const [adminPassword, setAdminPassword] = React.useState('');
  const [adminUnlocked, setAdminUnlocked] = React.useState(false);
  const [showPassword, setShowPassword] = React.useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = React.useState(false);
  const [fieldErrors, setFieldErrors] = React.useState<{ [key: string]: string }>({});

  // Helper to generate a random unique Barangay ID
  function generateBarangayID() {
    // New format: brgyparian-<YEAR>-<6 chars alphanumeric mixed case>
    const year = new Date().getFullYear();
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let rand = '';
    for (let i = 0; i < 6; i++) rand += chars.charAt(Math.floor(Math.random() * chars.length));
    return `brgyparian-${year}-${rand}`;
  }

  const formik = useFormik({
    initialValues: {
      firstName: '',
      middleName: '',
      lastName: '',
      username: '',
      email: '',
      password: '',
      confirmPassword: '',
      address: '',
      contactNumber: '',
      barangayID: generateBarangayID(),
      role: 'resident',
    },
    validationSchema: validationSchema.shape({
      firstName: Yup.string().required('First name is required'),
      lastName: Yup.string().required('Last name is required'),
      role: Yup.string().oneOf(['resident']).required('Role is required'),
    }),
    onSubmit: async (values, { setSubmitting, resetForm }) => {
  setError('');
  setFieldErrors({});

      // Prevent submission if there are validation errors
      const formErrors = await formik.validateForm();
      if (Object.keys(formErrors).length > 0) {
        setError('Please correct the highlighted errors before submitting.');
        setSubmitting(false);
        return;
      }

      // role is constrained to 'resident' on the client; admin/staff registration is not exposed here
      try {
        const fullName = [values.firstName, values.middleName, values.lastName].filter(Boolean).join(' ');

        const resp = await axiosInstance.post('/auth/register', {
          fullName,
          firstName: values.firstName,
          middleName: values.middleName,
          lastName: values.lastName,
          username: values.username,
          email: values.email,
          password: values.password,
          address: values.address,
          contactNumber: values.contactNumber,
          barangayID: values.barangayID,
          role: values.role,
        });
        let data: any = {};
        try {
          data = resp && resp.data ? resp.data : {};
        } catch (jsonErr) {
          setError('Server error: Could not parse response');
          return;
        }
        if (resp && resp.status >= 200 && resp.status < 300) {
          setSuccess('Registration successful! You may now log in.');
          resetForm();
          setTimeout(() => navigate('/login'), 1500);
        } else {
          // Try to extract field-specific errors if present
          if (data.errors && typeof data.errors === 'object') {
            setFieldErrors(data.errors);
            setError('Please correct the highlighted errors before submitting.');
            setSubmitting(false);
            return;
          } else if (data.message) {
            setError(data.message);
          } else if (data.error) {
            setError(data.error);
          } else {
            setError('Registration failed');
          }
        }
      } catch (err) {
        setError('Network or server error. Please try again later.');
      } finally {
        setSubmitting(false);
      }
    },
  });

  return (
    <Box sx={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #40c9ff 0%, #e81cff 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      py: 6,
    }}>
      <Container maxWidth="sm">
        <Paper elevation={6} sx={{
          borderRadius: 5,
          p: 4,
          background: 'rgba(255,255,255,0.95)',
          boxShadow: '0 8px 32px 0 rgba(64,201,255,0.15)',
        }}>
          <Box sx={{ textAlign: 'center', mb: 3 }}>
            <Box sx={{
              fontWeight: 800,
              fontSize: 32,
              letterSpacing: 1,
              background: 'linear-gradient(90deg, #40c9ff, #e81cff)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              display: 'inline-block',
            }}>
              Create Account
            </Box>
          </Box>
          <form onSubmit={formik.handleSubmit}>
            {/* ...existing fields... */}
            <TextField
              fullWidth
              margin="normal"
              label="First Name"
              name="firstName"
              value={formik.values.firstName}
              onChange={formik.handleChange}
              error={formik.touched.firstName && Boolean(formik.errors.firstName)}
              helperText={formik.touched.firstName && formik.errors.firstName}
              sx={{ borderRadius: 3, background: '#f4f6fa', mb: 2 }}
            />
            <TextField
              fullWidth
              margin="normal"
              label="Middle Name (optional)"
              name="middleName"
              value={formik.values.middleName}
              onChange={formik.handleChange}
              error={formik.touched.middleName && Boolean(formik.errors.middleName)}
              helperText={formik.touched.middleName && formik.errors.middleName}
              sx={{ borderRadius: 3, background: '#f4f6fa', mb: 2 }}
            />
            <TextField
              fullWidth
              margin="normal"
              label="Last Name"
              name="lastName"
              value={formik.values.lastName}
              onChange={formik.handleChange}
              error={formik.touched.lastName && Boolean(formik.errors.lastName)}
              helperText={formik.touched.lastName && formik.errors.lastName}
              sx={{ borderRadius: 3, background: '#f4f6fa', mb: 2 }}
            />
            <TextField
              fullWidth
              margin="normal"
              label="Username"
              name="username"
              value={formik.values.username}
              onChange={formik.handleChange}
              error={
                (formik.touched.username && Boolean(formik.errors.username)) ||
                Boolean(fieldErrors.username)
              }
              helperText={
                (formik.touched.username && formik.errors.username) ||
                fieldErrors.username ||
                'Username must be at least 4 characters long and contain only letters and numbers.'
              }
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Person color="primary" />
                  </InputAdornment>
                ),
              }}
              sx={{ borderRadius: 3, background: '#f4f6fa', mb: 2 }}
            />
            <TextField
              fullWidth
              margin="normal"
              label="Email"
              name="email"
              value={formik.values.email}
              onChange={formik.handleChange}
              error={
                (formik.touched.email && Boolean(formik.errors.email)) ||
                Boolean(fieldErrors.email)
              }
              helperText={
                (formik.touched.email && formik.errors.email) ||
                fieldErrors.email
              }
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Email color="primary" />
                  </InputAdornment>
                ),
              }}
              sx={{ borderRadius: 3, background: '#f4f6fa', mb: 2 }}
            />
            {/* Password Field with Show/Hide */}
            <TextField
              fullWidth
              margin="normal"
              label="Password"
              name="password"
              type={showPassword ? "text" : "password"}
              value={formik.values.password}
              onChange={formik.handleChange}
              error={formik.touched.password && Boolean(formik.errors.password)}
              helperText={
                (formik.touched.password && formik.errors.password) ||
                fieldErrors.password ||
                'Password must be at least 6 characters long and contain at least one number, one uppercase letter, and one special character.'
              }
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Lock color="primary" />
                  </InputAdornment>
                ),
                endAdornment: (
                  <InputAdornment position="end">
                    <Button
                      onClick={() => setShowPassword((prev) => !prev)}
                      tabIndex={-1}
                      sx={{ minWidth: 0, p: 0, color: 'inherit' }}
                    >
                      {showPassword ? <VisibilityOff /> : <Visibility />}
                    </Button>
                  </InputAdornment>
                ),
              }}
              sx={{ borderRadius: 3, background: '#f4f6fa', mb: 2 }}
            />
            {/* Confirm Password Field with Show/Hide */}
            <TextField
              fullWidth
              margin="normal"
              label="Confirm Password"
              name="confirmPassword"
              type={showConfirmPassword ? "text" : "password"}
              value={formik.values.confirmPassword}
              onChange={formik.handleChange}
              error={formik.touched.confirmPassword && Boolean(formik.errors.confirmPassword)}
              helperText={formik.touched.confirmPassword && formik.errors.confirmPassword}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Lock color="primary" />
                  </InputAdornment>
                ),
                endAdornment: (
                  <InputAdornment position="end">
                    <Button
                      onClick={() => setShowConfirmPassword((prev) => !prev)}
                      tabIndex={-1}
                      sx={{ minWidth: 0, p: 0, color: 'inherit' }}
                    >
                      {showConfirmPassword ? <VisibilityOff /> : <Visibility />}
                    </Button>
                  </InputAdornment>
                ),
              }}
              sx={{ borderRadius: 3, background: '#f4f6fa', mb: 2 }}
            />
            <TextField
              fullWidth
              margin="normal"
              label="Address"
              name="address"
              value={formik.values.address}
              onChange={formik.handleChange}
              error={formik.touched.address && Boolean(formik.errors.address)}
              helperText={formik.touched.address && formik.errors.address}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Home color="primary" />
                  </InputAdornment>
                ),
              }}
              sx={{ borderRadius: 3, background: '#f4f6fa', mb: 2 }}
            />
            <TextField
              fullWidth
              margin="normal"
              label="Contact Number"
              name="contactNumber"
              value={formik.values.contactNumber}
              onChange={formik.handleChange}
              error={formik.touched.contactNumber && Boolean(formik.errors.contactNumber)}
              helperText={formik.touched.contactNumber && formik.errors.contactNumber}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Phone color="primary" />
                  </InputAdornment>
                ),
              }}
              sx={{ borderRadius: 3, background: '#f4f6fa', mb: 2 }}
            />
            {/* Keep Barangay ID out of the visible form but include it as a hidden field so it's submitted */}
            <input type="hidden" name="barangayID" value={formik.values.barangayID} />

            {/* Role Dropdown */}
            <Box sx={{ mb: 2 }}>
              <TextField
                select
                fullWidth
                label="Role"
                name="role"
                value={formik.values.role}
                onChange={e => {
                  formik.handleChange(e);
                  if (e.target.value !== 'admin') setAdminPassword('');
                }}
                error={formik.touched.role && Boolean(formik.errors.role)}
                helperText={
                  formik.touched.role && formik.errors.role ? formik.errors.role : ''
                }
                sx={{ borderRadius: 3, background: '#f4f6fa' }}
              >
                  <MenuItem value="resident">Resident</MenuItem>
              </TextField>
            </Box>
              {/* admin/staff registration is not available through this public form */}
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{
                mt: 3, mb: 2, borderRadius: 3, fontWeight: 700, fontSize: '1.1rem',
                background: 'linear-gradient(90deg, #e81cff 0%, #40c9ff 100%)',
                color: '#fff',
                boxShadow: '0 2px 8px #e81cff44',
                transition: 'background 0.3s',
                '&:hover': {
                  background: 'linear-gradient(90deg, #40c9ff 0%, #e81cff 100%)',
                },
              }}
              disabled={formik.isSubmitting}
            >
              Register
            </Button>
            {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
            {/* Show field-specific errors in a list if present and not already shown in helperText */}
            {Object.keys(fieldErrors).length > 0 && !error && (
              <Alert severity="error" sx={{ mt: 2 }}>
                {Object.entries(fieldErrors).map(([field, msg]) => (
                  <div key={field}>{msg}</div>
                ))}
              </Alert>
            )}
            {/* Optionally, show a message or unlock admin features if adminUnlocked is true */}
            {adminUnlocked && (
              <Alert severity="info" sx={{ mt: 2 }}>
                Admin access is now unlocked! Redirecting to admin dashboard...
              </Alert>
            )}
            <Box sx={{ textAlign: 'center', mt: 2 }}>
              <Link component={RouterLink} to="/login" variant="body2" sx={{ color: '#e81cff', fontWeight: 600 }}>
                Already have an account? <span style={{ textDecoration: 'underline' }}>Sign In</span>
              </Link>
            </Box>
          </form>
        </Paper>
      </Container>
    </Box>
  );
};

