 			const e: any = err;
 			const serverMsg = e?.response?.data?.message || e?.message || 'Failed to send staff access request';
 			message.error(serverMsg);
 		} finally {
			setRequesting(false);
		}
	};

		// Feedback form state
	// ...existing code...

			return (
					<div className="max-w-3xl mx-auto p-6">
						{/* Resident Portal Banner -> avatar + upload */}
