							style={{
								background: 'linear-gradient(90deg, #f7f8fa 0%, #e0e7ff 100%)',
								borderRadius: 16,
								boxShadow: '0 4px 24px #40c9ff22',
								marginBottom: 32,
								border: 'none',
								padding: 0,
							}}
							styles={{ body: { padding: '24px 32px', display: 'flex', alignItems: 'center', justifyContent: 'space-between' } }}
							variant="outlined"
