import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Statistic, List, Typography, Space, Spin, Button, Modal, Input, Collapse, Tag, Empty, Badge, Timeline, Drawer, Table, notification, Grid } from 'antd';
import AppAvatar from './AppAvatar';
import {
  HourglassOutlined,
  CaretUpOutlined,
  FolderOutlined,
  DatabaseOutlined,
  CheckCircleOutlined,
  CheckOutlined,
  InboxOutlined,
  RightOutlined,
  FileTextOutlined,
  ClockCircleOutlined,
  ExclamationCircleOutlined,
  FileDoneOutlined,
  FileExclamationOutlined,
  MessageOutlined,
  SafetyCertificateOutlined,
  ProfileOutlined,
  FileSearchOutlined,
  SolutionOutlined,
  TeamOutlined,
  MailOutlined
} from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { documentsAPI, contactAPI, getAbsoluteApiUrl, axiosInstance } from '../services/api';


interface DocumentRequest {
  _id?: string;
  type?: string;
  title?: string;
  description?: string;
  username?: string;
  barangayID?: string;
  dateRequested?: string;
  status?: string;
  [key: string]: any;
}

const StaffDashboard: React.FC = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [documentRequests, setDocumentRequests] = useState<DocumentRequest[]>([]);
  const [inquiries, setInquiries] = useState<any[]>([]);
  const [inboxInquiries, setInboxInquiries] = useState<any[]>([]);
  const [viewedInquiryIds, setViewedInquiryIds] = useState<Set<string>>(() => {
    try {
      const raw = localStorage.getItem('viewedInquiries');
      const arr = raw ? JSON.parse(raw) as string[] : [];
      return new Set(arr || []);
    } catch (e) {
      return new Set<string>();
    }
  });

  const markInquiryViewed = (id?: string) => {
    if (!id) return;
    setViewedInquiryIds(prev => {
      const next = new Set(Array.from(prev));
      next.add(String(id));
      try { localStorage.setItem('viewedInquiries', JSON.stringify(Array.from(next))); } catch (e) {}
      return next;
    });
  };

  // Handle responding to an inquiry from the Inquiry Response modal
  const handleInquiryResponse = async () => {
    if (!selectedInquiry) return;
    const id = selectedInquiry._id || selectedInquiry.id;
    if (!id) return;
    try {
      notification.info({ message: 'Sending response', description: 'Posting your reply...', duration: 1.2 });
      await contactAPI.respondToInquiry(id, { response: responseText });
      // Update local inquiries list if present
      setInquiries(prev => (prev || []).map(q => (q && (q._id === id || q.id === id)) ? { ...q, status: 'resolved' } : q));
      notification.success({ message: 'Response sent', description: 'Inquiry has been updated' });
      setSelectedInquiry(null);
      setResponseText('');
    } catch (err: any) {
      console.error('Failed to send inquiry response', err);
      const msg = err?.response?.data?.message || err?.message || 'Failed to send response';
      notification.error({ message: 'Error', description: msg });
    }
  };
  const [recentActivity, setRecentActivity] = useState<any[]>([]);
  // Mini announcements (replace Recent Activity)
  const [miniAnns, setMiniAnns] = useState<any[]>([]);
  const [miniLoading, setMiniLoading] = useState(false);
  const [miniSelected, setMiniSelected] = useState<any | null>(null);
  const [drawerVisible, setDrawerVisible] = useState(false);
  const [selectedDocument, setSelectedDocument] = useState<DocumentRequest | null>(null);
  const [selectedInquiry, setSelectedInquiry] = useState<any>(null);
  const [manageModalVisible, setManageModalVisible] = useState(false);
  const [manageTableData, setManageTableData] = useState<any[] | null>(null);
  const [responseText, setResponseText] = useState('');
  const [documentStatus, setDocumentStatus] = useState('');
  const [responding, setResponding] = useState(false);
  const [completedModalVisible, setCompletedModalVisible] = useState(false);
  // Documents modal state
  const [docsModalVisible, setDocsModalVisible] = useState(false);
  const [allDocuments, setAllDocuments] = useState<any[]>([]);
  const [docsLoading, setDocsLoading] = useState(false);
  const [previewVisible, setPreviewVisible] = useState(false);
  const [previewDoc, setPreviewDoc] = useState<any | null>(null);
  const [docsSearch, setDocsSearch] = useState<string>('');
  const screens = Grid.useBreakpoint();

  // Group document requests by type for processing
  const requestsByCategory: { [type: string]: DocumentRequest[] } = {};
  documentRequests.forEach((req) => {
    const docType = req.type || 'Unknown';
    if (!requestsByCategory[docType]) requestsByCategory[docType] = [];
    requestsByCategory[docType].push(req);
  });

  // Example stats for staff
  const [stats, setStats] = useState({
    pendingRequests: 0,
    totalDocuments: 0,
    completedRequests: 0,
  });

  // Group requests by category
  const categorizeRequests = (requests: DocumentRequest[]) => {
    const categories = {
      'Certificates': {
        icon: <SafetyCertificateOutlined style={{ fontSize: '18px', color: '#1890ff' }} />,
        items: [] as DocumentRequest[],
        color: '#1890ff',
        description: 'Birth, Death, Residency Certificates and more'
      },
      'Permits': {
        icon: <ProfileOutlined style={{ fontSize: '18px', color: '#52c41a' }} />,
        items: [] as DocumentRequest[],
        color: '#52c41a',
        description: 'Building, Business, and Special Event Permits'
      }
      ,
      'Complete': {
        icon: <FileDoneOutlined style={{ fontSize: '18px', color: '#52c41a' }} />,
        items: [] as DocumentRequest[],
        color: '#52c41a',
        description: 'All completed and approved document requests'
      }
    };

    requests.forEach(req => {
      const type = (req.type || '').toLowerCase();
      const status = (req.status || '').toLowerCase();
      if (type.includes('certificate') || type.includes('certification')) {
        categories['Certificates'].items.push(req);
      } else if (type.includes('permit')) {
        categories['Permits'].items.push(req);
      }

      // Add to Complete category if status is approved/completed
      if (status === 'approved' || status === 'completed') {
        categories['Complete'].items.push(req);
      }
    });

    return categories;
  };

  // Download processed document (handles both metadata id and direct GridFS file id)
  const handleDownloadProcessed = async (rec: any) => {
    try {
      // Follow TemplatesManager behavior: fetch the raw endpoint, convert to blob, and save using the server-provided filename when available.
      // Try metadata id first, then fall back to gridFsFileId if present and the first attempt 404s.
      const normalizeId = (v: any): string | null => {
        if (!v && v !== 0) return null;
        if (typeof v === 'string') return v;
        if (typeof v === 'object') {
          // Mongoose ObjectId-like objects may expose $oid (BSON) or toString()
          if (typeof v.$oid === 'string') return v.$oid;
          try {
            const s = (v as any).toString();
            if (s && s !== '[object Object]') return s;
          } catch (e) {}
          return null;
        }
        return String(v);
      };

      const tryIds = [] as string[];
      const nid = normalizeId(rec && rec._id);
      const ngfid = normalizeId(rec && rec.gridFsFileId);
      if (nid) tryIds.push(nid);
      if (ngfid) tryIds.push(ngfid);
      // de-dupe
      const ids = Array.from(new Set(tryIds));
      let finalResp: Response | null = null;
      let usedId: string | null = null;
      let lastError: any = null;

      console.debug('[handleDownloadProcessed] record:', rec, 'candidateIds:', ids);
      let finalBlob: Blob | null = null;
      let finalHeaders: any = null;
      for (const id of ids) {
        try {
          console.debug('[handleDownloadProcessed] attempting id=', id);
          const r = await (await import('../services/api')).axiosInstance.get(`/processed-documents/${id}/raw`, { responseType: 'blob' });
          if (r && r.status >= 200 && r.status < 300) {
            finalBlob = r.data as Blob;
            finalHeaders = r.headers || {};
            usedId = id;
            break;
          }
        } catch (err: any) {
          const status = err && err.response && err.response.status ? err.response.status : null;
          if (status === 404) {
            lastError = { status: 404, body: err.response && err.response.data ? err.response.data : null };
            continue;
          }
          notification.error({ message: 'Download failed', description: status ? `Server returned ${status}` : String(err) });
          return;
        }
      }

      if (!finalBlob) {
        const msg = lastError && lastError.body && lastError.body.message ? lastError.body.message : (lastError && lastError.message) ? lastError.message : 'File not found';
        notification.error({ message: 'Download failed', description: msg });
        return;
      }

      const blob = finalBlob;

      // Prefer filename from Content-Disposition header if provided
      let filename: string | null = null;
      const cd = (finalHeaders && (finalHeaders['content-disposition'] || finalHeaders['Content-Disposition'])) || null;
      if (cd) {
        const m = cd.match(/filename\*=UTF-8''([^;\n\r]+)/i);
        if (m && m[1]) filename = decodeURIComponent(m[1]);
        else {
          const m2 = cd.match(/filename="?([^";]+)"?/i);
          if (m2 && m2[1]) filename = m2[1];
        }
      }
      // Fallback to X-Processed headers or record fields
      if (!filename) filename = (finalHeaders && (finalHeaders['x-processed-transactioncode'] || finalHeaders['X-Processed-TransactionCode'])) || rec.filename || rec.name || `document_${usedId || rec._id}.docx`;

      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      const downloadName = filename || `document_${usedId || rec._id}.docx`;
      a.download = downloadName;
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error('Download error', err);
      notification.error({ message: 'Download error', description: String(err) });
    }
  };

  const getStatusTag = (status?: string) => {
    const statusColors: { [key: string]: string } = {
      'pending': 'gold',
      'approved': 'success',
      'rejected': 'error',
      'processing': 'processing'
    };

    return (
      <Tag color={statusColors[status?.toLowerCase() || 'default']}>
        {status?.toUpperCase() || 'UNKNOWN'}
      </Tag>
    );
  };

  const getActivityIcon = (type: string) => {
    const iconStyle = { fontSize: '20px' };
    const iconMap: { [key: string]: React.ReactNode } = {
      'document_approved': <FileDoneOutlined style={{ ...iconStyle, color: '#52c41a' }} />,
      'document_rejected': <FileExclamationOutlined style={{ ...iconStyle, color: '#ff4d4f' }} />,
      'inquiry_responded': <MessageOutlined style={{ ...iconStyle, color: '#1890ff' }} />,
      'document_submitted': <FileTextOutlined style={{ ...iconStyle, color: '#faad14' }} />,
      'default': <ClockCircleOutlined style={{ ...iconStyle, color: '#8c8c8c' }} />
    };
    return iconMap[type] || iconMap['default'];
  };

  const fetchData = async () => {
    setLoading(true);
    try {
      // Fetch document records from /documents (not /document-requests/all)
      const [docRecords, inq] = await Promise.all([
        documentsAPI.getDocumentRecords(),
        contactAPI.getAllInquiries()
      ]);
      setDocumentRequests(docRecords);
      setInquiries(inq);
      // Filter for inbox: assignedRole matches user.role or assignedTo includes user._id
      const filtered = inq.filter((inq: any) =>
        (inq.assignedRole && user && inq.assignedRole === user.role) ||
        (inq.assignedTo && Array.isArray(inq.assignedTo) && user && inq.assignedTo.includes(user._id))
      );
      setInboxInquiries(filtered);
      // Attempt to get count of processed documents from server-side processed_documents metadata
      let processedCount = docRecords.length;
      try {
        const resp = await axiosInstance.get('/processed-documents', { params: { page: 1, limit: 1 } });
        const j = resp && resp.data ? resp.data : null;
        if (j && typeof j.total === 'number') processedCount = j.total;
      } catch (e) {
        // ignore and fall back to docRecords.length
      }

      setStats({
        pendingRequests: docRecords.filter((d: any) => d.status === 'pending').length,
        totalDocuments: processedCount,
        completedRequests: docRecords.filter((d: any) => d.status === 'approved').length,
      });
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    fetchMiniAnnouncements();
  }, []);

  // Fetch processed documents (from documents collection) and open modal
  const fetchAllProcessedDocuments = async () => {
    setDocsLoading(true);
    try {
      // Prefer processed_documents metadata endpoint; fallback to older documents listing
      let processedItems: any[] = [];
      try {
        const resp = await axiosInstance.get('/processed-documents', { params: { page: 1, limit: 200 } });
        const j = resp && resp.data ? resp.data : null;
        if (j && Array.isArray(j.items)) {
          processedItems = j.items;
        } else if (Array.isArray(j)) {
          processedItems = j as any[];
        }
      } catch (e) {
        // fallback to older endpoints
        try {
          const files: any = await documentsAPI.listFiles();
          const arr = Array.isArray(files) ? files : (files && files.data) ? files.data : [];
          processedItems = arr.filter((d: any) => {
            const s = (d.status || '').toString().toLowerCase();
            return ['processed', 'approved', 'completed'].includes(s) || d.processed === true;
          });
        } catch (e2) {
          processedItems = [];
        }
      }

      setAllDocuments(processedItems || []);
    } catch (err) {
      console.error('Failed to fetch documents for modal', err);
      setAllDocuments([]);
    } finally {
      setDocsLoading(false);
      setDocsModalVisible(true);
    }
  };

  const fetchMiniAnnouncements = async () => {
    setMiniLoading(true);
    try {
      const data = await contactAPI.getAnnouncements();
      setMiniAnns(Array.isArray(data) ? data.slice(0, 6) : []);
    } catch (err) {
      console.error('Failed to load mini announcements', err);
      setMiniAnns([]);
    } finally {
      setMiniLoading(false);
    }
  };
  // Open Manage/Expand modal helper (centralized for logging + notification)
  const openManageModal = (e?: React.MouseEvent) => {
    try {
      if (e && typeof e.stopPropagation === 'function') e.stopPropagation();
    } catch (err) {}
    try {
      console.debug('[StaffDashboard] openManageModal clicked');
      notification.info({ message: 'Opening Inquiries', description: 'Loading inquiries into the table...', duration: 1.5 });
    } catch (err) {}
    setManageTableData(sortByDateDesc(inquiries));
    setManageModalVisible(true);
  };

  // Handle approve/reject/other document actions from Document Response modal
  const handleDocumentAction = async () => {
    if (!selectedDocument) return;
    const id = selectedDocument._id || selectedDocument.id;
    if (!id) return;
    try {
      // Use documentsAPI to update status; server expects lowercase status strings
      const payload = { status: (documentStatus || '').toString().toLowerCase(), notes: responseText };
      // Show a short in-UI indicator using notification
      notification.info({ message: 'Processing', description: `Updating document status to ${payload.status}...`, duration: 1.2 });
      await documentsAPI.updateDocumentStatus(id, payload);
      // Update local list if present
      setDocumentRequests(prev => (prev || []).map(d => d && (d._id === id || d.id === id) ? { ...d, status: payload.status } : d));
      notification.success({ message: 'Success', description: `Document marked ${payload.status}` });
      // Clear modal and fields
      setSelectedDocument(null);
      setResponseText('');
      setDocumentStatus('');
    } catch (err: any) {
      console.error('Failed to update document status', err);
      const msg = err?.response?.data?.message || err?.message || 'Failed to update document';
      notification.error({ message: 'Error', description: msg });
    }
  };

  // Format helpers
  // Helper to format document type for display
  function formatDocumentType(type?: string) {
    if (!type) return 'Unknown';
    // If you have a typeToCategory mapping, use it here
    // let name = typeToCategory[type] || type;
    let name = type;
    // Remove 'Other' prefix and extra spaces
    name = name.replace(/^Other\s*/i, '').trim();
    // Replace underscores with spaces and capitalize each word
    return name
      .split('_')
      .map(word => {
        const w = word || '';
        return w.length > 0 ? (w.charAt(0).toUpperCase() + w.slice(1).toLowerCase()) : '';
      })
      .filter(Boolean)
      .join(' ');
  }

  // Helper to format date for display
  function formatDate(dateString?: string) {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleString();
  }

  // Sort an array of inquiries/documents by createdAt descending (newest first)
  const sortByDateDesc = (arr: any[] = []) => {
    return (arr || []).slice().sort((a: any, b: any) => {
      const ta = a && a.createdAt ? new Date(a.createdAt).getTime() : 0;
      const tb = b && b.createdAt ? new Date(b.createdAt).getTime() : 0;
      return tb - ta;
    });
  };

  // Only one List for Document Categories will be rendered below in the Card section
  // (Card/Col/Row structure continues below)

  const goWithState = (path: string, state?: any) => {
    navigate(path, { state });
  };

  const go = (path: string) => {
    try {
      navigate(path);
    } catch (e) {
      window.location.href = path;
    }
  };

  // No replacement needed - removing the duplicate function

  return (
    <Spin spinning={loading} tip="Loading...">
      <div>
        {/* KPI Cards Row */}
        <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
          <Col xs={24} sm={12} md={6}>
            <Card 
              hoverable 
              bordered={false}
              size="small"
              onClick={() => {
                // If there are pending requests, navigate to document processing and open the first one
                const pending = documentRequests.find(d => d.status === 'pending');
                if (pending && pending._id) {
                  goWithState('/document-processing', { openRequestId: pending._id });
                } else {
                  goWithState('/document-processing');
                }
              }} 
              style={{ 
                cursor: 'pointer',
                transition: 'all 0.3s',
                boxShadow: '0 1px 2px rgba(0,0,0,0.05)'
              }}
            >
              <Statistic
                title={
                  <Space>
                    <AppAvatar size="small" background="#fff7e6" color="#faad14" icon={<HourglassOutlined style={{ fontSize: '14px' }} />} />
                    <span>Pending Requests</span>
                  </Space>
                }
                value={stats.pendingRequests}
                valueStyle={{ color: '#faad14', fontSize: '28px', marginBottom: '4px' }}
                prefix={<CaretUpOutlined style={{ fontSize: '16px' }} />}
              />
              <Typography.Link style={{ fontSize: '13px', color: '#faad14' }}>
                View Pending Requests
                <RightOutlined style={{ fontSize: '11px', marginLeft: '4px' }} />
              </Typography.Link>
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card 
              hoverable 
              bordered={false}
              size="small"
              onClick={() => fetchAllProcessedDocuments()} 
              style={{ 
                cursor: 'pointer',
                transition: 'all 0.3s',
                boxShadow: '0 1px 2px rgba(0,0,0,0.05)'
              }}
            >
              <Statistic
                title={
                  <Space>
                    <AppAvatar size="small" background="#e6f7ff" color="#1890ff" icon={<FolderOutlined style={{ fontSize: '14px' }} />} />
                    <span>Total Documents</span>
                  </Space>
                }
                value={stats.totalDocuments}
                valueStyle={{ color: '#1890ff', fontSize: '28px', marginBottom: '4px' }}
                prefix={<DatabaseOutlined style={{ fontSize: '16px' }} />}
              />
              <Typography.Link style={{ fontSize: '13px', color: '#1890ff' }}>
                Browse Documents
                <RightOutlined style={{ fontSize: '11px', marginLeft: '4px' }} />
              </Typography.Link>
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card 
              hoverable 
              bordered={false}
              size="small"
              onClick={() => setCompletedModalVisible(true)} 
              style={{ 
                cursor: 'pointer',
                transition: 'all 0.3s',
                boxShadow: '0 1px 2px rgba(0,0,0,0.05)'
              }}
            >
              <Statistic
                title={
                  <Space>
                    <AppAvatar size="small" background="#f6ffed" color="#52c41a" icon={<CheckCircleOutlined style={{ fontSize: '14px' }} />} />
                    <span>Completed Requests</span>
                  </Space>
                }
                value={stats.completedRequests}
                valueStyle={{ color: '#52c41a', fontSize: '28px', marginBottom: '4px' }}
                prefix={<CheckOutlined style={{ fontSize: '16px' }} />}
              />
              <Typography.Link style={{ fontSize: '13px', color: '#52c41a' }}>
                View Completed
                <RightOutlined style={{ fontSize: '11px', marginLeft: '4px' }} />
              </Typography.Link>
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card 
              hoverable 
              bordered={false}
              size="small"
              onClick={() => go('/staff/inbox')} 
              style={{ 
                cursor: 'pointer',
                transition: 'all 0.3s',
                boxShadow: '0 1px 2px rgba(0,0,0,0.05)'
              }}
            >
              <Statistic
                title={
                  <Space>
                    <AppAvatar size="small" background="#f9f0ff" color="#722ed1" icon={<InboxOutlined style={{ fontSize: '14px' }} />} />
                    <span>Staff Inbox</span>
                  </Space>
                }
                value={inboxInquiries.length}
                valueStyle={{ color: '#722ed1', fontSize: '28px', marginBottom: '4px' }}
                prefix={<MessageOutlined style={{ fontSize: '16px' }} />}
              />
              <Typography.Link style={{ fontSize: '13px', color: '#722ed1' }}>
                Open Inbox
                <RightOutlined style={{ fontSize: '11px', marginLeft: '4px' }} />
              </Typography.Link>
            </Card>
          </Col>
        </Row>
        {/* Main Content Cards */}
        <Row gutter={[16, 16]}>
          <Col xs={24} lg={8}>
            <Card 
              title={<Space><FileSearchOutlined /> Document Categories</Space>} 
              hoverable
              bordered={false}
              size="small"
              className="dashboard-card"
              style={{ 
                transition: 'all 0.3s',
                boxShadow: '0 1px 2px rgba(0,0,0,0.05)'
              }}
              styles={{ body: { 
                padding: '0',
                height: '400px',
                maxHeight: '400px',
                overflow: 'hidden'
              } }}
            >
              <Collapse
                defaultActiveKey={['Certificates']}
                expandIcon={({ isActive }) => <RightOutlined rotate={isActive ? 90 : 0} />}
                style={{ 
                  background: 'white',
                  height: '400px',
                  overflowY: 'auto'
                }}
              >
                {Object.entries(categorizeRequests(documentRequests)).map(([category, data]) => {
                  const pendingCount = data.items.filter((req: DocumentRequest) => req.status?.toLowerCase() === 'pending').length;
                  return (
                    <Collapse.Panel
                      key={category}
                      header={
                        <div style={{ display: 'flex', alignItems: 'center', width: '100%' }}>
                          <Space align="center">
                            <AppAvatar size="small" background={data.color + '15'} color={data.color} icon={data.icon} />
                            <span style={{ fontWeight: 500, fontSize: '14px' }}>{category}</span>
                          </Space>
                          <div style={{ marginLeft: 'auto', display: 'flex', gap: '8px', alignItems: 'center' }}>
                            {pendingCount > 0 && (
                              <Tag color="gold" style={{ marginRight: 0 }}>
                                {pendingCount} pending
                              </Tag>
                            )}
                            <Badge 
                              count={data.items.length} 
                              style={{ backgroundColor: data.color }}
                              size="small"
                            />
                          </div>
                        </div>
                      }
                    >
                      <div style={{ marginBottom: '16px' }}>
                        <Typography.Text type="secondary" style={{ fontSize: '13px' }}>
                          {data.description}
                        </Typography.Text>
                      </div>
                      
                      <List
                        size="small"
                        dataSource={data.items.slice(0, 5)}
                        split={true}
                        renderItem={request => {
                          const statusColors = {
                            PENDING: { color: '#faad14', bg: '#fff7e6' },
                            APPROVED: { color: '#52c41a', bg: '#f6ffed' },
                            REJECTED: { color: '#ff4d4f', bg: '#fff1f0' },
                            DEFAULT: { color: '#8c8c8c', bg: '#f5f5f5' }
                          };
                          
                          const status = request.status?.toUpperCase() || 'DEFAULT';
                          const statusStyle = statusColors[status as keyof typeof statusColors] || statusColors.DEFAULT;

                          return (
                            <List.Item
                              onClick={() => navigate('/document-processing', { state: { openRequestId: request._id } })}
                              actions={[
                                request.status === 'PENDING' && (
                                  <Space size={4}>
                                    <Button
                                      type="text"
                                      size="small"
                                      icon={<CheckOutlined style={{ color: '#52c41a' }} />}
                                      onClick={(e) => { e.stopPropagation(); setSelectedDocument(request); setDocumentStatus('APPROVED'); }}
                                    />
                                    <Button
                                      type="text"
                                      size="small"
                                      icon={<ExclamationCircleOutlined style={{ color: '#ff4d4f' }} />}
                                      onClick={(e) => { e.stopPropagation(); setSelectedDocument(request); setDocumentStatus('REJECTED'); }}
                                    />
                                  </Space>
                                )
                              ].filter(Boolean)}
                              style={{ padding: '12px 0' }}
                            >
                              <List.Item.Meta
                                avatar={
                                  <AppAvatar
                                    size={36}
                                    background={statusStyle.bg}
                                    color={statusStyle.color}
                                    icon={<FileTextOutlined />}
                                  />
                                }
                                title={
                                  <Space size={8} style={{ marginBottom: 4 }}>
                                    <Typography.Text strong style={{ fontSize: '14px' }}>
                                      {formatDocumentType(request.type || request.title)}
                                    </Typography.Text>
                                    <Tag
                                      color={statusStyle.color}
                                      style={{ 
                                        margin: 0,
                                        padding: '0 6px',
                                        fontSize: '12px',
                                        lineHeight: '18px'
                                      }}
                                    >
                                      {status}
                                    </Tag>
                                  </Space>
                                }
                                description={
                                  <Space direction="vertical" size={0}>
                                    <Typography.Text type="secondary" style={{ fontSize: '13px' }}>
                                      Requested by: {request.username || 'Unknown'}
                                    </Typography.Text>
                                    <Typography.Text type="secondary" style={{ fontSize: '12px' }}>
                                      {formatDate(request.dateRequested || '')}
                                    </Typography.Text>
                                  </Space>
                                }
                              />
                            </List.Item>
                          );
                        }}
                        locale={{ 
                          emptyText: (
                            <Empty 
                              image={Empty.PRESENTED_IMAGE_SIMPLE} 
                              description={
                                <Typography.Text type="secondary">
                                  No {category.toLowerCase()} requests
                                </Typography.Text>
                              }
                            />
                          )
                        }}
                      />
                      {data.items.length > 0 && (
                        <div style={{ 
                          textAlign: 'center', 
                          marginTop: '16px',
                          paddingTop: '16px',
                          borderTop: '1px solid #f0f0f0'
                        }}>
                          <Button
                            type="link"
                            size="small"
                            icon={data.icon}
                            onClick={() => go('/documents')}
                          >
                            View All {category}
                          </Button>
                        </div>
                      )}
                    </Collapse.Panel>
                  );
                })}
              </Collapse>
            </Card>
          </Col>
          <Col xs={24} lg={8}>
              <Card 
              title={
                <Space>
                  <MailOutlined />
                  <span>Inquiries Inbox</span>
                  <Badge 
                    count={inboxInquiries.filter(i => i.status === 'open' || i.status === 'PENDING').length} 
                    style={{ backgroundColor: '#722ed1' }}
                  />
                </Space>
              }
              extra={
                <Space>
                  <Statistic
                    value={inboxInquiries.length}
                    suffix="total"
                    valueStyle={{ fontSize: '14px', color: '#8c8c8c' }}
                  />
                </Space>
              }
              hoverable
              bordered={false}
              size="small"
              className="dashboard-card"
              style={{ 
                transition: 'all 0.3s',
                boxShadow: '0 1px 2px rgba(0,0,0,0.05)',
                position: 'relative'
              }}
              styles={{ body: { 
                height: '400px',
                maxHeight: '400px',
                padding: 0,
                overflow: 'hidden'
              } }}
            >
              <div style={{ padding: '0 12px' }}>
                <Collapse
                  accordion={false}
                  defaultActiveKey={[ 'open' ]}
                  expandIcon={({ isActive }) => <RightOutlined rotate={isActive ? 90 : 0} />}
                  style={{ background: 'white' }}
                >
                  <Collapse.Panel
                    key="open"
                    header={
                      <div style={{ display: 'flex', alignItems: 'center', width: '100%' }}>
                        <Space align="center">
                          <MailOutlined style={{ color: '#722ed1', fontSize: '16px' }} />
                          <Typography.Text strong>Open Inquiries</Typography.Text>
                          <Badge count={inboxInquiries.filter(i => i.status === 'PENDING' || i.status === 'open').length} style={{ backgroundColor: '#722ed1' }} />
                        </Space>
                      </div>
                    }
                  >
                    <div style={{ maxHeight: 300, overflowY: 'auto', paddingRight: 8 }}>
                      {inboxInquiries.filter(i => i.status === 'PENDING' || i.status === 'open').length === 0 ? (
                        <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} description={<Typography.Text type="secondary">No open inquiries</Typography.Text>} />
                      ) : (
            <List
                size="large"
              dataSource={sortByDateDesc(inboxInquiries.filter(i => i.status === 'PENDING' || i.status === 'open'))}
                          renderItem={inquiry => {
                  const isOpen = inquiry.status === 'PENDING' || inquiry.status === 'open';
          const avatarColor = isOpen ? '#722ed1' : '#8c8c8c';
          const avatarBg = isOpen ? '#f9f0ff' : '#f5f5f5';
          const isViewed = inquiry._id && viewedInquiryIds.has(String(inquiry._id));

          const displayName = inquiry.username || inquiry.residentName || inquiry.subject || 'Unknown';
          const letter = (displayName && displayName !== 'Unknown' && displayName.length > 0) ? displayName.charAt(0).toUpperCase() : '?';

                      return (
                        <List.Item
                      style={{ 
                        padding: '18px 20px',
                        minHeight: 72,
                        borderBottom: '1px solid #f0f0f0',
                        transition: 'all 0.3s ease',
                        cursor: 'pointer',
                        backgroundColor: 'white'
                      }}
                      onClick={() => {
                        // mark as viewed locally so the dot color changes (persisted)
                        if (inquiry._id) markInquiryViewed(String(inquiry._id));
                        navigate('/staff/inbox', { state: { openInquiryId: inquiry._id } });
                      }}
                      onMouseEnter={(e) => {
                        e.currentTarget.style.backgroundColor = '#fafafa';
                      }}
                      onMouseLeave={(e) => {
                        e.currentTarget.style.backgroundColor = 'white';
                      }}
                      
                      >
                      <List.Item.Meta
                        avatar={
                          <Badge dot={isOpen} color={isOpen && !isViewed ? '#faad14' : '#722ed1'} offset={[-6, 6]}>
                            <AppAvatar
                              size={40}
                              style={{
                                backgroundColor: avatarBg,
                                color: avatarColor,
                                fontSize: '15px',
                                fontWeight: 600
                              }}
                            >
                              {letter}
                            </AppAvatar>
                          </Badge>
                        }
                        title={
                          <div style={{ 
                            display: 'flex',
                            alignItems: 'center',
                            gap: '8px',
                            marginBottom: 0
                          }}>
                            <div style={{ 
                              fontSize: '14px',
                              fontWeight: 600,
                              color: 'rgba(0, 0, 0, 0.85)'
                            }}>
                              {inquiry.username || 'Unknown User'}
                            </div>
                            <Tag 
                              color={isOpen ? 'gold' : 'success'}
                              style={{ 
                                margin: 0,
                                fontSize: '12px',
                                lineHeight: '20px',
                                height: '22px',
                                padding: '0 10px',
                                fontWeight: 'normal',
                                borderRadius: '4px'
                              }}
                            >
                              {isOpen ? 'PENDING' : 'RESOLVED'}
                            </Tag>
                            {isOpen && !isViewed && (
                              <Tag
                                color="default"
                                onClick={(e) => { e.stopPropagation(); if (inquiry._id) markInquiryViewed(String(inquiry._id)); }}
                                style={{ marginLeft: 8, cursor: 'pointer', background: '#fff7e6', color: '#d48806', border: '1px solid #ffd591' }}
                              >
                                UNREAD
                              </Tag>
                            )}
                          </div>
                        }
                        description={
                          <div style={{ display: 'flex', alignItems: 'center', gap: '12px', width: '100%' }}>
                            <Typography.Text
                              style={{ 
                                fontSize: '13px',
                                color: 'rgba(0, 0, 0, 0.65)',
                                flexGrow: 1,
                                whiteSpace: 'nowrap',
                                overflow: 'hidden',
                                textOverflow: 'ellipsis',
                                lineHeight: '1.5'
                              }}
                            >
                              {inquiry.message}
                            </Typography.Text>
                            <Typography.Text 
                              type="secondary" 
                              style={{ 
                                fontSize: '12px', 
                                flexShrink: 0,
                                color: '#8c8c8c'
                              }}
                            >
                              {formatDate(inquiry.createdAt)}
                            </Typography.Text>
                          </div>
                        }
                      />
                    </List.Item>
                          );
                        }}
                      />
                    )}
                  </div>
                </Collapse.Panel>
                <Collapse.Panel
                  key="resolved"
                  header={
                    <div style={{ display: 'flex', alignItems: 'center', width: '100%' }}>
                      <Space align="center">
                        <MailOutlined style={{ color: '#52c41a', fontSize: '16px' }} />
                        <Typography.Text strong>Resolved Inquiries</Typography.Text>
                        <Badge count={inboxInquiries.filter(i => !(i.status === 'PENDING' || i.status === 'open')).length} style={{ backgroundColor: '#52c41a' }} />
                      </Space>
                    </div>
                  }
                >
                  <div style={{ maxHeight: 220, overflowY: 'auto', paddingRight: 8 }}>
                    {inboxInquiries.filter(i => !(i.status === 'PENDING' || i.status === 'open')).length === 0 ? (
                      <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} description={<Typography.Text type="secondary">No resolved inquiries</Typography.Text>} />
                    ) : (
                      <List
                        size="small"
                        dataSource={sortByDateDesc(inboxInquiries.filter(i => !(i.status === 'PENDING' || i.status === 'open')))}
                        renderItem={inquiry => {
                          const isOpen = false;
                          const avatarColor = '#8c8c8c';
                          const avatarBg = '#f5f5f5';

                          return (
                            <List.Item
                              style={{ padding: '12px 0', borderBottom: '1px solid #f0f0f0' }}
                            onClick={() => {
                              if (inquiry._id) markInquiryViewed(String(inquiry._id));
                              setSelectedInquiry(inquiry);
                            }}
                            >
                              <List.Item.Meta
                                avatar={<AppAvatar size={40} style={{ backgroundColor: avatarBg, color: avatarColor, fontSize: 14 }}>{(inquiry.username || 'Unknown').charAt(0).toUpperCase()}</AppAvatar>}
                                title={<div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                                  <div style={{ fontSize: 13, fontWeight: 600 }}>{inquiry.username || 'Unknown User'}</div>
                                  <Tag color="success" style={{ margin: 0, fontSize: 12 }}>{'RESOLVED'}</Tag>
                                </div>}
                                description={<div style={{ fontSize: 12, color: '#888' }}>{inquiry.message}</div>}
                              />
                            </List.Item>
                          );
                        }}
                      />
                    )}
                  </div>
                </Collapse.Panel>
              </Collapse>
              {/* Manage button positioned at bottom-right of the card */}
              <div style={{ position: 'absolute', right: 12, bottom: 12, zIndex: 40 }}>
                <Button
                  type="link"
                  style={{ position: 'absolute', right: 16, bottom: 0, fontSize: 13, color: '#1890ff', zIndex: 50, pointerEvents: 'auto' }}
                  onClick={(e) => { try { e.stopPropagation(); } catch (err) {} ; navigate('/inquiry-tracker'); }}
                >
                  Expand
                </Button>
              </div>
            </div>
            </Card>
          </Col>
          <Col xs={24} lg={8}>
            <Card
              title={<Space><FileTextOutlined /> Announcements</Space>}
              style={{ marginTop: 0, background: '#fafbfc', borderRadius: 12, boxShadow: '0 2px 8px #d9d9d933', border: '1px solid #f0f0f0', position: 'relative' }}
              styles={{ body: { padding: 12 } }}
              size="small"
              hoverable={false}
            >
              {miniAnns.length === 0 ? (
                <Empty
                  image={<FileTextOutlined style={{ fontSize: 42, color: '#d9d9d9' }} />}
                  description={<span style={{ color: '#888' }}>No announcements</span>}
                />
              ) : (
                <div style={{ maxHeight: 320, overflowY: 'auto', paddingRight: 6 }}>
                  <List
                    loading={miniLoading}
                    dataSource={miniAnns}
                    renderItem={(item) => (
                      <List.Item style={{ cursor: 'pointer', padding: '10px 8px', alignItems: 'center' }} onClick={() => { setMiniSelected(item); setDrawerVisible(true); }}>
                        <List.Item.Meta
                          title={<div style={{ display: 'flex', alignItems: 'flex-start', gap: 8 }}>
                            <div style={{ flex: 1, minWidth: 0 }}>
                              <div style={{ fontWeight: 600, fontSize: 13, display: '-webkit-box', WebkitLineClamp: 3, WebkitBoxOrient: 'vertical', overflow: 'hidden', textOverflow: 'ellipsis', lineHeight: '1.2em' }}>{item.text || 'Untitled'}</div>
                              <div style={{ fontSize: 12, color: '#888', marginTop: 6 }}>{new Date(item.createdAt).toLocaleString()}</div>
                            </div>
                          </div>}
                          description={null}
                        />
                        {item.imagePath && (
                          <div style={{ marginLeft: 12, width: 92, display: 'flex', justifyContent: 'flex-end', flexShrink: 0 }}>
                            <img loading="lazy" className="rounded-img" src={getAbsoluteApiUrl(`/announcements/${item._id}/image`)} alt="ann" style={{ width: 92, height: 60, objectFit: 'cover', borderRadius: 6, background: '#f0f0f0' }} />
                          </div>
                        )}
                      </List.Item>
                    )}
                    size="small"
                  />
                </div>
              )}
              <Button type="link" style={{ position: 'absolute', right: 16, bottom: 0, fontSize: 13, color: '#1890ff' }} onClick={() => navigate('/admin/announcements')}>Manage</Button>

              <Drawer open={drawerVisible} onClose={() => { setDrawerVisible(false); setMiniSelected(null); }} title="Announcement" width={720} placement="right">
                {miniSelected && (
                  <div>
                    <Typography.Text style={{ display: 'block', marginBottom: 12, whiteSpace: 'pre-wrap' }}>{miniSelected.text}</Typography.Text>
                    {miniSelected.imagePath && (
                      <img loading="lazy" className="rounded-img rounded-img-lg" src={getAbsoluteApiUrl(`/announcements/${miniSelected._id}/image`)} alt="announcement" style={{ width: '100%', height: 'auto', borderRadius: 8, background: '#f6f6f6' }} />
                    )}
                    <div style={{ marginTop: 8, color: '#888' }}>{new Date(miniSelected.createdAt).toLocaleString()}</div>
                  </div>
                )}
              </Drawer>
            </Card>

            {/* Completed Requests card removed per user request */}
          </Col>
        </Row>
        {/* Documents Modal - shows processed documents from documents collection */}
        <Modal
          open={docsModalVisible}
          title={`Processed Documents (${allDocuments.length})`}
          onCancel={() => { setDocsModalVisible(false); }}
          footer={null}
          width={screens.xs ? '95%' : screens.md ? 1000 : 1200}
          styles={{ body: { padding: '12px 18px' } }}
        >
          <Spin spinning={docsLoading}>
            <div style={{ display: 'flex', gap: 12, marginBottom: 12, flexDirection: screens.xs ? 'column' : 'row', alignItems: screens.xs ? 'stretch' : 'center' }}>
              <Input.Search
                placeholder="Search by title, filename, transaction code or uploader"
                allowClear
                enterButton={false}
                onSearch={(v) => setDocsSearch(String(v || '').trim())}
                onChange={(e) => setDocsSearch(e.target.value)}
                style={{ maxWidth: 420, flex: 1 }}
                value={docsSearch}
              />
              <div style={{ marginLeft: 'auto', display: 'flex', gap: 8 }}>
                <Button onClick={() => { setDocsSearch(''); }}>Clear</Button>
                <Button type="primary" onClick={() => { setAllDocuments(allDocuments.slice().sort((a,b) => new Date(b.createdAt||0).getTime() - new Date(a.createdAt||0).getTime())); }}>Sort Newest</Button>
              </div>
            </div>

            <Table
              dataSource={allDocuments.filter(d => {
                if (!docsSearch) return true;
                const q = docsSearch.toLowerCase();
                const candidates = [d.title, d.filename, d.name, d.transactionCode, d.transactioncode, (d.uploadedBy && (d.uploadedBy.username || d.uploadedBy.name)) || d.uploadedBy || ''];
                return candidates.some((c: any) => c && String(c).toLowerCase().includes(q));
              })}
              rowKey={(r: any) => r._id || r.id || r.filename}
              pagination={{ pageSize: 10 }}
              scroll={{ x: 'max-content' }}
              columns={[
                {
                  title: 'Title',
                  dataIndex: 'title',
                  key: 'title',
                  render: (t: any, r: any) => (
                    <div style={{ display: 'flex', gap: 10, alignItems: 'center' }}>
                      <AppAvatar size={36} icon={<FileTextOutlined />} />
                      <div style={{ minWidth: 0 }}>
                        <div style={{ fontWeight: 600, whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>{t || r.filename || r.name || 'Untitled'}</div>
                        <div style={{ fontSize: 12, color: '#888' }}>{r.sourceTemplateName || r.templateName || ''}</div>
                      </div>
                    </div>
                  )
                },
                { title: 'Transaction', dataIndex: 'transactionCode', key: 'transactionCode', responsive: ['sm'], render: (v: any) => v || '-' },
                { title: 'Uploaded By', dataIndex: 'uploadedBy', key: 'uploadedBy', responsive: ['md'], render: (u: any) => (u && (u.username || u.name)) || (typeof u === 'string' ? u : '-') },
                { title: 'Date', dataIndex: 'createdAt', key: 'createdAt', responsive: ['sm'], render: (d: any) => formatDate(d) },
                {
                  title: 'Actions',
                  key: 'action',
                  width: 220,
                  render: (_: any, rec: any) => (
                    <Space>
                      <Button size="small" onClick={() => handleDownloadProcessed(rec)}>Download</Button>
                      <Button size="small" onClick={() => { setPreviewDoc(rec); setPreviewVisible(true); }}>Preview</Button>
                      { (rec.transactionCode || rec.transactioncode) && (
                        <Button size="small" onClick={() => { navigator.clipboard?.writeText(rec.transactionCode || rec.transactioncode || ''); notification.success({ message: 'Copied', description: 'Transaction code copied to clipboard' }); }}>Copy TX</Button>
                      ) }
                    </Space>
                  )
                }
              ]}
            />
          </Spin>
          {/* Preview Drawer for a processed document */}
          <Drawer
            title={previewDoc ? (previewDoc.title || previewDoc.filename || 'Document') : 'Document'}
            placement="right"
            onClose={() => { setPreviewVisible(false); setPreviewDoc(null); }}
            open={previewVisible}
            width={screens.xs ? '95%' : 560}
          >
            {previewDoc ? (
              <div style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <div>
                    <div style={{ fontWeight: 700 }}>{previewDoc.title || previewDoc.filename || 'Untitled'}</div>
                    <div style={{ fontSize: 12, color: '#888' }}>{previewDoc.sourceTemplateName || previewDoc.templateName || ''}</div>
                  </div>
                  <div>
                    <Button onClick={() => handleDownloadProcessed(previewDoc)} type="primary">Download</Button>
                  </div>
                </div>

                <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
                  <Tag color="blue">Created: {formatDate(previewDoc.createdAt)}</Tag>
                  {previewDoc.transactionCode && <Tag color="purple">TX: {previewDoc.transactionCode}</Tag>}
                  {previewDoc.uploadedBy && <Tag>By: {(previewDoc.uploadedBy.username || previewDoc.uploadedBy.name) || previewDoc.uploadedBy}</Tag>}
                </div>

                <div style={{ color: '#444', fontSize: 13 }}>
                  <div style={{ marginBottom: 8 }}>{previewDoc.description || previewDoc.notes || ''}</div>
                  <div style={{ marginTop: 8 }}>
                    <Button type="link" onClick={() => { navigator.clipboard?.writeText(previewDoc.transactionCode || ''); notification.success({ message: 'Copied', description: 'Transaction code copied' }); }}>Copy Transaction Code</Button>
                  </div>
                </div>
              </div>
            ) : (
              <div>No document selected</div>
            )}
          </Drawer>
          <Modal
            title="Manage Inquiries"
            open={manageModalVisible}
            onCancel={() => { setManageModalVisible(false); setManageTableData(null); }}
            footer={null}
            width={900}
          >
            <Table
              dataSource={manageTableData !== null ? manageTableData : sortByDateDesc(inquiries)}
              rowKey={(r: any) => r._id}
              pagination={{ pageSize: 10 }}
              onRow={(record) => ({
                onClick: () => {
                  // mark viewed and navigate to the thread
                  if (record && record._id) markInquiryViewed(String(record._id));
                  setManageModalVisible(false);
                  navigate('/staff/inbox', { state: { openInquiryId: record._id } });
                }
              })}
            >
              <Table.Column
                title="Name"
                dataIndex="username"
                key="username"
                render={(v: any, r: any) => v || r.residentName || r.subject || 'Unknown'}
              />
              <Table.Column
                title="Type of Inquiry"
                dataIndex="type"
                key="type"
                render={(t: any) => (t ? String(t) : 'General')}
              />
              <Table.Column
                title="Status"
                dataIndex="status"
                key="status"
                render={(s: any) => getStatusTag(s)}
              />
              <Table.Column
                title="Date Inquired"
                dataIndex="createdAt"
                key="createdAt"
                render={(d: any) => formatDate(d)}
              />
            </Table>
          </Modal>
        </Modal>

        {/* Document Response Modal */}
        <Modal
          open={!!selectedDocument}
          title={selectedDocument ? `${documentStatus} Document Request` : ''}
          onCancel={() => { setSelectedDocument(null); setResponseText(''); setDocumentStatus(''); }}
          footer={selectedDocument ? [
            <Input.TextArea
              key="notes"
              rows={4}
              value={responseText}
              onChange={e => setResponseText(e.target.value)}
              placeholder="Type notes..."
            />,
            <Button
              key="submit"
              type="primary"
              disabled={!responseText.trim()}
              onClick={handleDocumentAction}
            >Submit</Button>
          ] : null}
        >
          {selectedDocument && (
            <div>
              <Typography.Text strong>Type:</Typography.Text> {formatDocumentType(selectedDocument.type || selectedDocument.title)}<br />
              <Typography.Text strong>Description:</Typography.Text> {selectedDocument.description || ''}<br />
              <Typography.Text strong>Requested by:</Typography.Text> {selectedDocument.username || 'Unknown'}<br />
              <Typography.Text strong>Barangay ID:</Typography.Text> {selectedDocument.barangayID || 'Unknown'}<br />
              <Typography.Text strong>Date:</Typography.Text> {formatDate(selectedDocument.dateRequested || '')}<br />
            </div>
          )}
        </Modal>
        {/* Inquiry Response Modal */}
        <Modal
          open={!!selectedInquiry}
          title={selectedInquiry ? `Respond to Inquiry: ${selectedInquiry.subject}` : ''}
          onCancel={() => { setSelectedInquiry(null); setResponseText(''); }}
          footer={selectedInquiry ? [
            <Input.TextArea
              key="response"
              rows={4}
              value={responseText}
              onChange={e => setResponseText(e.target.value)}
              placeholder="Type your response..."
              disabled={responding}
            />,
            <Button
              key="send"
              type="primary"
              loading={responding}
              disabled={!responseText.trim()}
              onClick={handleInquiryResponse}
            >Send Response</Button>
          ] : null}
        >
          {selectedInquiry && (
            <div>
              <Typography.Text strong>Type:</Typography.Text> {selectedInquiry.type}<br />
              <Typography.Text strong>Message:</Typography.Text> {selectedInquiry.message}<br />
              <Typography.Text strong>From:</Typography.Text> {selectedInquiry.username ? `${selectedInquiry.username} (Barangay ID: ${selectedInquiry.barangayID || 'Unknown'})` : 'Unknown'}<br />
              <Typography.Text strong>Date:</Typography.Text> {formatDate(selectedInquiry.createdAt)}<br />
            </div>
          )}
        </Modal>
        {/* Completed Requests Modal */}
        <Modal
          open={completedModalVisible}
          title={`Completed Requests (${stats.completedRequests || 0})`}
          onCancel={() => setCompletedModalVisible(false)}
          footer={null}
          width={1100}
          styles={{ body: { padding: 12 } }}
        >
          <div style={{ overflowX: 'auto' }}>
            {/* Table of completed document requests (horizontally scrollable) */}
            <Table
              dataSource={documentRequests.filter(d => (d.status || '').toLowerCase() === 'approved' || (d.status || '').toLowerCase() === 'completed')}
              rowKey={(record: any) => record._id}
              pagination={{ pageSize: 10 }}
              scroll={{ x: 1000 }}
              columns={[
                {
                  title: 'Requester',
                  dataIndex: 'username',
                  key: 'username',
                  width: 200,
                  render: (val: any, rec: any) => val || rec.requesterId?.fullName || 'Unknown'
                },
                {
                  title: 'Document',
                  dataIndex: 'type',
                  key: 'type',
                  width: 220,
                  render: (val: any, rec: any) => formatDocumentType(val || rec.title)
                },
                {
                  title: 'Description',
                  dataIndex: 'description',
                  key: 'description',
                  width: 300,
                  render: (val: any) => val || '-'
                },
                {
                  title: 'Date Requested',
                  dataIndex: 'dateRequested',
                  key: 'dateRequested',
                  width: 180,
                  render: (val: any) => formatDate(val)
                },
                {
                  title: 'Status',
                  dataIndex: 'status',
                  key: 'status',
                  width: 120,
                  render: (val: any) => getStatusTag(val)
                },
                {
                  title: 'Action',
                  key: 'action',
                  width: 120,
                  render: (_: any, rec: any) => (
                    <Button type="link" onClick={() => { setSelectedDocument(rec); setCompletedModalVisible(false); setDocumentStatus(''); }}>
                      View
                    </Button>
                  )
                }
              ]}
            />
          </div>
        </Modal>
      </div>
    </Spin>

  );
}

export default StaffDashboard;

import React, { useState } from 'react';
import { Form, Input, Button, Select, message, Card } from 'antd';
import { staffRegister } from '../services/api';
import { useNavigate } from 'react-router-dom';

const { Option } = Select;

const StaffRegisterForm: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const onFinish = async (values: any) => {
    setLoading(true);
    try {
  await staffRegister(values);
      message.success('Staff registered successfully!');
      navigate('/login');
    } catch (error: any) {
      message.error(error?.response?.data?.message || 'Registration failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card title="Staff Registration" style={{ maxWidth: 400, margin: '40px auto' }}>
      <Form layout="vertical" onFinish={onFinish}>
        <Form.Item name="fullName" label="Full Name" rules={[{ required: true }]}> <Input /> </Form.Item>
        <Form.Item name="email" label="Email" rules={[{ required: true, type: 'email' }]}> <Input /> </Form.Item>
        <Form.Item name="username" label="Username" rules={[{ required: true }]}> <Input /> </Form.Item>
        <Form.Item name="password" label="Password" rules={[{ required: true, min: 6 }]}> <Input.Password /> </Form.Item>
        <Form.Item name="department" label="Department" rules={[{ required: true }]}> <Input /> </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit" loading={loading} block>Register as Staff</Button>
        </Form.Item>
      </Form>
    </Card>
  );
};

export default StaffRegisterForm;

import React, { useEffect, useState } from 'react';
import { Card, Typography, Statistic, Row, Col, Spin } from 'antd';
import { UserOutlined, CrownOutlined, TeamOutlined, HomeOutlined, FileTextOutlined, NotificationOutlined } from '@ant-design/icons';
import { adminAPI, documentsAPI, contactAPI } from '../services/api';

const StatsPanel: React.FC = () => {
  const [stats, setStats] = useState<any | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        // Fetch supporting public resources in parallel (files, documents, announcements)
        const [filesRes, docRequestsRes, announcementsRes] = await Promise.all([
          documentsAPI.listFiles().catch(() => ([])),
          documentsAPI.getAllDocuments().catch(() => ([])),
          contactAPI.getAnnouncements().catch(() => ([]))
        ]);

        // Attempt to fetch admin statistics, but tolerate 401/unauthenticated errors
        let resStats: any = null;
        try {
          resStats = await adminAPI.getSystemStatistics();
        } catch (statErr) {
          console.warn('Could not fetch admin statistics (likely unauthenticated):', String(statErr));
          resStats = null;
        }

        if (mounted) {
          // attach filesCount, docRequestsCount and announcementsCount to stats for display
          const filesCount = Array.isArray(filesRes) ? filesRes.length : (filesRes && filesRes.length) || 0;
          const docRequestsCount = Array.isArray(docRequestsRes) ? docRequestsRes.length : (docRequestsRes && docRequestsRes.length) || 0;
          const announcementsCount = Array.isArray(announcementsRes) ? announcementsRes.length : (announcementsRes && announcementsRes.length) || 0;
          setStats({ ...(resStats || {}), filesCount, docRequestsCount, announcementsCount });
        }
      } catch (err) {
        console.error('Failed to load system statistics', err);
      } finally {
        if (mounted) setLoading(false);
      }
    })();
    return () => { mounted = false; };
  }, []);

  if (loading) return <div style={{ width: '100%', textAlign: 'center' }}><Spin /></div>;
  if (!stats) return <div style={{ textAlign: 'center' }}><Typography.Text type="secondary">No statistics available</Typography.Text></div>;

  const users = stats.users || {};
  const documents = stats.documents || {};
  const filesCount = stats.filesCount ?? 0;

  // Arrange items so that with a 3-column layout the Documents tile falls under Admins
  const items = [
    { key: 'users', title: 'Total Users', value: users.total ?? 0, icon: <UserOutlined style={{ color: '#9254de', fontSize: 20 }} /> },
    { key: 'admins', title: 'Admins', value: users.byRole?.admin ?? 0, icon: <CrownOutlined style={{ color: '#f5a623', fontSize: 20 }} /> },
    { key: 'staff', title: 'Staff', value: users.byRole?.staff ?? 0, icon: <TeamOutlined style={{ color: '#1677ff', fontSize: 20 }} /> },
    { key: 'residents', title: 'Residents', value: users.byRole?.resident ?? 0, icon: <HomeOutlined style={{ color: '#22c55e', fontSize: 20 }} /> },
    { key: 'announcements', title: 'Announcements', value: stats.announcementsCount ?? 0, icon: <NotificationOutlined style={{ color: '#faad14', fontSize: 20 }} /> },
    { key: 'docFiles', title: 'Documents Available', value: filesCount, icon: <FileTextOutlined style={{ color: '#0ea5e9', fontSize: 20 }} /> },
  ];

  return (
    <Card className="glass-card stats-card" style={{ width: '100%' }}>
      <Typography.Title level={4}>Quick Stats</Typography.Title>
      <div style={{ height: 6, background: 'linear-gradient(90deg, rgba(22,119,255,0.08), rgba(146,84,222,0.08))', borderRadius: 4, marginBottom: 12 }} />

      <Row gutter={[12,12]} style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexWrap: 'wrap' }}>
        {items.map(it => (
          <Col key={it.key} xs={12} sm={8} md={8} lg={8} style={{ display: 'flex' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: 12, width: '100%' }}>
              <div style={{ width: 44, height: 44, display: 'flex', alignItems: 'center', justifyContent: 'center', background: 'rgba(255,255,255,0.9)', borderRadius: 8, boxShadow: '0 6px 14px rgba(2,6,23,0.06)' }}>
                {it.icon}
              </div>
              <div style={{ flex: 1 }}>
                <Statistic title={it.title} value={it.value} />
              </div>
            </div>
          </Col>
        ))}
      </Row>
    </Card>
  );
};

export default StatsPanel;

import React, { useState } from 'react';
import { Button, Tooltip, Upload, message } from 'antd';
import { UploadOutlined, EyeOutlined, DownloadOutlined, DeleteOutlined, FileWordOutlined } from '@ant-design/icons';
import { axiosInstance, axiosPublic } from '../services/api';
import styles from './TemplatesManager.module.css';

const getLabel = (filename?: string) =>
  filename ? filename.replace(/_/g, " ").replace(/\.docx$/, "") : "Untitled";

const TemplatesManager: React.FC = () => {
  const [templateList, setTemplateList] = useState<any[]>([]);
  const [previewId, setPreviewId] = useState<string | null>(null);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [downloadError, setDownloadError] = useState<string | null>(null);
  const [previewType, setPreviewType] = useState<'html' | 'pdf' | null>(null);
  const [htmlContent, setHtmlContent] = useState<string>('');

  React.useEffect(() => {
    // Fetch files from Templates folder
    const fetchTemplatesFolderFiles = async () => {
      try {
        await axiosPublic.get('/templates/list');
      } catch (err) {
        // Ignore error for now
      }
    };
    const fetchTemplates = async () => {
      setLoading(true);
      setError(null);
        try {
          const res = await axiosPublic.get('/documents/list');
          setTemplateList(res.data || []);
        } catch (err) {
          setError('Could not load templates.');
        }
      setLoading(false);
    };
    fetchTemplates();
  }, []);

  return (
    <div className={styles.wrapper}>
      <h2 className={styles.heading}>≡ƒôé Templates Manager</h2>
      {/* GridFS Files Section */}
      <div style={{ marginBottom: 32 }}>
      <h3 style={{ fontWeight: "bold", fontSize: "1.1rem", marginBottom: "0.5rem", color: '#374151' }}></h3>
        {loading && <div>Loading templates...</div>}
        {error && <div style={{ color: 'red' }}>{error}</div>}
        <div className={styles.grid}>
          {templateList.filter((file: any) => file && file.filename).map((file: any) => (
            <div key={file._id} className={styles.card}>
              <img src="/word.png" alt="Word Icon" className={styles.icon} />
              <div className={styles.cardBody}>
                <span className={styles.cardTitle}>{getLabel(file.filename)}</span>
                <div className={styles.actions}>
                  <Tooltip title="Preview">
                    <Button
                      icon={<EyeOutlined />}
                      onClick={async (e) => {
                        e.stopPropagation();
                        if (previewId === file._id) {
                          setPreviewId(null);
                          setHtmlContent('');
                          return;
                        }
                        setPreviewId(file._id);
                        setHtmlContent('');
                          try {
                            const res = await axiosPublic.get(`/documents/preview/${file._id}?format=html`, { responseType: 'text' });
                            setHtmlContent(res.data || '');
                          } catch (err) {
                            setHtmlContent('<div style="color:red">Failed to load preview.</div>');
                          }
                      }}
                    >Preview</Button>
                  </Tooltip>
                  <Tooltip title="Download">
                    <Button
                      type="primary"
                      icon={<DownloadOutlined />}
                      onClick={async () => {
                        setDownloadError(null);
                        try {
                          const res = await axiosInstance.get(`/documents/original/${file._id}`, { responseType: 'blob' });
                          const blob = res.data;
                          const url = window.URL.createObjectURL(blob);
                          const a = document.createElement('a');
                          a.href = url;
                          a.download = file.filename;
                          document.body.appendChild(a);
                          a.click();
                          a.remove();
                          window.URL.revokeObjectURL(url);
                        } catch (err) {
                          setDownloadError(`Failed to download ${file.filename} from MongoDB.`);
                        }
                      }}
                    >Download</Button>
                  </Tooltip>
                  <Tooltip title="Remove">
                    <Button
                      danger
                      icon={<DeleteOutlined />}
                      onClick={async () => {
                        setLoading(true);
                        setError(null);
                          try {
                            const res = await axiosInstance.delete(`/documents/file/${file._id}`);
                            const data = res.data;
                            if (data && data.success) {
                              // Refresh list
                              const resList = await axiosPublic.get('/documents/list');
                              setTemplateList(resList.data || []);
                            } else {
                              setError('Delete failed.');
                            }
                          } catch (err) {
                            setError('Delete failed.');
                          }
                        setLoading(false);
                      }}
                    >Remove</Button>
                  </Tooltip>
                </div>
                {previewId === file._id && htmlContent && (
                  <div className={styles.previewModal}>
                    <div className={styles.previewHeader}>
                      <span className={styles.previewTitle}>Preview</span>
                      <button className={styles.previewClose} onClick={() => { setPreviewId(null); setHtmlContent(''); }}>Close</button>
                    </div>
                    <div dangerouslySetInnerHTML={{ __html: htmlContent }} />
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
      {downloadError && (
        <div style={{ color: 'red', marginBottom: 16 }}>{downloadError}</div>
      )}
      {/* Upload Section */}
      <div className={styles.uploadWrap}>
        <Upload
          accept=".docx"
          showUploadList={false}
          customRequest={async ({ file, onSuccess, onError }) => {
            setLoading(true);
            setError(null);
            const formData = new FormData();
            formData.append('file', file);
              try {
              const res = await axiosInstance.post('/documents/upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' }
              });
              const data = res.data;
              if (data && data.success) {
                // Refresh list
                const resList = await axiosPublic.get('/documents/list');
                setTemplateList(resList.data || []);
                message.success('Upload successful');
                onSuccess && onSuccess('ok');
              } else {
                setError('Upload failed.');
                message.error('Upload failed');
                onError && onError(new Error('Upload failed'));
              }
            } catch (err) {
              setError('Upload failed.');
              message.error('Upload failed');
              onError && onError(new Error('Upload failed'));
            }
            setLoading(false);
          }}
        >
          <Button icon={<UploadOutlined />} type="primary">Upload .docx</Button>
        </Upload>
      </div>
    </div>
  );
};

