                ].filter(Boolean)}
              >
                <List.Item.Meta
                  avatar={<AppAvatar icon={(notification.type || '').toString().toLowerCase().includes('staff') ? <TeamOutlined /> : <BellOutlined />} style={{ backgroundColor: notification.read ? '#8c8c8c' : ((notification.type || '').toString().toLowerCase().includes('staff') ? '#faad14' : '#1890ff') }} />}
                  title={<Text strong={(notification.type || '').toString().toLowerCase().includes('staff')}>{notification.message}</Text>}
                  description={
                    <div>
                      <Text type="secondary">{new Date(notification.createdAt).toLocaleString()}</Text>
                      {((notification.type || '').toString().toLowerCase().includes('staff') && notification.data) && (
                        <div style={{ marginTop: 8 }}>
                          <Text strong>Full Name:</Text> {notification.data.fullName || 'N/A'}<br />
                          <Text strong>Email:</Text> {notification.data.email || 'N/A'}<br />
                          <Text strong>Username:</Text> {notification.data.username || 'N/A'}
                        </div>
                      )}
                    </div>
                  }
                />
              </List.Item>
            )}
            size="small"
          />
        )
      )}
  <Button type="link" style={{ position: 'absolute', right: 16, bottom: 8, fontSize: 13, color: '#1890ff' }} onClick={() => navigate('/admin/notifications')}>View all</Button>
    </Card>
  );

  // Verification widget for admin dashboard
  const handleApproveVerif = async (arg: any) => {
    // arg may be a userId string or the verification request object
    try {
      setVerifsLoading(true);
      if (arg && typeof arg === 'object') {
        const reqId = arg._id;
        const userId = arg.userId?._id || arg.userId;
        // mark request approved on server (if endpoint exists)
        try { await verificationAPI.approveRequest(reqId); } catch (e) { /* best-effort */ }
        // set the user verified
        if (userId) await verificationAPI.verifyUser(userId, true);
      } else if (typeof arg === 'string') {
        await verificationAPI.verifyUser(arg, true);
      }
      await loadVerifs();
    } catch (err) {
      console.error('Failed to verify user', err);
    } finally {
      setVerifsLoading(false);
    }
  };

  const handleRejectVerif = async (arg: any) => {
    try {
      setVerifsLoading(true);
      if (arg && typeof arg === 'object') {
        const reqId = arg._id;
        const userId = arg.userId?._id || arg.userId;
        try { await verificationAPI.rejectRequest(reqId); } catch (e) { /* best-effort */ }
        if (userId) await verificationAPI.verifyUser(userId, false);
      } else if (typeof arg === 'string') {
        // no request id available; just flip user verified to false
        await verificationAPI.verifyUser(arg, false);
      }
      await loadVerifs();
    } catch (err) {
      console.error('Failed to reject verification', err);
    } finally {
      setVerifsLoading(false);
    }
  };

  const openCheckId = (req: any) => {
    setSelectedVerif(req);
    setVerifModalVisible(true);
  };

  const renderVerificationWidget = () => (
    <Card
      title={<Space><UserOutlined /> Verification Requests</Space>}
      style={{ marginTop: 0, background: '#fff', borderRadius: 12, boxShadow: '0 2px 8px #d9d9d933', border: '1px solid #f0f0f0' }}
      size="small"
      hoverable={false}
    >
      {verifsLoading ? (
        <div style={{ textAlign: 'center', padding: 24 }}><Spin /></div>
      ) : (!verifs || verifs.length === 0) ? (
        <Empty description="No verification requests" />
      ) : (
        <List
          dataSource={verifs.slice(0, 6)}
          renderItem={(item: any) => (
            <List.Item
              actions={[
                <Button key="view" size="small" onClick={() => openCheckId(item)}>Check ID</Button>,
                <Button key="verify" type="primary" size="small" onClick={() => handleApproveVerif(item.userId?._id || item.userId)}>Verify</Button>,
                <Button key="reject" danger size="small" onClick={() => handleRejectVerif(item.userId?._id || item.userId)}>Reject</Button>
              ]}
            >
              <List.Item.Meta
                title={<span style={{ fontWeight: 600 }}>{(item.userId && (item.userId.fullName || item.userId.username)) || 'Unknown Resident'}</span>}
                description={<span style={{ color: '#888' }}>{item.status || 'pending'} ┬╖ {new Date(item.createdAt).toLocaleString()}</span>}
              />
            </List.Item>
          )}
          size="small"
        />
      )}

      <Button type="link" style={{ marginTop: 12 }} onClick={() => navigate('/admin/verification-requests')}>View all</Button>

      <Modal
        title="Verification Files"
        open={verifModalVisible}
        onCancel={() => setVerifModalVisible(false)}
        footer={null}
        width={720}
      >
        {selectedVerif ? (
          <div>
            <p><strong>Resident:</strong> {(selectedVerif.userId && (selectedVerif.userId.fullName || selectedVerif.userId.username)) || 'Unknown'}</p>
            <div style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
              {(selectedVerif.gridFileIds || []).map((id: string) => (
                <div key={id} style={{ border: '1px solid #f0f0f0', padding: 8, borderRadius: 6 }}>
                    <div style={{ marginBottom: 8 }}>
                      <a href={getAbsoluteApiUrl(`/verification/file/${id}`)} target="_blank" rel="noreferrer">Open file in new tab</a>
                    </div>
                  {/* inline preview for images if possible */}
                    <div>
                      <img src={getAbsoluteApiUrl(`/verification/file/${id}`)} alt="id" style={{ maxWidth: '100%', maxHeight: 400 }} onError={() => { /* ignore errors for non-images */ }} />
                    </div>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <Empty description="No files" />
        )}
      </Modal>
    </Card>
  );

  // Mini announcements viewer to replace Recent Activity
  const [miniAnns, setMiniAnns] = useState<any[]>([]);
  const [miniLoading, setMiniLoading] = useState(false);
  const [miniSelected, setMiniSelected] = useState<any | null>(null);
  const [drawerVisible, setDrawerVisible] = useState(false);

  const fetchMiniAnnouncements = async () => {
    setMiniLoading(true);
    try {
      const data = await contactAPI.getAnnouncements();
      setMiniAnns(Array.isArray(data) ? data.slice(0, 6) : []); // show up to 6
    } catch (err) {
      console.error('Failed to load mini announcements', err);
      setMiniAnns([]);
    } finally {
      setMiniLoading(false);
    }
  };

  useEffect(() => { fetchMiniAnnouncements(); }, []);

  // helper: simple time-ago
  const timeAgo = (iso?: string) => {
    if (!iso) return ''; 
    const d = new Date(iso);
    const diff = Math.floor((Date.now() - d.getTime()) / 1000);
    if (diff < 60) return `${diff}s ago`;
    if (diff < 3600) return `${Math.floor(diff / 60)}m ago`;
    if (diff < 86400) return `${Math.floor(diff / 3600)}h ago`;
    if (diff < 604800) return `${Math.floor(diff / 86400)}d ago`;
    return d.toLocaleDateString();
  };

  const renderRecentActivity = () => (
    <Card
      title={<Space><FileTextOutlined /> Announcements</Space>}
      style={{ marginTop: 0, background: '#fafbfc', borderRadius: 12, boxShadow: '0 2px 8px #d9d9d933', border: '1px solid #f0f0f0', position: 'relative' }}
      styles={{ body: { padding: 12 } }}
      size="small"
      hoverable={false}
    >
      {miniAnns.length === 0 ? (
        <Empty
          image={<FileTextOutlined style={{ fontSize: 42, color: '#d9d9d9' }} />}
          description={<span style={{ color: '#888' }}>No announcements</span>}
        />
      ) : (
  <div style={{ maxHeight: 320, overflowY: 'auto', paddingRight: 6, paddingBottom: 48 }}>
          <List
            loading={miniLoading}
            dataSource={miniAnns}
            renderItem={(item) => (
              <List.Item style={{ cursor: 'pointer', padding: '10px 8px', alignItems: 'center' }} onClick={() => { setMiniSelected(item); setDrawerVisible(true); }}>
                <List.Item.Meta
                  title={<div style={{ display: 'flex', alignItems: 'flex-start', gap: 8 }}>
                    <div style={{ flex: 1, minWidth: 0 }}>
                      <div style={{ fontWeight: 600, fontSize: 13, display: '-webkit-box', WebkitLineClamp: 3, WebkitBoxOrient: 'vertical', overflow: 'hidden', textOverflow: 'ellipsis', lineHeight: '1.2em' }}>{item.text || 'Untitled'}</div>
                      <div style={{ fontSize: 12, color: '#888', marginTop: 6 }}>{timeAgo(item.createdAt)}</div>
                    </div>
                  </div>}
                  description={null}
                />
                  {item.imagePath && (
                    <div style={{ marginLeft: 12, width: 92, display: 'flex', justifyContent: 'flex-end', flexShrink: 0 }}>
                      <img loading="lazy" className="rounded-img" src={getAbsoluteApiUrl(`/announcements/${item._id}/image`)} alt="ann" style={{ width: 92, height: 60, objectFit: 'cover', borderRadius: 6, background: '#f0f0f0' }} />
                    </div>
                  )}
              </List.Item>
            )}
            size="small"
          />
        </div>
      )}

      {/* Footer area to host Manage link and avoid overlapping the scrollable list */}
      <div style={{ marginTop: 8, display: 'flex', justifyContent: 'flex-end' }}>
        <Button type="link" style={{ fontSize: 13, color: '#1890ff' }} onClick={() => navigate('/admin/announcements')}>Manage</Button>
      </div>

      <Drawer open={drawerVisible} onClose={() => { setDrawerVisible(false); setMiniSelected(null); }} title="Announcement" width={720} placement="right">
        {miniSelected && (
          <div>
            <Text style={{ display: 'block', marginBottom: 12, whiteSpace: 'pre-wrap' }}>{miniSelected.text}</Text>
                    {miniSelected.imagePath && (
                      <img loading="lazy" className="rounded-img rounded-img-lg" src={getAbsoluteApiUrl(`/announcements/${miniSelected._id}/image`)} alt="announcement" style={{ width: '100%', height: 'auto', borderRadius: 8, background: '#f6f6f6' }} />
                    )}
            <div style={{ marginTop: 8, color: '#888' }}>{timeAgo(miniSelected.createdAt)}</div>
          </div>
        )}
      </Drawer>
    </Card>
  );

  function renderInbox(): React.ReactNode {
    return (
      <Card
        title={<Space><MailOutlined /> Inquiries Inbox (Assigned to You)</Space>}
        style={{ marginTop: 0, background: '#fafbfc', borderRadius: 12, boxShadow: '0 2px 8px #d9d9d933', border: '1px solid #f0f0f0', position: 'relative' }}
  styles={{ body: { padding: 16 } }}
        size="small"
        hoverable={false}
      >
        {inboxInquiries.length === 0 ? (
          <Empty
            image={<MailOutlined style={{ fontSize: 48, color: '#d9d9d9' }} />}
            description={<span style={{ color: '#888' }}>No assigned inquiries</span>}
          />
        ) : (
          <List
            dataSource={inboxInquiries}
            renderItem={(inquiry: any) => (
              <List.Item
                actions={[
                  <Button key="view" type="link" onClick={() => setSelectedInquiry(inquiry)}>
                    View / Respond
                  </Button>
                ]}
              >
                <List.Item.Meta
                  title={<Text strong>{inquiry.subject}</Text>}
                  description={
                    <>
                      <Text type="secondary">{inquiry.type}</Text><br />
                      <Text>{inquiry.message}</Text><br />
                      <Text type="secondary">
                        Submitted by: {inquiry.createdBy?.fullName || inquiry.createdBy?.username || 'Resident'} on {new Date(inquiry.createdAt).toLocaleString()}
                      </Text>
                    </>
                  }
                />
              </List.Item>
            )}
            size="small"
          />
        )}
    <Button type="link" style={{ position: 'absolute', right: 16, bottom: 8, fontSize: 13, color: '#1890ff' }} onClick={() => navigate('/admin/inquiries')}>View all</Button>
        <Modal
          title="Document Requests"
          open={documentsModalVisible}
          onCancel={() => setDocumentsModalVisible(false)}
          footer={null}
          width={1200}
        >
          {/* Modal content here */}
          <Table
            columns={documentColumns}
            dataSource={documentRequests}
            rowKey={record => record._id || record.dateRequested + record.requestedByName}
            pagination={{ pageSize: 10 }}
          />
        </Modal>
      </Card>
    );
  }

  return (
    <Spin spinning={loading}>
      <div style={{ padding: 24, backgroundColor: '#f0f2f5', minHeight: '100vh' }}>
        <Title level={4} style={{ marginBottom: 24 }}>Admin Dashboard</Title>
        {renderStatCards()}
        <Row gutter={[24, 24]} style={{ marginTop: 0 }}>
          <Col xs={24} lg={12}>
            {renderNotifications()}
          </Col>
          <Col xs={24} lg={12}>
            {renderRecentActivity()}
          </Col>
        </Row>
        <Row gutter={[24, 24]} style={{ marginTop: 24 }}>
          <Col xs={24}>
            {renderVerificationWidget()}
          </Col>
        </Row>
        {/* Modal is rendered inside renderInbox above */}
      </div>
    </Spin>
  );
};

export default AdminDashboard;

import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { Card, Input, Upload, Button, message, Typography, List, Popconfirm, Image, Modal, Space, Tooltip } from 'antd';
import { UploadOutlined, CheckCircleOutlined, CloseCircleOutlined, DeleteOutlined, EyeOutlined, EditOutlined } from '@ant-design/icons';
import { adminAPI, getAbsoluteApiUrl } from '../../services/api';

const { TextArea } = Input;

const Announcements: React.FC = () => {
  const [text, setText] = useState('');
  const [file, setFile] = useState<File | null>(null);
  const [submitting, setSubmitting] = useState(false);
  const [anns, setAnns] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [viewing, setViewing] = useState<any | null>(null);
  const [editing, setEditing] = useState<any | null>(null);
  const [editText, setEditText] = useState('');
  const [editFile, setEditFile] = useState<File | null>(null);

  const { user } = useAuth();

  const loadAnns = async () => {
    // Avoid calling admin endpoint when user isn't admin or no token available
    if (!user || user.role !== 'admin') return;
    const token = localStorage.getItem('token');
    if (!token) {
      // No token - likely not logged in yet
      return;
    }
    setLoading(true);
    try {
      const data = await adminAPI.listAdminAnnouncements();
      setAnns(Array.isArray(data) ? data : []);
    } catch (err: any) {
      // Handle 401 explicitly
      if (err?.response?.status === 401) {
        message.error('Not authorized. Please sign in as an admin.');
      } else {
        console.error('Failed to load announcements', err);
        message.error('Failed to load announcements');
      }
    } finally {
      setLoading(false);
    }
  };

  // Load admin announcements only when we have an authenticated admin user
  useEffect(() => {
    if (user?.role === 'admin') {
      loadAnns();
    }
  }, [user]);

  const beforeUpload = (f: File) => {
    // accept only images
    const isImage = f.type.startsWith('image/');
    if (!isImage) {
      message.error('Only image files are allowed');
      return Upload.LIST_IGNORE;
    }
    // keep single file
    setFile(f);
    return Upload.LIST_IGNORE; // Prevent auto upload
  };

  const removeFile = () => setFile(null);

  const beforeEditUpload = (f: File) => {
    const isImage = f.type.startsWith('image/');
    if (!isImage) {
      message.error('Only image files are allowed');
      return Upload.LIST_IGNORE;
    }
    setEditFile(f);
    return Upload.LIST_IGNORE;
  };

  const handleSubmit = async () => {
    if (!text || text.trim() === '') return message.warning('Announcement text cannot be empty');
    setSubmitting(true);
    try {
      const form = new FormData();
      form.append('text', text.trim());
      if (file) form.append('image', file, file.name);
      // POST to admin announcements endpoint
      await adminAPI.createAnnouncement(form);
      message.success('Announcement posted');
      setText('');
      setFile(null);
    } catch (err: any) {
      console.error('Failed to post announcement', err);
      message.error(err?.response?.data?.message || 'Failed to post announcement');
    } finally {
      setSubmitting(false);
    }
  };

  const openView = (item: any) => setViewing(item);

  const openEdit = (item: any) => {
    setEditing(item);
    setEditText(item.text || '');
    setEditFile(null);
  };

  const handleEditSave = async () => {
    if (!editing) return;
    try {
      const form = new FormData();
      form.append('text', editText.trim());
      if (editFile) form.append('image', editFile, editFile.name);
      await adminAPI.updateAnnouncement(editing._id, form);
      message.success('Updated');
      setEditing(null);
      setEditFile(null);
      setEditText('');
      loadAnns();
    } catch (err) {
      console.error(err);
      message.error('Update failed');
    }
  };

  return (
    <>
      <Card title={<Typography.Title level={4} style={{ margin: 0 }}>{user?.role === 'admin' ? 'Post Announcement' : 'Announcements'}</Typography.Title>}>
        <div style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
          {/* Show create form only to admins */}
          {user?.role === 'admin' && (
            <>
              <TextArea value={text} onChange={e => setText(e.target.value)} rows={6} placeholder="Write your announcement here" />

              <Upload beforeUpload={beforeUpload} onRemove={removeFile} accept="image/*" maxCount={1} showUploadList={{ showPreviewIcon: false }}>
                <Button icon={<UploadOutlined />}>Upload Image (optional)</Button>
              </Upload>

              {/* Image upload confirmation */}
              {file && (
                <div style={{ display: 'flex', alignItems: 'center', gap: 8, color: '#237804' }}>
                  <CheckCircleOutlined style={{ color: '#52c41a' }} />
                  <span style={{ fontWeight: 600 }}>Image uploaded:</span>
                  <span style={{ marginLeft: 6 }}>{file.name}</span>
                  <Button type="text" icon={<CloseCircleOutlined />} onClick={removeFile} style={{ marginLeft: 8 }} />
                </div>
              )}

              <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
                <Button type="primary" onClick={handleSubmit} loading={submitting} disabled={!text.trim()}>
                  Post Announcement
                </Button>
              </div>
            </>
          )}

          <div style={{ marginTop: 12 }}>
            <Typography.Title level={5} style={{ marginBottom: 8 }}>Existing Announcements</Typography.Title>
            <List
              loading={loading}
              dataSource={anns}
              renderItem={item => (
                <List.Item actions={
                  [
                    <Tooltip key="view" title="View" placement="top">
                      <Button shape="circle" size="small" icon={<EyeOutlined />} onClick={() => openView(item)} aria-label={`View announcement ${item._id}`} />
                    </Tooltip>,
                    user?.role === 'admin' ? (
                      <Tooltip key="edit" title="Edit" placement="top">
                        <Button shape="circle" size="small" icon={<EditOutlined />} onClick={() => openEdit(item)} aria-label={`Edit announcement ${item._id}`} />
                      </Tooltip>
                    ) : null,
                    user?.role === 'admin' ? (
                      <Popconfirm key="del" title="Delete announcement?" onConfirm={async () => {
                        try {
                          await adminAPI.deleteAnnouncement(item._id);
                          message.success('Deleted');
                          loadAnns();
                        } catch (err) {
                          console.error(err);
                          message.error('Delete failed');
                        }
                      }}>
                        <Tooltip title="Delete" placement="top">
                          <Button danger shape="circle" size="small" icon={<DeleteOutlined />} aria-label={`Delete announcement ${item._id}`} />
                        </Tooltip>
                      </Popconfirm>
                    ) : null
                  ].filter(Boolean) as any
                }>
                  <List.Item.Meta title={new Date(item.createdAt).toLocaleString()} description={item.text} />
                  {item.imagePath && <Image className="rounded-img" width={80} src={getAbsoluteApiUrl(`/announcements/${item._id}/image`)} />}
                </List.Item>
              )}
            />
          </div>
        </div>
      </Card>

      {/* View Modal */}
      <Modal
        title={viewing ? `Announcement - ${new Date(viewing.createdAt).toLocaleString()}` : 'Announcement'}
        open={!!viewing}
        onCancel={() => setViewing(null)}
        footer={[<Button key="close" onClick={() => setViewing(null)}>Close</Button>]}
      >
        {viewing && (
          <div>
            <p>{viewing.text}</p>
            {viewing.imagePath && <Image className="rounded-img rounded-img-lg" src={getAbsoluteApiUrl(`/announcements/${viewing._id}/image`)} width={320} />}
          </div>
        )}
      </Modal>

      {/* Edit Modal */}
      <Modal
        title={`Edit Announcement`}
        open={!!editing}
        onCancel={() => { setEditing(null); setEditFile(null); setEditText(''); }}
        onOk={handleEditSave}
        okText="Save"
      >
        <div style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
          <TextArea value={editText} onChange={e => setEditText(e.target.value)} rows={6} />
          <Upload beforeUpload={beforeEditUpload} onRemove={() => setEditFile(null)} accept="image/*" maxCount={1} showUploadList={{ showPreviewIcon: false }}>
            <Button icon={<UploadOutlined />}>Replace Image</Button>
          </Upload>
          {editFile && (
            <div style={{ display: 'flex', alignItems: 'center', gap: 8, color: '#237804' }}>
              <CheckCircleOutlined style={{ color: '#52c41a' }} />
              <span style={{ fontWeight: 600 }}>New image selected:</span>
              <span style={{ marginLeft: 6 }}>{editFile?.name}</span>
              <Button type="text" icon={<CloseCircleOutlined />} onClick={() => setEditFile(null)} style={{ marginLeft: 8 }} />
            </div>
          )}
        </div>
      </Modal>
    </>
  );
};

export default Announcements;

import React, { useEffect, useState } from 'react';
import { Card, List, Typography, Spin, Button, Space } from 'antd';
import AppAvatar from '../AppAvatar';
import { BellOutlined, TeamOutlined, CheckCircleOutlined, ArrowLeftOutlined } from '@ant-design/icons';
import { getInbox, axiosInstance } from '../../services/api';
import { Notification } from '../../types/notification';
import { useNavigate } from 'react-router-dom';

const { Title, Text } = Typography;

const MessageInbox: React.FC = () => {
  // now use messages (inbox) which includes verification request messages
  const [messages, setMessages] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const fetchNotifications = async () => {
    setLoading(true);
    try {
  const resp = await getInbox();
  const data = resp && resp.data ? resp.data : [];
  setMessages(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Failed to fetch inbox messages', err);
      setMessages([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchNotifications();
  }, []);

  const handleMarkAsRead = async (msg: any) => {
    if (!msg._id || msg.status === 'read') return;
    setLoading(true);
    try {
      // mark the message as read via messages endpoint
      await axiosInstance.patch(`/messages/${msg._id}/read`);
      await fetchNotifications();
    } catch (err) {
      // Optionally show error
    } finally {
      setLoading(false);
    }
  };

  return (
    <Spin spinning={loading}>
      <div style={{ padding: 24, backgroundColor: '#f0f2f5', minHeight: '100vh' }}>
        <Space style={{ marginBottom: 24 }}>
          <Button type="default" icon={<ArrowLeftOutlined />} onClick={() => navigate('/admin/dashboard')}>
            Back to Dashboard
          </Button>
          <Title level={4} style={{ marginBottom: 0 }}>Message Inbox</Title>
        </Space>
        <Card>
          <List
            dataSource={Array.isArray(messages) ? messages : []}
            renderItem={notification => (
              <List.Item
                actions={[
                  notification.status === 'unread' && (
                    <Button
                      size="small"
                      icon={<CheckCircleOutlined />}
                      onClick={() => handleMarkAsRead(notification)}
                    >Mark Read</Button>
                  )
                ].filter(Boolean)}
              >
                <List.Item.Meta
                  avatar={<AppAvatar icon={<BellOutlined />} style={{ backgroundColor: notification.status === 'read' ? '#8c8c8c' : '#1890ff' }} />}
                  title={<Text strong>{notification.subject}</Text>}
                  description={
                    <div>
                      <Text type="secondary">{new Date(notification.createdAt).toLocaleString()}</Text>
                      {/* Show body for verification requests */}
                      {notification.subject === 'New Verification Request' && (
                        <div style={{ marginTop: 8 }}>
                          <Text strong>Verification request submitted.</Text>
                          <div>{notification.body}</div>
                        </div>
                      )}
                    </div>
                  }
                />
              </List.Item>
            )}
            locale={{ emptyText: 'No notifications' }}
          />
        </Card>
      </div>
    </Spin>
  );
};

