  // ChartCard component
  const ChartCard: React.FC<{ id?: string; title: string; chartType: 'pie'|'bar'|'line'|'area'; data?: any[]; loading?: boolean; colors?: string[] }> = ({ id, title, chartType, data = [], loading, colors }) => {
