  // Download full analytics (dashboard + narrative) as styled A4 PDF
  const downloadFullAnalytics = async () => {
    if (!dashboardRef.current) {
      message.error('Dashboard not available for capture');
      return;
    }

    message.loading({ content: 'Preparing full report...', key: 'report' });

    try {
      const html2canvas = (await import('html2canvas')).default;
      // capture the dashboard DOM
      const el = dashboardRef.current;
  // increase scale for better resolution
  const canvas = await html2canvas(el, { background: '#ffffff' });
      const imgData = canvas.toDataURL('image/png');

      // A4 dimensions in pt at 72 DPI: 595.28 x 841.89; jsPDF default unit is 'pt'
      const pdf = new jsPDF({ unit: 'pt', format: 'a4', orientation: 'portrait' });

      // cover page
      const title = 'Barangay Analytics Report';
      pdf.setFontSize(24);
      pdf.text(title, 40, 80);
      pdf.setFontSize(12);
      pdf.text(`Generated: ${new Date().toLocaleString()}`, 40, 110);
      pdf.addPage();

      // add dashboard image scaling to page width with margins
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = pdf.internal.pageSize.getHeight();
      const margin = 40;
      const usableWidth = pageWidth - margin * 2;
      // compute image size in PDF points based on natural image dimensions
      const tmpImg = new Image();
      tmpImg.src = imgData;
      await new Promise((res) => (tmpImg.onload = res));

      const imgWidthPts = usableWidth; // target width in PDF points
      const imgHeightPts = (tmpImg.height * imgWidthPts) / tmpImg.width;

      // convert pixels to PDF points ratio
      const pxPerPt = tmpImg.height / imgHeightPts; // pixels per PDF point
      const sliceHeightPt = pageHeight - margin * 2; // available height in PDF points
      const sliceHeightPx = Math.floor(sliceHeightPt * pxPerPt);

      // draw the full image into offscreen canvas at natural size, then slice vertically
      const offCanvas = document.createElement('canvas');
      offCanvas.width = tmpImg.width;
      offCanvas.height = tmpImg.height;
      const ctx = offCanvas.getContext('2d');
      if (!ctx) throw new Error('Canvas not supported');
      ctx.fillStyle = '#ffffff';
      ctx.fillRect(0,0,offCanvas.width, offCanvas.height);
      ctx.drawImage(tmpImg, 0, 0);

      let yOffset = 0;
      while (yOffset < offCanvas.height) {
        const sliceCanvas = document.createElement('canvas');
        sliceCanvas.width = offCanvas.width;
        sliceCanvas.height = Math.min(sliceHeightPx, offCanvas.height - yOffset);
        const sctx = sliceCanvas.getContext('2d');
        if (!sctx) break;
        sctx.fillStyle = '#ffffff';
        sctx.fillRect(0,0,sliceCanvas.width, sliceCanvas.height);
        sctx.drawImage(offCanvas, 0, yOffset, sliceCanvas.width, sliceCanvas.height, 0, 0, sliceCanvas.width, sliceCanvas.height);
        const sliceData = sliceCanvas.toDataURL('image/png');
        const drawHeightPts = sliceCanvas.height / pxPerPt; // convert slice pixels back to PDF points
        pdf.addImage(sliceData, 'PNG', margin, margin, imgWidthPts, drawHeightPts);
        yOffset += sliceHeightPx;
        if (yOffset < offCanvas.height) pdf.addPage();
      }

      pdf.save('barangay-analytics-report.pdf');
      message.success({ content: 'Report ready', key: 'report' });
    } catch (err) {
      console.error('Full report export failed', err);
      message.error({ content: 'Failed to export full report (see console).', key: 'report' });
    }
  };

  return (
    <Card
      title={<Typography.Title level={4} style={{ margin: 0 }}>Statistics & Analytics</Typography.Title>}
      style={{ borderRadius: 16, boxShadow: '0 2px 16px #40c9ff11' }}
      styles={{ body: { padding: 24 } }}
    >
      <div ref={dashboardRef} style={{ background: '#ffffff', padding: 8 }}>
      {/* Filters */}
  <Form layout={isMobile ? "vertical" : "inline"} style={{ marginBottom: 24, width: '100%', boxSizing: 'border-box' }}>
        <Form.Item label="Date Range">
          <RangePicker
            value={filters.dateRange as any}
            onChange={dates => setFilters(f => ({ ...f, dateRange: (dates ? dates.filter(Boolean) : []) as Moment[] }))}
            allowClear
          />
        </Form.Item>
        <Form.Item label="Resident Type">
          <Select
            value={filters.residentType}
            onChange={val => setFilters(f => ({ ...f, residentType: val }))}
            style={{ minWidth: 140 }}
            allowClear
            placeholder="All Types"
          >
            <Option value="">All</Option>
            <Option value="active">Active</Option>
            <Option value="inactive">Inactive</Option>
          </Select>
        </Form.Item>

        <Form.Item label="Charts">
          <Dropdown
            open={chartsDropdownOpen}
            onOpenChange={(open) => {
              if (open) {
                // initialize pending selection from current selection
                setPendingSelectedCharts(selectedCharts.slice());
              } else {
                // when closing without apply, reset pending to current
                setPendingSelectedCharts(selectedCharts.slice());
              }
              setChartsDropdownOpen(open);
            }}
            popupRender={() => (
              <div style={{ padding: 12, background: '#fff', borderRadius: 8, boxShadow: '0 6px 20px rgba(0,0,0,0.08)', minWidth: 340 }}>
                {/* Tidy vertical list with scroll when content overflows */}
                <div style={{ maxHeight: 260, overflowY: 'auto', paddingRight: 8 }} onWheel={(e) => { /* allow native wheel scrolling */ }}>
                  <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
                    {Object.keys(CHARTS).map(k => {
                      const checked = (pendingSelectedCharts || []).includes(k);
                      return (
                        <Checkbox key={k} checked={checked} onChange={(ev) => {
                          const next = new Set(pendingSelectedCharts || []);
                          if ((ev.target as HTMLInputElement).checked) next.add(k); else next.delete(k);
                          setPendingSelectedCharts(Array.from(next));
                        }}>
                          <span style={{ fontSize: 13 }}>{CHARTS[k].title}</span>
                        </Checkbox>
                      );
                    })}
                  </div>
                </div>
                <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8, marginTop: 12 }}>
                  <Button size="small" onClick={() => { setPendingSelectedCharts(selectedCharts.slice()); setChartsDropdownOpen(false); }}>Cancel</Button>
                  <Button size="small" type="primary" onClick={() => { setSelectedCharts(pendingSelectedCharts.slice()); setChartsDropdownOpen(false); }}>Apply</Button>
                </div>
              </div>
            )}
            trigger={['click']}
          >
            <Button>
              Charts <DownOutlined />
            </Button>
          </Dropdown>
        </Form.Item>
        <Form.Item style={{ width: '100%', boxSizing: 'border-box' }}>
          {/* Use a wrapping flex container so buttons stack/wrap on narrow screens */}
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8, alignItems: 'center', width: '100%', boxSizing: 'border-box' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: 8, minWidth: 200 }}>
              <Switch checked={autoEnableWhenData} onChange={v => setAutoEnableWhenData(!!v)} />
              <span style={{ fontSize: 13 }}>Enable when data exists</span>
            </div>
            {/* Buttons container: on mobile take full row so buttons don't overflow */}
            <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap', flex: isMobile ? '0 0 100%' : '0 1 auto', alignItems: 'center', width: isMobile ? '100%' : undefined, boxSizing: 'border-box', paddingRight: 8 }}>
              <Button onClick={() => { setSettingsOpen(true); setSettingsChartId(selectedCharts[0] || 'gender'); }} block={isMobile} style={isMobile ? { width: '100%', marginBottom: 8 } : undefined}>Chart Settings</Button>
              <Button type="primary" onClick={openReport} block={isMobile} style={isMobile ? { width: '100%', marginBottom: 8 } : undefined}>Generate Report</Button>
              <Button onClick={async () => { await downloadFullAnalytics(); }} block={isMobile} style={isMobile ? { width: '100%' } : undefined}>Download Full Analytics</Button>
            </div>
          </div>
        </Form.Item>
      </Form>

      <Drawer title="Chart Settings" placement="right" onClose={() => setSettingsOpen(false)} open={settingsOpen} width={420}>
        <Form layout="vertical">
          <Form.Item label="Select chart">
            <Select value={settingsChartId} onChange={(v) => setSettingsChartId(v)}>
              {Object.keys(CHARTS).map(k => <Select.Option key={k} value={k}>{CHARTS[k].title}</Select.Option>)}
            </Select>
          </Form.Item>

          <Form.Item label="Chart type">
            <Select value={(chartSettings[settingsChartId] && chartSettings[settingsChartId].chartType) || CHARTS[settingsChartId].chartType} onChange={(val) => setChartSettings(s => ({ ...s, [settingsChartId]: { ...(s[settingsChartId] || {}), chartType: val } }))}>
              <Select.Option value="pie">Pie</Select.Option>
              <Select.Option value="bar">Bar</Select.Option>
              <Select.Option value="line">Line</Select.Option>
              <Select.Option value="area">Area</Select.Option>
            </Select>
          </Form.Item>

          <Form.Item label="Date range for this chart">
            <RangePicker value={(chartSettings[settingsChartId] && chartSettings[settingsChartId].dateRange) || filters.dateRange} onChange={(dates) => setChartSettings(s => ({ ...s, [settingsChartId]: { ...(s[settingsChartId] || {}), dateRange: dates ? dates.filter(Boolean) : [] } }))} allowClear />
          </Form.Item>

          <Form.Item label="Show labels">
            <Switch checked={(chartSettings[settingsChartId] && chartSettings[settingsChartId].showLabels) ?? true} onChange={(v) => setChartSettings(s => ({ ...s, [settingsChartId]: { ...(s[settingsChartId] || {}), showLabels: v } }))} />
          </Form.Item>

          <Form.Item label="Show tooltips">
            <Switch checked={(chartSettings[settingsChartId] && chartSettings[settingsChartId].showTooltips) ?? true} onChange={(v) => setChartSettings(s => ({ ...s, [settingsChartId]: { ...(s[settingsChartId] || {}), showTooltips: v } }))} />
          </Form.Item>

          <Form.Item label="Show legend">
            <Switch checked={(chartSettings[settingsChartId] && chartSettings[settingsChartId].showLegend) ?? true} onChange={(v) => setChartSettings(s => ({ ...s, [settingsChartId]: { ...(s[settingsChartId] || {}), showLegend: v } }))} />
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" onClick={() => { setSettingsOpen(false); fetchChart(settingsChartId); }}>Apply</Button>
              <Button onClick={() => { setChartSettings(s => ({ ...s, [settingsChartId]: {} })); }}>Reset</Button>
            </Space>
          </Form.Item>
        </Form>
      </Drawer>

      <Modal title="Analytics Report" open={reportModalOpen} onCancel={() => setReportModalOpen(false)} footer={[
        <Button key="copy" onClick={copyReport}>Copy</Button>,
        <Button key="download" type="primary" onClick={downloadPdf}>Download as PDF</Button>
      ]}>
        <Typography.Paragraph>{reportText || 'No data available for report.'}</Typography.Paragraph>
      </Modal>

      {/* Top Metrics */}
      <Row gutter={[24, 24]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={8}>
          <Card variant="borderless" style={{ borderRadius: 12, boxShadow: '0 8px 20px rgba(15,15,15,0.04)', textAlign: 'left' }} styles={{ body: { padding: 16 } }}>
            {loadingSummary ? <Skeleton active paragraph={false} /> : (
              <div>
                <Typography.Text type="secondary" style={{ display: 'block', fontSize: 12 }}>Total Residents</Typography.Text>
                <Typography.Title level={3} style={{ margin: 0 }}>{totalResidents}</Typography.Title>
              </div>
            )}
          </Card>
        </Col>

        <Col xs={24} sm={12} md={8}>
          <Card variant="borderless" style={{ borderRadius: 12, boxShadow: '0 8px 20px rgba(15,15,15,0.04)', textAlign: 'left' }} styles={{ body: { padding: 16 } }}>
            {loadingSummary ? <Skeleton active paragraph={false} /> : (
              <div>
                <Typography.Text type="secondary" style={{ display: 'block', fontSize: 12 }}>Total Documents</Typography.Text>
                <Typography.Title level={3} style={{ margin: 0 }}>{totalDocuments}</Typography.Title>
              </div>
            )}
          </Card>
        </Col>

        <Col xs={24} sm={24} md={8}>
          <Card style={{ borderRadius: 12, boxShadow: '0 8px 20px rgba(15,15,15,0.04)', textAlign: 'left' }} styles={{ body: { padding: 16 } }}>
            {loadingSummary ? <Skeleton active paragraph={false} /> : (
              <div>
                <Typography.Text type="secondary" style={{ display: 'block', fontSize: 12 }}>Requests (selected period)</Typography.Text>
                <Typography.Title level={3} style={{ margin: 0 }}>{monthlyDocData ? monthlyDocData.reduce((s:any,m:any)=>s + (Number(m.value)||0), 0) : 0}</Typography.Title>
                <Typography.Text type="secondary" style={{ display: 'block', fontSize: 12, marginTop: 6 }}>{monthlyDocData && monthlyDocData.length ? `Latest: ${monthlyDocData[monthlyDocData.length-1].type} (${monthlyDocData[monthlyDocData.length-1].value})` : ''}</Typography.Text>
              </div>
            )}
          </Card>
        </Col>
      </Row>

      <Divider />
      {/* Charts */}
      {/* Show Alerts for failed chart queries */}
      {chartIds.map(id => {
    const q = chartQueriesMapMemo[id];
        if (q?.isError) {
          return (
            <Alert
              key={`alert-${id}`}
              message={`Failed to load ${CHARTS[id].title}`}
              description={<div>{String(q.error?.message || q.error)} <Button size="small" onClick={() => q.refetch()}>Retry</Button></div>}
              type="error"
              showIcon
              style={{ marginBottom: 12 }}
            />
          );
        }
        return null;
      })}
      <Row gutter={[24, 24]}>
        {selectedCharts.includes('gender') && (
          <Col xs={24} md={12} lg={12}>
            <ChartCard id="gender" title={CHARTS['gender'].title} chartType={CHARTS['gender'].chartType} data={chartData['gender']} loading={chartLoading['gender']} colors={CHARTS['gender'].colors} />
          </Col>
        )}

        {selectedCharts.includes('age') && (
          <Col xs={24} md={12} lg={12}>
            <ChartCard id="age" title={CHARTS['age'].title} chartType={CHARTS['age'].chartType} data={chartData['age']} loading={chartLoading['age']} colors={CHARTS['age'].colors} />
          </Col>
        )}

        {selectedCharts.includes('documents-monthly') && (
          <Col xs={24} md={12} lg={12}>
            <ChartCard id="documents-monthly" title={CHARTS['documents-monthly'].title} chartType={CHARTS['documents-monthly'].chartType} data={chartData['documents-monthly']} loading={chartLoading['documents-monthly']} colors={CHARTS['documents-monthly'].colors} />
          </Col>
        )}

        {selectedCharts.includes('civil-status') && (
          <Col xs={24} md={12} lg={12}>
            <ChartCard id="civil-status" title={CHARTS['civil-status'].title} chartType={CHARTS['civil-status'].chartType} data={chartData['civil-status']} loading={chartLoading['civil-status']} colors={CHARTS['civil-status'].colors} />
          </Col>
        )}

        {selectedCharts.includes('education') && (
          <Col xs={24} md={12} lg={12}>
            <ChartCard id="education" title={CHARTS['education'].title} chartType={CHARTS['education'].chartType} data={chartData['education']} loading={chartLoading['education']} colors={CHARTS['education'].colors} />
          </Col>
        )}
      </Row>
      
      </div>
  </Card>
  );
};

const Statistics: React.FC = () => {
  return (
    <QueryClientProvider client={defaultQueryClient}>
      <StatisticsInner />
    </QueryClientProvider>
  );
};

export default Statistics;

import React, { useState, useEffect, useRef } from 'react';
import {
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  FormControlLabel,
  Switch,
  Divider,
  Alert,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import TestEmailModal from '../TestEmailModal';
import { adminAPI } from '../../services/api';
import axios from 'axios';
import { API_URL } from '../../services/api';
import { UploadOutlined, UsergroupAddOutlined, DeleteOutlined } from '@ant-design/icons';
import { Upload as AntdUpload, message as antdMessage, Row, Col, Card, Form as AntdForm, Input as AntdInput, Carousel, Divider as AntdDivider, notification, Button as AntdButton } from 'antd';
import AppAvatar from '../AppAvatar';
// framer-motion removed to avoid dependency conflicts; use CSS transitions for preview
import defaultSystemSettings from '../../config/defaultSystemSettings';
import getOfficialPhotoSrc from '../../utils/officials';

interface SystemSettings {
  siteName: string;
  barangayName: string;
  barangayAddress: string;
  contactEmail: string;
  contactPhone: string;
  maintainanceMode: boolean;
  allowNewRegistrations: boolean;
  requireEmailVerification: boolean;
  enableVerifications?: boolean;
  maxDocumentRequests: number;
  documentProcessingDays: number;
  // new rate-limiting settings
  allowMultipleAccountsPerIP?: boolean;
  maxAccountsPerIP?: number;
  systemNotice: string;
  smtp?: {
    host?: string;
    port?: number;
    user?: string;
    password?: string;
  };
}

interface Official {
  _id?: string;
  name: string;
  title: string;
  term: string;
  photoUrl?: string;
  photoPath?: string;
  previewUrl?: string; // client-side temporary preview for selected file
}

const SystemSettings: React.FC = () => {
  const [settings, setSettings] = useState<SystemSettings>(() => ({ ...defaultSystemSettings } as SystemSettings));
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [confirmDisableOpen, setConfirmDisableOpen] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [testModalOpen, setTestModalOpen] = useState(false);
  // Officials state
  const [officials, setOfficials] = useState<Official[]>([]);
  const [officialsLoading, setOfficialsLoading] = useState(false);
  const [savingOfficials, setSavingOfficials] = useState(false);
  const [manualSaveError, setManualSaveError] = useState<string | null>(null);
  const autoSaveTimers = useRef<Record<string, number>>({});
  const [officialSaveStatus, setOfficialSaveStatus] = useState<Record<string, 'idle'|'saving'|'saved'|'error'>>({});
  const previewUrlsRef = useRef<Record<string, string>>({});
  const previewContainerRef = useRef<HTMLDivElement | null>(null);
  const prevOfficialsCountRef = useRef(0);
  const [highlightedIds, setHighlightedIds] = useState<string[]>([]);
  const highlightTimeouts = useRef<Record<string, number>>({});
  const originalSettingsRef = useRef<SystemSettings | null>(null);
  const [dirty, setDirty] = useState(false);

  // helper to make MUI InputLabel shrink when the field has content or a non-empty value
  const inputLabelShrink = (val: any) => ({ InputLabelProps: { shrink: val !== undefined && val !== null && val !== '' } });

  useEffect(() => {
    const ac = new AbortController();
    fetchSettings(ac.signal);
    return () => {
      // cancel pending fetch
      try { ac.abort(); } catch (e) {}
      // revoke any created object URLs
      try {
        Object.values(previewUrlsRef.current).forEach(u => {
          try { URL.revokeObjectURL(u); } catch (e) {}
        });
      } catch (e) {}
    };
  }, []);

  // Scroll preview container to the right when items are appended
  useEffect(() => {
    try {
      const container = previewContainerRef.current;
      if (!container) return;
      if (officials.length > prevOfficialsCountRef.current) {
        container.scrollTo({ left: container.scrollWidth, behavior: 'smooth' });
      }
      prevOfficialsCountRef.current = officials.length;
    } catch (e) {}
  }, [officials.length]);

  // Detect newly added temp officials and highlight them briefly
  useEffect(() => {
    try {
      const newTemp = officials.filter(o => o._id && o._id.toString().startsWith('new-')).map(o => o._id as string);
      // highlight any temp ids that are new and not already highlighted
      newTemp.forEach(id => {
        if (!highlightedIds.includes(id)) {
          setHighlightedIds(prev => [...prev, id]);
          // remove highlight after 2.5s
          const t = window.setTimeout(() => {
            setHighlightedIds(prev => prev.filter(x => x !== id));
            delete highlightTimeouts.current[id];
          }, 2500);
          highlightTimeouts.current[id] = t;
        }
      });
    } catch (e) {}
    // cleanup when unmounting
    return () => {
      Object.values(highlightTimeouts.current).forEach((tid) => { try { clearTimeout(tid); } catch (e) {} });
      highlightTimeouts.current = {};
    };
  }, [officials, highlightedIds]);

  // Accept an optional AbortSignal so caller can cancel when unmounting
  const fetchSettings = async (signal?: AbortSignal) => {
    setLoading(true);
    try {
      // primary attempt using adminAPI (uses axiosInstance and auth interceptors)
      let sys: SystemSettings | null = null;
      try {
        sys = await adminAPI.getSystemSettings();
      } catch (err) {
        // fallback: call backend directly at configured API_URL (avoid client origin)
        try {
          const res = await axios.get(`${API_URL}/admin/settings`, { withCredentials: true, signal } as any);
          if (res?.data) sys = res.data;
        } catch (err2) {
          console.warn('Failed to load system settings via adminAPI and API_URL fallback', err, err2);
        }
      }
      if (sys) {
        setSettings(sys);
        originalSettingsRef.current = sys;
      }

      // officials: try adminAPI then API_URL fallback
      setOfficialsLoading(true);
      try {
        const offs = await adminAPI.getOfficials();
        if (Array.isArray(offs)) {
          setOfficials(offs);
        } else {
          const r = await axios.get(`${API_URL}/admin/officials`, { withCredentials: true, signal } as any);
          if (r?.data) setOfficials(r.data);
        }
      } catch (err) {
        try {
          const r = await axios.get(`${API_URL}/admin/officials`, { withCredentials: true, signal } as any);
          if (r?.data) setOfficials(r.data);
        } catch (err2) {
          console.warn('Failed to load officials via adminAPI and API_URL fallback', err, err2);
        }
      } finally {
        setOfficialsLoading(false);
      }
    } catch (err) {
      if ((err as any)?.name === 'CanceledError' || (err as any)?.name === 'AbortError') {
        // fetch canceled, ignore
        return;
      }
      console.error('Unexpected error in fetchSettings', err);
      antdMessage.error('Unexpected error while loading settings');
    } finally {
      setLoading(false);
    }
  };

  // Save system settings (used by Save Changes button)
  // Internal save implementation (performs actual API call)
  const performSave = async () => {
    try {
      setSaving(true);
      setError(null);
      // Normalize numeric fields and map client keys to server-side field names
      const payload: any = {
        ...settings,
        // compatibility: server uses 'maintenanceMode' while client uses 'maintainanceMode' (typo)
        maintenanceMode: (settings as any).maintainanceMode,
        // server expects allowRegistrations and maxDocumentRequestsPerUser
        allowRegistrations: (settings as any).allowNewRegistrations,
        maxDocumentRequestsPerUser: Number((settings as any).maxDocumentRequests) || 1,
        documentProcessingDays: Number(settings.documentProcessingDays) || 1,
        ...(typeof (settings as any).maxAccountsPerIP !== 'undefined'
          ? { maxAccountsPerIP: Number((settings as any).maxAccountsPerIP) || 1 }
          : {}),
      } as SystemSettings;

      await adminAPI.updateSystemSettings(payload);
      // optimistic: update original copy and clear dirty flag
      originalSettingsRef.current = payload;
      setDirty(false);
      setSuccess(true);
      antdMessage.success('Settings saved');
    } catch (err) {
      console.error('Failed to save settings', err);
      setError('Failed to save settings');
      antdMessage.error('Failed to save settings');
    } finally {
      setSaving(false);
      window.setTimeout(() => setSuccess(false), 2000);
    }
  };

  // Public handler invoked by Save button. If the admin is disabling verifications
  // (enableVerifications toggled from true -> false), show a confirmation dialog
  // to prevent accidental destructive cleanup on the server.
  const handleSave = async () => {
    try {
      // Determine if we are flipping enableVerifications from true -> false
      const prev = originalSettingsRef.current;
      const prevEnabled = prev ? Boolean((prev as any).enableVerifications) : true;
      const nowEnabled = Boolean((settings as any).enableVerifications);
      if (prevEnabled && nowEnabled === false) {
        // Show confirmation dialog
        setConfirmDisableOpen(true);
        return;
      }
      // Otherwise proceed directly
      await performSave();
    } catch (e) {
      console.error('handleSave error', e);
    }
  };

  const confirmAndSave = async () => {
    setConfirmDisableOpen(false);
    await performSave();
  };

  // Manual save for officials (fallback)
  const handleManualSaveOfficials = async () => {
    if (!officials || officials.length === 0) return;
    setSavingOfficials(true);
    setManualSaveError(null);
    try {
      const updatedOfficials: Official[] = [];
      for (const off of officials) {
        if (!off._id || off._id.toString().startsWith('new-')) {
          // create
          const created = await adminAPI.createOfficial({ name: off.name, title: off.title, term: off.term });
          updatedOfficials.push(created);
        } else {
          try {
            const updated = await adminAPI.updateOfficial(off._id!, { name: off.name, title: off.title, term: off.term });
            updatedOfficials.push(updated);
          } catch (e) {
            // if update failed, keep local copy so user can retry
            updatedOfficials.push(off);
          }
        }
      }
      // replace list with refreshed items from server if possible
      try {
        const refreshed = await adminAPI.getOfficials();
        if (Array.isArray(refreshed)) setOfficials(refreshed);
        else setOfficials(updatedOfficials);
      } catch (e) {
        setOfficials(updatedOfficials);
      }
      antdMessage.success('Officials saved');
    } catch (err) {
      console.error('Manual save officials failed', err);
      setManualSaveError('Manual save failed');
      antdMessage.error('Manual save failed');
    } finally {
      setSavingOfficials(false);
    }
  }

  // Combined save used by floating action button: save system settings first, then officials
  const saveAll = async () => {
    // prefer existing saving flags inside the individual handlers
    try {
      await handleSave();
    } catch (e) {
      // handleSave already logs and reports errors
    }
    try {
      await handleManualSaveOfficials();
    } catch (e) {
      // manual save already reports errors
    }
  }

  // Track whether settings are different from the original copy loaded from server
  useEffect(() => {
    try {
      if (!originalSettingsRef.current) {
        setDirty(false);
        return;
      }
      setDirty(JSON.stringify(originalSettingsRef.current) !== JSON.stringify(settings));
    } catch (e) {
      setDirty(false);
    }
  }, [settings]);

  const handleDeleteOfficial = async (id?: string) => {
    if (!id) return;
    setSavingOfficials(true);
    try {
      await adminAPI.deleteOfficial(id);
      setOfficials(prev => prev.filter(p => p._id !== id));
      antdMessage.success('Official deleted');
    } catch (err) {
      console.error('Delete official failed', err);
      antdMessage.error('Failed to delete official');
    } finally {
      setSavingOfficials(false);
    }
  }

  return (
    <Box sx={{
      // ensure content is pushed below the app header which writes its height to --app-header-height
      pt: 'calc(var(--app-header-height, 64px) + 24px)',
      px: 3,
      pb: 3,
    }}>
      <Paper sx={{ p: 3 }}>
        {/* Barangay Information */}
        <Typography variant="subtitle1" gutterBottom>
          Barangay Information
        </Typography>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mb: 3 }}>
          <TextField
            label="Site Name"
            value={settings.siteName}
            onChange={(e) => setSettings({ ...settings, siteName: e.target.value })}
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            fullWidth
          />
          <TextField
            label="Barangay Name"
            value={settings.barangayName}
            onChange={(e) => setSettings({ ...settings, barangayName: e.target.value })}
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            fullWidth
          />
          <TextField
            label="Barangay Address"
            value={settings.barangayAddress}
            onChange={(e) => setSettings({ ...settings, barangayAddress: e.target.value })}
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            fullWidth
            multiline
            rows={2}
          />
        </Box>

        <Divider sx={{ my: 3 }} />
        {/* SMTP / Email Settings */}
        <Typography variant="subtitle1" gutterBottom>
          SMTP / Email Settings
        </Typography>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mb: 3 }}>
          <TextField
            label="SMTP Host"
            value={(settings as any).smtp?.host || ''}
            onChange={(e) => setSettings((prev) => ({ ...(prev as any), smtp: { ...(prev as any).smtp, host: e.target.value } }) as SystemSettings)}
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            fullWidth
          />
          <TextField
            label="SMTP Port"
            type="number"
            value={(settings as any).smtp?.port || ''}
            onChange={(e) => setSettings((prev) => ({ ...(prev as any), smtp: { ...(prev as any).smtp, port: parseInt(e.target.value || '0') } }) as SystemSettings)}
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            fullWidth
          />
          <TextField
            label="SMTP User"
            value={(settings as any).smtp?.user || ''}
            onChange={(e) => setSettings((prev) => ({ ...(prev as any), smtp: { ...(prev as any).smtp, user: e.target.value } }) as SystemSettings)}
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            fullWidth
          />
          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button variant="outlined" onClick={() => setTestModalOpen(true)}>Send Test Email</Button>
            <Button variant="text" onClick={() => setSettings((prev) => ({ ...(prev as any), smtp: { ...(prev as any).smtp, password: '' } }) as SystemSettings)}>Clear SMTP Password</Button>
          </Box>
        </Box>

        <TestEmailModal open={testModalOpen} onClose={() => setTestModalOpen(false)} contactEmail={settings.contactEmail} />

          <Divider sx={{ my: 3 }} />
          {/* Verification Settings */}
          <Typography variant="subtitle1" gutterBottom>
            Resident Verifications
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
            <FormControlLabel
              control={
                <Switch
                  checked={Boolean((settings as any).enableVerifications)}
                  onChange={(e) => setSettings((prev) => ({ ...(prev as any), enableVerifications: e.target.checked } as any))}
                />
              }
              label="Enable Resident Verifications"
            />
            <Button variant="text" onClick={() => antdMessage.info('When disabled, all pending verification requests will be deleted on the server.')}>More info</Button>
          </Box>
          {(settings as any).enableVerifications === false && (
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}> 
              <Alert severity="warning">Disabling verifications will permanently delete pending verification requests and uploaded files when you save changes.</Alert>
              {/* If settings haven't been loaded from server yet, indicate the default state */}
              {!loading && !originalSettingsRef.current && (
                <Alert severity="info">Resident verifications are currently disabled by default. Enable them to allow residents to submit verification requests.</Alert>
              )}
            </Box>
          )}

          <Divider sx={{ my: 3 }} />
          {/* Barangay Officials Section */}
          <Typography variant="subtitle1" gutterBottom>
            Barangay Officials
          </Typography>
          <Box sx={{ display: 'flex', gap: 3, alignItems: 'flex-start', mb: 3, flexDirection: { xs: 'column', md: 'row' } }}>
            {/* Left column: header + scrollable list */}
            <Box sx={{ flex: 1, width: '100%' }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
                <Typography variant="body1">Manage officials (auto-saves changes)</Typography>
                <Button onClick={() => {
                  // add new (append to end so preview shows on the right)
                  const temp: Official = { _id: `new-${Date.now()}`, name: '', title: '', term: '' };
                  setOfficials(prev => [...prev, temp]);
                }} startIcon={<UsergroupAddOutlined />}>Add Official</Button>
              </Box>

              {/* Scrollable list area */}
              <Box sx={{ maxHeight: 420, overflowY: 'auto', pr: 1, border: '1px solid rgba(0,0,0,0.04)', borderRadius: 1, boxShadow: 'inset 0 1px 0 rgba(255,255,255,0.6)' }}>
                {officialsLoading ? <CircularProgress size={24} /> : (
                  <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                    {officials.map((off, idx) => (
                      <Paper key={off._id || idx} sx={{ p: 2, display: 'flex', gap: 2, alignItems: 'center' }}>
                      <Box sx={{ width: 72, height: 72, borderRadius: '50%', overflow: 'hidden', flexShrink: 0, border: '3px solid rgba(0,0,0,0.04)', boxShadow: '0 1px 6px rgba(0,0,0,0.06)' }}>
                        <img
                          src={getOfficialPhotoSrc(off as any)}
                          alt={off.name || 'photo'}
                          style={{ width: '100%', height: '100%', objectFit: 'cover', transition: 'transform .2s', display: 'block' }}
                        />
                      </Box>
                      <Box sx={{ flex: 1 }}>
                        <TextField label="Full name" value={off.name} onChange={(e) => {
                          const v = e.target.value;
                          setOfficials(prev => prev.map(o => o._id === off._id ? { ...o, name: v } : o));
                          // schedule autosave
                          if (autoSaveTimers.current[off._id || '']) clearTimeout(autoSaveTimers.current[off._id || '']);
                          autoSaveTimers.current[off._id || ''] = window.setTimeout(async () => {
                            try {
                              setSavingOfficials(true);
                              if (off._id && !off._id.toString().startsWith('new-')) {
                                await adminAPI.updateOfficial(off._id, { ...off, name: v });
                              } else {
                                const created = await adminAPI.createOfficial({ ...off, name: v });
                                setOfficials(prev => prev.map(p => p._id === off._id ? created : p));
                              }
                              setManualSaveError(null);
                            } catch (err) {
                              console.error('auto-save failed', err);
                              setManualSaveError('Auto-save failed');
                            } finally {
                              setSavingOfficials(false);
                            }
                          }, 900);
                        }} fullWidth size="small" sx={{ mb: 1 }} variant="outlined" InputLabelProps={{ shrink: true }} />
                        <TextField label="Title/Position" value={off.title} onChange={(e) => {
                          const v = e.target.value;
                          setOfficials(prev => prev.map(o => o._id === off._id ? { ...o, title: v } : o));
                          if (autoSaveTimers.current[off._id || '']) clearTimeout(autoSaveTimers.current[off._id || '']);
                          autoSaveTimers.current[off._id || ''] = window.setTimeout(async () => {
                            try {
                              setSavingOfficials(true);
                              if (off._id && !off._id.toString().startsWith('new-')) {
                                await adminAPI.updateOfficial(off._id, { ...off, title: v });
                              } else {
                                const created = await adminAPI.createOfficial({ ...off, title: v });
                                setOfficials(prev => prev.map(p => p._id === off._id ? created : p));
                              }
                              setManualSaveError(null);
                            } catch (err) {
                              console.error('auto-save failed', err);
                              setManualSaveError('Auto-save failed');
                            } finally {
                              setSavingOfficials(false);
                            }
                          }, 900);
                        }} fullWidth size="small" sx={{ mb: 1 }} variant="outlined" InputLabelProps={{ shrink: true }} />
                        <TextField label="Term" value={off.term} onChange={(e) => {
                          const v = e.target.value;
                          setOfficials(prev => prev.map(o => o._id === off._id ? { ...o, term: v } : o));
                          if (autoSaveTimers.current[off._id || '']) clearTimeout(autoSaveTimers.current[off._id || '']);
                          autoSaveTimers.current[off._id || ''] = window.setTimeout(async () => {
                            try {
                              setSavingOfficials(true);
                              if (off._id && !off._id.toString().startsWith('new-')) {
                                await adminAPI.updateOfficial(off._id, { ...off, term: v });
                              } else {
                                const created = await adminAPI.createOfficial({ ...off, term: v });
                                setOfficials(prev => prev.map(p => p._id === off._id ? created : p));
                              }
                              setManualSaveError(null);
                            } catch (err) {
                              console.error('auto-save failed', err);
                              setManualSaveError('Auto-save failed');
                            } finally {
                              setSavingOfficials(false);
                            }
                          }, 900);
                        }} fullWidth size="small" variant="outlined" InputLabelProps={{ shrink: true }} />
                        <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                          <AntdUpload showUploadList={false} beforeUpload={(file) => {
                            // Client-side file size limit: 2MB (match server multer limit)
                            const MAX_BYTES = 2 * 1024 * 1024;
                            if (file.size > MAX_BYTES) {
                              antdMessage.warning('File is too large. Maximum allowed size is 2 MB.');
                              return false;
                            }
                            // create local preview immediately
                            try {
                              const url = URL.createObjectURL(file);
                              previewUrlsRef.current[off._id || `temp-${Date.now()}`] = url;
                              setOfficials(prev => prev.map(o => o._id === off._id ? { ...o, previewUrl: url } : o));
                            } catch (e) {}

                            // upload file
                            (async () => {
                              try {
                                if (!off._id || off._id.toString().startsWith('new-')) {
                                  antdMessage.warning('Please save official first by entering name/title/term');
                                  return;
                                }
                                await adminAPI.uploadOfficialPhoto(off._id, file as File);
                                // refresh list
                                const refreshed = await adminAPI.getOfficials();
                                setOfficials(Array.isArray(refreshed) ? refreshed : officials);
                                antdMessage.success('Photo uploaded');
                                // revoke preview for this official if any
                                try {
                                  const key = off._id || '';
                                  const u = previewUrlsRef.current[key];
                                  if (u) { URL.revokeObjectURL(u); delete previewUrlsRef.current[key]; }
                                } catch (e) {}
                              } catch (err) {
                                console.error('upload failed', err);
                                antdMessage.error('Upload failed');
                              }
                            })();
                            return false; }}>

                            <Button startIcon={<UploadOutlined />}>Upload Photo</Button>
                          </AntdUpload>
                          <Button color="error" startIcon={<DeleteOutlined />} onClick={() => {
                            if (off._id && !off._id.toString().startsWith('new-')) {
                              handleDeleteOfficial(off._id);
                            } else {
                              setOfficials(prev => prev.filter(p => p._id !== off._id));
                            }
                          }}>Delete</Button>
                        </Box>
                      </Box>
                    </Paper>
                  ))}
                </Box>
              )}
            </Box>
          </Box>

            <Box sx={{ width: { xs: '100%', md: 360 }, position: { xs: 'relative', md: 'sticky' }, top: { md: 24 }, alignSelf: 'flex-start', mt: { xs: 2, md: 0 } }}>
              <Typography variant="body2" sx={{ mb: 1 }}>Officials Preview</Typography>
              <Box sx={{ background: 'white', borderRadius: 2, p: 2 }}>
                {officials.length === 0 ? (
                  <Typography variant="body2" color="text.secondary">No officials to preview</Typography>
                ) : (
                  (() => {
                    const stable = officials.filter(o => !(o._id && o._id.toString().startsWith('new-')));
                    const newly = officials.filter(o => o._id && o._id.toString().startsWith('new-'));
                    const previewOrder = [...stable, ...newly];
                    return (
                      <div
                        ref={previewContainerRef}
                        style={{ display: 'flex', gap: 12, overflowX: 'auto', padding: '6px 4px', scrollSnapType: 'x mandatory' }}
                        aria-label="Officials preview"
                      >
                        {previewOrder.map(off => {
                          const id = off._id || '';
                          const isHighlighted = highlightedIds.includes(id);
                          return (
                            <div
                              key={id || Math.random()}
                              style={{
                                minWidth: 140,
                                textAlign: 'center',
                                padding: 8,
                                scrollSnapAlign: 'center' as const,
                                transition: 'box-shadow .25s, transform .18s',
                                boxShadow: isHighlighted ? '0 10px 30px rgba(25,118,210,0.18)' : undefined,
                                transform: isHighlighted ? 'translateY(-4px)' : undefined,
                                borderRadius: 8,
                                background: isHighlighted ? 'linear-gradient(180deg, rgba(255,255,255,0.98), rgba(250,250,255,0.98))' : undefined,
                                flex: '0 0 auto'
                              }}
                            >
                              <AppAvatar size={80} src={getOfficialPhotoSrc(off as any)} style={{ margin: '0 auto', boxShadow: '0 6px 18px rgba(0,0,0,0.06)' }} />
                              <div style={{ marginTop: 8 }}>
                                <div style={{ fontWeight: 600, fontSize: 14 }}>{off.name || 'ΓÇö'}</div>
                                <div style={{ color: '#888', fontSize: 12 }}>{off.title}</div>
                                <div style={{ color: '#888', fontSize: 12 }}>{off.term}</div>
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    );
                  })()
                )}
              </Box>
            </Box>
          </Box>

          {/* Manual Save handled via floating action button ΓÇö errors still shown inline */}
          {manualSaveError && <Typography color="error" sx={{ mt: 1 }}>{manualSaveError}</Typography>}

        {/* Contact Information */}
        <Typography variant="subtitle1" gutterBottom>
          Contact Information
        </Typography>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mb: 3 }}>
          <TextField
            label="Contact Email"
            type="email"
            value={settings.contactEmail}
            onChange={(e) => setSettings({ ...settings, contactEmail: e.target.value })}
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            fullWidth
          />
          <TextField
            label="Contact Phone"
            value={settings.contactPhone}
            onChange={(e) => setSettings({ ...settings, contactPhone: e.target.value })}
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            fullWidth
          />
        </Box>

        <Divider sx={{ my: 3 }} />

        {/* System Configuration */}
        <Typography variant="subtitle1" gutterBottom>
          System Configuration
        </Typography>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mb: 3 }}>
          <FormControlLabel
            control={
              <Switch
                checked={settings.maintainanceMode}
                onChange={(e) => setSettings({ ...settings, maintainanceMode: e.target.checked })}
              />
            }
            label="Maintenance Mode"
          />
          <FormControlLabel
            control={
              <Switch
                checked={settings.allowNewRegistrations}
                onChange={(e) => setSettings({ ...settings, allowNewRegistrations: e.target.checked })}
              />
            }
            label="Allow New Registrations"
          />
          {/* Allow multiple accounts per IP toggle and limit */}
          <Box>
            <FormControlLabel
              control={
                <Switch
                  checked={(settings as any).allowMultipleAccountsPerIP}
                  onChange={(e) => setSettings({ ...settings, allowMultipleAccountsPerIP: e.target.checked } as SystemSettings)}
                />
              }
              label="Allow multiple accounts per IP"
            />
            <Box sx={{ mt: 1, ml: 4, display: 'flex', alignItems: 'center', gap: 2 }}>
              <TextField
                label="Max accounts per IP"
                type="number"
                size="small"
                value={(settings as any).maxAccountsPerIP ?? 1}
                onChange={(e) => setSettings({ ...settings, maxAccountsPerIP: parseInt(e.target.value || '1') } as SystemSettings)}
                variant="outlined"
                InputLabelProps={{ shrink: true }}
                inputProps={{ min: 1, max: 100 }}
                sx={{ width: 140 }}
              />
              <Typography variant="caption" color="text.secondary">
                Current limit: {(settings as any).maxAccountsPerIP ?? 1}
              </Typography>
            </Box>
          </Box>
          <FormControlLabel
            control={
              <Switch
                checked={settings.requireEmailVerification}
                onChange={(e) => setSettings({ ...settings, requireEmailVerification: e.target.checked })}
              />
            }
            label="Require Email Verification"
          />
          <TextField
            label="Maximum Document Requests per User"
            type="number"
            value={settings.maxDocumentRequests}
            onChange={(e) => setSettings({ ...settings, maxDocumentRequests: parseInt(e.target.value) })}
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            inputProps={{ min: 1, max: 20 }}
          />
          <TextField
            label="Document Processing Days"
            type="number"
            value={settings.documentProcessingDays}
            onChange={(e) => setSettings({ ...settings, documentProcessingDays: parseInt(e.target.value) })}
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            inputProps={{ min: 1, max: 30 }}
            helperText="Standard number of days to process document requests"
          />
        </Box>

        <Divider sx={{ my: 3 }} />

        {/* System Notice */}
        <Typography variant="subtitle1" gutterBottom>
          System Notice
        </Typography>
        <Box sx={{ mb: 3 }}>
          <TextField
            label="System-wide Notice"
            value={settings.systemNotice}
            onChange={(e) => setSettings({ ...settings, systemNotice: e.target.value })}
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            fullWidth
            multiline
            rows={3}
            helperText="This notice will be displayed to all users"
          />
        </Box>

        {/* Save button relocated to floating action button */}

        {/* Floating Manual Save button (bottom-right) */}
        <Box sx={{
          position: 'fixed',
          right: 24,
          bottom: 24,
          zIndex: 1300,
        }}>
          <Button
            variant="contained"
            onClick={() => saveAll()}
            disabled={saving || savingOfficials}
            sx={{
              width: 64,
              height: 64,
              borderRadius: '50%',
              minWidth: 64,
              boxShadow: '0 8px 24px rgba(25,118,210,0.24)',
              backgroundColor: '#1976d2',
              color: '#fff',
              '&:hover': {
                boxShadow: '0 12px 32px rgba(25,118,210,0.32)',
                backgroundColor: '#1565c0',
              },
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontWeight: 600,
            }}
            aria-label="Save Settings and Officials"
          >
            {(saving || savingOfficials) ? '...' : 'Save'}
          </Button>
        </Box>
      </Paper>
      <Dialog open={confirmDisableOpen} onClose={() => setConfirmDisableOpen(false)}>
        <DialogTitle>Disable Resident Verifications?</DialogTitle>
        <DialogContent>
          <Typography>
            Disabling resident verifications will permanently delete all pending verification requests and their uploaded files on the server. This action cannot be undone. Are you sure you want to proceed?
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setConfirmDisableOpen(false)} disabled={saving}>Cancel</Button>
          <Button onClick={() => confirmAndSave()} color="error" disabled={saving}>Yes, disable and save</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default SystemSettings;

import React, { useState, useEffect } from 'react';
import {
  Card,
  Table,
  Button,
  Space,
  Tag,
  Typography,
