import React, { useEffect, useState } from 'react';
import Profile from '../../components/Profile';
import { axiosInstance } from '../../services/api';
import { useAuth } from '../../contexts/AuthContext';

const AdminProfilePage: React.FC = () => {
  const { user } = useAuth();
  const [profile, setProfile] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    // Replace with API call that uses runtime-aware axiosInstance
    axiosInstance.get('/admin/profile')
      .then(resp => {
        setProfile(resp.data);
        setError(null);
      })
      .catch(() => setError('Failed to load profile'))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div className="p-8 text-center text-gray-400">Loading profile...</div>;
  if (error) return <div className="p-8 text-center text-red-400">{error}</div>;
  if (!profile) return <div className="p-8 text-center text-gray-400">No profile data.</div>;

  return <Profile profile={profile} onProfileUpdate={setProfile} />;
};

export default AdminProfilePage;

import React, { useState } from 'react';
import { Form, Input, Button, Card, Typography, message, Steps } from 'antd';
import { axiosPublic } from '../services/api';
import { useNavigate } from 'react-router-dom';

const ForgotPassword: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [step, setStep] = useState<'request' | 'otp' | 'reset' | 'done'>('request');
  const [contact, setContact] = useState('');
  const [otp, setOtp] = useState('');
  const [token, setToken] = useState('');
  const navigate = useNavigate();

  // Step 1: Request OTP
  const onRequest = async (values: { contact: string }) => {
    setLoading(true);
    try {
      const res = await axiosPublic.post('/auth/forgot-password', { contact: values.contact });
      const data = res.data;
      setContact(values.contact);
      setStep('otp');
      message.success('OTP sent to your email or phone.');
    } catch (error: any) {
      message.error(error.message || 'Error sending OTP');
    } finally {
      setLoading(false);
    }
  };

  // Step 2: Verify OTP
  const onVerifyOtp = async (values: { otp: string }) => {
    setLoading(true);
    try {
      const res = await axiosPublic.post('/auth/verify-otp', { contact, otp: values.otp });
      const data = res.data;
      setToken(data.token); // token for password reset
      setStep('reset');
      message.success('OTP verified. You may now reset your password.');
    } catch (error: any) {
      message.error(error.message || 'Error verifying OTP');
    } finally {
      setLoading(false);
    }
  };

  // Step 3: Reset Password
  const onResetPassword = async (values: { password: string }) => {
    setLoading(true);
    try {
      const res = await axiosPublic.post('/auth/reset-password', { token, password: values.password });
      const data = res.data;
      setStep('done');
      message.success('Password reset successful! You may now log in.');
    } catch (error: any) {
      message.error(error.message || 'Error resetting password');
    } finally {
      setLoading(false);
    }
  };

  const stepIndex = step === 'request' ? 0 : step === 'otp' ? 1 : step === 'reset' ? 2 : 3;
  return (
    <div style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', background: 'linear-gradient(135deg, #40a9ff 0%, #9254de 50%, #ff85c0 100%)' }}>
      <Card style={{ width: 400, borderRadius: 18, boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.2)' }}>
        <Typography.Title level={3} style={{ textAlign: 'center', color: '#1890ff', marginBottom: 8 }}>Forgot Password</Typography.Title>
        <Steps
          current={stepIndex}
          size="small"
          style={{ marginBottom: 24 }}
          items={[
            { title: 'Contact' },
            { title: 'OTP' },
            { title: 'Reset' },
            { title: 'Done' },
          ]}
        />
        {step === 'request' && (
          <Form layout="vertical" onFinish={onRequest} requiredMark={false}>
            <Form.Item label="Email or Phone Number" name="contact" rules={[{ required: true, message: 'Please input your email or phone number!' }]}> 
              <Input placeholder="Enter your email or phone number" size="large" />
            </Form.Item>
            <Form.Item>
              <Button type="primary" htmlType="submit" size="large" loading={loading} style={{ width: '100%' }}>
                {loading ? 'Sending OTP...' : 'Send OTP'}
              </Button>
            </Form.Item>
            <Button type="link" onClick={() => navigate('/login')} style={{ width: '100%' }}>Back to Login</Button>
          </Form>
        )}
        {step === 'otp' && (
          <Form layout="vertical" onFinish={onVerifyOtp} requiredMark={false}>
            <Typography.Text style={{ display: 'block', textAlign: 'center', marginBottom: 16 }}>
              Enter the OTP sent to your email or phone.
            </Typography.Text>
            <Form.Item label="OTP" name="otp" rules={[{ required: true, message: 'Please input the OTP!' }]}> 
              <Input placeholder="Enter OTP" size="large" maxLength={6} />
            </Form.Item>
            <Form.Item>
              <Button type="primary" htmlType="submit" size="large" loading={loading} style={{ width: '100%' }}>
                {loading ? 'Verifying...' : 'Verify OTP'}
              </Button>
            </Form.Item>
            <Button type="link" onClick={() => setStep('request')} style={{ width: '100%' }}>Back</Button>
          </Form>
        )}
        {step === 'reset' && (
          <Form layout="vertical" onFinish={onResetPassword} requiredMark={false}>
            <Typography.Text style={{ display: 'block', textAlign: 'center', marginBottom: 16 }}>
              Enter your new password.
            </Typography.Text>
            <Form.Item label="New Password" name="password" rules={[
              { required: true, message: 'Please input your new password!' },
              { min: 6, message: 'Password must be at least 6 characters' },
            ]}>
              <Input.Password placeholder="Enter new password" size="large" />
            </Form.Item>
            <Form.Item>
              <Button type="primary" htmlType="submit" size="large" loading={loading} style={{ width: '100%' }}>
                {loading ? 'Resetting...' : 'Reset Password'}
              </Button>
            </Form.Item>
          </Form>
        )}
        {step === 'done' && (
          <>
            <Typography.Text type="success" style={{ display: 'block', textAlign: 'center', marginBottom: 16 }}>
              Password reset successful! You may now log in.
            </Typography.Text>
            <Button type="primary" onClick={() => navigate('/login')} style={{ width: '100%' }}>Back to Login</Button>
          </>
        )}
      </Card>
    </div>
  );
};

export default ForgotPassword;

import React, { useEffect, useRef, useState } from 'react';
import { Layout, Card, List, Typography, Spin, Empty, message as antdMessage, Divider, Row, Col, Space, Badge, Select, Input, Collapse, Button, Tag, Upload, Popconfirm, Result, Skeleton, Tooltip } from 'antd';
import AppAvatar from '../components/AppAvatar';
import { EyeOutlined, EyeInvisibleOutlined, DeleteOutlined, DownloadOutlined, InboxOutlined, SendOutlined, MenuFoldOutlined, MenuUnfoldOutlined } from '@ant-design/icons';
import styles from './inbox.module.css';
import { InboxFilters, defaultFilterState, FilterState } from './inbox-filters';
import { contactAPI, getAbsoluteApiUrl } from '../services/api';

const Inbox: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [inquiries, setInquiries] = useState<any[]>([]);
  const [filter, setFilter] = useState<FilterState>(defaultFilterState);
  const [category, setCategory] = useState<string>('All');
  const [reply, setReply] = useState<{ [id: string]: string }>({});
  const [replyLoading, setReplyLoading] = useState<{ [id: string]: boolean }>({});
  const [selectedInquiry, setSelectedInquiry] = useState<any | null>(null);
  const [threadVisible, setThreadVisible] = useState(false);
  const [leftCollapsed, setLeftCollapsed] = useState(false);
  // Make messages list fullscreen by default
  const [listFullscreen, setListFullscreen] = useState(true);
  const [isMobile, setIsMobile] = useState<boolean>(typeof window !== 'undefined' ? window.innerWidth <= 720 : false);
  const [replyText, setReplyText] = useState<string>('');
  const [fileList, setFileList] = useState<any[]>([]);
  const [loadingThread, setLoadingThread] = useState<boolean>(false);
  // Ref for auto-scrolling message area
  const messagesEndRef = useRef<HTMLDivElement>(null);
  // read current user profile from localStorage so we can mark replies with the correct author/role
  // Auto-scroll to bottom of message area when selectedInquiry or its responses change
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [selectedInquiry, selectedInquiry && selectedInquiry.responses && selectedInquiry.responses.length]);
  const storedProfile = (() => {
    try {
      return localStorage.getItem('userProfile') ? JSON.parse(localStorage.getItem('userProfile') || 'null') : null;
    } catch (err) {
      return null;
    }
  })();

  useEffect(() => {
    setLoading(true);
    contactAPI.getMyInquiries()
      .then(res => {
        setInquiries(res || []);
      })
      .catch(() => {
        antdMessage.error('Failed to load inbox.');
      })
      .finally(() => setLoading(false));
  }, []);

  // Track mobile breakpoint and auto-collapse the list on small screens
  useEffect(() => {
    const onResize = () => {
      try {
        setIsMobile(window.innerWidth <= 720);
      } catch (e) {
        // ignore
      }
    };
    onResize();
    window.addEventListener('resize', onResize);
    return () => window.removeEventListener('resize', onResize);
  }, []);

  useEffect(() => {
    if (isMobile) {
      setLeftCollapsed(true);
      // ensure when selecting a thread the conversation shows
      setListFullscreen(false);
    } else {
      setLeftCollapsed(false);
      setListFullscreen(true);
    }
  }, [isMobile]);

  // Filtering and search logic
  const filteredInquiries = inquiries.filter(inquiry => {
    // Status filter
    let statusMatch = true;
    if (filter.status === 'pending') statusMatch = !inquiry.responses || inquiry.responses.length === 0;
    // Treat "responded" filter as "has responses OR explicitly resolved"
    if (filter.status === 'responded') statusMatch = inquiry.status === 'resolved' || (inquiry.responses && inquiry.responses.length > 0);
    if (filter.status === 'closed') statusMatch = inquiry.status === 'closed';
  // Category filter
  let categoryMatch = true;
  if (category && category !== 'All') categoryMatch = inquiry.category === category;
    // Search filter
    const search = filter.search.toLowerCase();
    const searchMatch =
      !search ||
      (inquiry.residentName && inquiry.residentName.toLowerCase().includes(search)) ||
      (inquiry.category && inquiry.category.toLowerCase().includes(search)) ||
      (inquiry.subject && inquiry.subject.toLowerCase().includes(search)) ||
      (inquiry.createdAt && new Date(inquiry.createdAt).toLocaleString().toLowerCase().includes(search));
    return statusMatch && searchMatch && categoryMatch;
  });

  // Inline reply handler (stub)
  const handleReply = async (id: string) => {
    setReplyLoading(r => ({ ...r, [id]: true }));
    try {
      // TODO: Replace with actual API call
      await new Promise(res => setTimeout(res, 800));
      setReply(r => ({ ...r, [id]: '' }));
      antdMessage.success('Reply sent!');
    } finally {
      setReplyLoading(r => ({ ...r, [id]: false }));
    }
  };

  const openThread = (inquiry: any) => {
    // simulate loading the thread
    setLoadingThread(true);
    // ensure the conversation panel is visible when opening a thread
    setListFullscreen(false);
    // keep the left conversation list collapsed on mobile when a thread is opened
    setLeftCollapsed(true);
    setTimeout(() => {
      setSelectedInquiry(inquiry);
      setLoadingThread(false);
      setThreadVisible(true);
    }, 250);
  };

  const timeAgo = (iso?: string) => {
    if (!iso) return '';
    const diff = Date.now() - new Date(iso).getTime();
    const mins = Math.floor(diff / 60000);
    if (mins < 1) return 'just now';
    if (mins < 60) return `${mins}m`;
    const hrs = Math.floor(mins / 60);
    if (hrs < 24) return `${hrs}h`;
    const days = Math.floor(hrs / 24);
    return `${days}d`;
  };

  // Lightweight replacement for AntD Comment to avoid import/type issues
  const MessageComment: React.FC<{ author?: string; avatar?: React.ReactNode; content?: React.ReactNode; datetime?: React.ReactNode; align?: 'left'|'right' }> = ({ author, avatar, content, datetime, align = 'left' }) => (
    <div style={{ display: 'flex', justifyContent: align === 'left' ? 'flex-start' : 'flex-end' }}>
      <div className={styles['message-container']}>
        {align === 'left' && avatar}
        <div>
          <div style={{ fontSize: 13, fontWeight: 600 }}>{author}</div>
          <div style={{ marginTop: 6 }}>{content}</div>
          {datetime && <div style={{ marginTop: 6, fontSize: 12, color: '#777' }}>{datetime}</div>}
        </div>
        {align === 'right' && avatar}
      </div>
    </div>
  );

  // Helper to safely get the first initial from a name-like value
  const getInitial = (val: any, fallback = '?') => {
    if (!val) return fallback;
    try {
      const s = String(val).trim();
      return s.length > 0 ? s.charAt(0).toUpperCase() : fallback;
    } catch (e) {
      return fallback;
    }
  };

  return (
  <div className={styles.inboxPage} style={{ minHeight: '100vh', background: '#f0f4f8', padding: '24px', fontFamily: 'Poppins, Arial, sans-serif', position: 'relative' }}>
      <Card
        style={{ maxWidth: 1200, margin: '0 auto', borderRadius: 18, boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.08)', padding: 0, background: '#fff' }}
              styles={{ body: { padding: 0, background: '#fff' } }}
      >
        <div style={{ padding: '24px 32px 0 32px' }}>
          <Row gutter={[12, 12]} align="middle" style={{ marginBottom: 12 }}>
            <Col xs={24} sm={6}>
              <Select value={category} onChange={(val) => setCategory(val)} style={{ width: '100%' }}>
                <Select.Option value="All">All</Select.Option>
                <Select.Option value="Complaints">Complaints</Select.Option>
                <Select.Option value="Requests">Requests</Select.Option>
                <Select.Option value="Announcements">Announcements</Select.Option>
              </Select>
            </Col>
            <Col xs={24} sm={12}>
              <Input.Search placeholder="Search by date, name, or category" allowClear value={filter.search} onChange={(e) => setFilter(s => ({ ...s, search: e.target.value }))} onSearch={(val) => setFilter(s => ({ ...s, search: val }))} />
            </Col>
            <Col xs={24} sm={6} style={{ display: 'flex', alignItems: 'center', justifyContent: 'flex-end', gap: 8 }}>
              <div style={{ flex: 1 }} />
              <Button
                type="text"
                onClick={() => setLeftCollapsed(v => !v)}
                style={{ borderRadius: 8 }}
                title={leftCollapsed ? 'Show conversation list' : 'Hide conversation list'}
                icon={leftCollapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
              />
            </Col>
          </Row>
          <Typography.Title level={3} style={{ marginBottom: 0, color: '#1890ff', display: 'flex', alignItems: 'center', gap: 8, fontFamily: 'Poppins, Arial, sans-serif' }}>
            <span role="img" aria-label="inbox">≡ƒô⌐</span> Inbox
          </Typography.Title>
        </div>
        <Divider style={{ margin: '16px 0 0 0' }} />
        <div style={{ padding: '0 32px 32px 32px' }}>
          {loading ? (
            <Spin />
          ) : filteredInquiries.length === 0 ? (
            <Result
              icon={<InboxOutlined />}
              status="info"
              title="No messages yet"
              subTitle="There are no messages in your inbox."
            />
          ) : (
            <div style={{ display: 'flex', gap: 16 }}>
            {/* Conversation list (collapsible) */}
            {!leftCollapsed ? (
              <div className={styles.conversationList} style={{ flex: listFullscreen ? 1 : '0 0 300px', maxHeight: listFullscreen ? 'calc(100vh - 220px)' : 500, overflowY: 'auto', position: 'relative' }}>
                <Button
                  type="text"
                  onClick={() => setLeftCollapsed(true)}
                  style={{ position: 'absolute', right: -12, top: -8, zIndex: 10, borderRadius: 8, background: '#fff', boxShadow: '0 2px 8px rgba(0,0,0,0.06)' }}
                  title="Collapse list"
                >
                  <MenuFoldOutlined />
                </Button>
                <List
                  itemLayout="vertical"
                  dataSource={filteredInquiries}
                  renderItem={inquiry => {
                // Prefer explicit 'resolved' status. If not resolved, fall back to checking responses.
                const statusTag = inquiry.status === 'resolved' ? (
                  <Tag color="green">Resolved</Tag>
                ) : Array.isArray(inquiry.responses) && inquiry.responses.length > 0 ? (
                  null
                ) : (
                  <Tag color="gold">Pending</Tag>
                );

                const typeIcon = (() => {
                  switch ((inquiry.category || '').toLowerCase()) {
                    case 'complaints': return '≡ƒ¢æ';
                    case 'requests': return '≡ƒô¿';
                    case 'announcements': return '≡ƒôú';
                    default: return 'Γ£ë∩╕Å';
                  }
                })();

                const isSelected = selectedInquiry && selectedInquiry._id === inquiry._id;

                    return (
                  <List.Item
                    className={styles.listItem}
                    style={{ marginBottom: 8, background: isSelected ? '#f7fbff' : '#fff', borderRadius: 12, border: '1px solid #e6edf3', boxShadow: '0 1px 4px rgba(31,38,135,0.04)', cursor: 'pointer' }}
                    onClick={() => openThread(inquiry)}
                  >
                    <div className={styles.listItemInner}>
                      <div className={styles.listItemMain}>
                        <div className={styles.listItemHeader}>
                          <span className={styles.typeIcon} aria-hidden>{typeIcon}</span>
                          <Typography.Text strong className={styles.listItemName}>{inquiry.residentName || inquiry.username || (inquiry.createdBy && (inquiry.createdBy.fullName || inquiry.createdBy.username)) || 'Unknown'}</Typography.Text>
                          <Typography.Text type="secondary" className={styles.listItemSubject}>ΓÇö {inquiry.subject || 'No subject'}</Typography.Text>
                          <span className={styles.listItemTime}>{inquiry.createdAt ? timeAgo(inquiry.createdAt) : ''}</span>
                          {statusTag ? React.cloneElement(statusTag as any, { style: { fontSize: 11, padding: '0 6px', marginLeft: 8 } }) : null}
                        </div>
                        <div className={styles.listItemPreview}>
                          <Typography.Text type="secondary" className={styles.listItemPreviewText}>{inquiry.message}</Typography.Text>
                        </div>
                        <div className={styles.listItemResponses}>
                          {Array.isArray(inquiry.responses) && inquiry.responses.length > 0 ? (
                            <Typography.Text type="secondary" style={{ fontSize: 12 }}>{inquiry.responses.length} response(s)</Typography.Text>
                          ) : (
                            <Typography.Text type="secondary" style={{ fontSize: 12 }}>No responses yet</Typography.Text>
                          )}
                        </div>
                      </div>
                      <div className={styles.listItemAvatar}>
                        {(() => {
                          const displayName = inquiry.residentName || inquiry.username || (inquiry.createdBy && (inquiry.createdBy.fullName || inquiry.createdBy.username)) || inquiry.subject || 'Unknown';
                          const initial = (displayName && displayName !== 'Unknown') ? getInitial(displayName, '?') : '?';
                          return (
                            <AppAvatar style={{ background: '#1890ff', color: '#fff', fontWeight: 700 }} size={36}>
                              {initial}
                            </AppAvatar>
                          );
                        })()}
                      </div>
                    </div>
                  </List.Item>
                );
              }}
                />
              </div>
            ) : (
              <div style={{ width: 72, maxHeight: 500, overflowY: 'auto', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 8, paddingTop: 8 }}>
                {/* collapse: show compact vertical icons similar to dashboard sidebar */}
                <Button
                  type="text"
                  onClick={() => setLeftCollapsed(false)}
                  style={{ borderRadius: 8, background: '#fff', boxShadow: '0 2px 8px rgba(0,0,0,0.06)' }}
                  title="Show list"
                >
                  <MenuUnfoldOutlined />
                </Button>

                <div style={{ width: '100%', display: 'flex', flexDirection: 'column', gap: 8, padding: 8 }}>
                  {filteredInquiries.map(inquiry => {
                    const displayName = inquiry.residentName || inquiry.username || (inquiry.createdBy && (inquiry.createdBy.fullName || inquiry.createdBy.username)) || inquiry.subject || 'Unknown';
                    const initial = (displayName && displayName !== 'Unknown') ? getInitial(displayName, '?') : '?';
                    const isSelected = selectedInquiry && selectedInquiry._id === inquiry._id;
                    return (
                      <Tooltip key={inquiry._id} title={`${displayName} ΓÇö ${inquiry.subject || 'No subject'}`} placement="right">
                        <button
                          onClick={() => openThread(inquiry)}
                          style={{
                            width: '100%',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            padding: 6,
                            borderRadius: 8,
                            border: isSelected ? '2px solid #1890ff' : '1px solid transparent',
                            background: isSelected ? '#e6f7ff' : 'transparent',
                            cursor: 'pointer'
                          }}
                          aria-label={displayName}
                        >
                          <AppAvatar style={{ background: '#1890ff', color: '#fff', fontWeight: 700 }} size={36}>{initial}</AppAvatar>
                        </button>
                      </Tooltip>
                    );
                  })}
                </div>
              </div>
            )}

            {!listFullscreen && (<div style={{ flex: 1 }}>)
              {/* Make conversation panel visually blend into the page by removing card background/shadow */}
              <Card className={styles.conversationCard}
                style={{ borderRadius: 12, height: '100%', background: 'transparent', boxShadow: 'none', border: 'none' }}
                styles={{ body: { display: 'flex', flexDirection: 'column', padding: 0, gap: 10, background: 'transparent' } }}
              >
                {selectedInquiry ? (
                  <>
                    <div style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
                      {/* Place staff/respondent info on the left and resident on the right to reflect message flow */}
                      <div style={{ display: 'flex', gap: 6, alignItems: 'center' }}>
                        {/* Removed General and Responded tags */}
                        {selectedInquiry.status === 'resolved' ? (
                          <Tag color="green">Resolved</Tag>
                        ) : (
                          <Tag color="gold">Pending</Tag>
                        )}
                      </div>
                      <div style={{ flex: 1 }}>
                        {(() => {
                          const displayNameSelected = selectedInquiry.residentName || selectedInquiry.username || selectedInquiry.subject || 'Unknown';
                          return (
                            <>
                              <Typography.Text strong style={{ display: 'block', textAlign: 'right', fontSize: 15 }}>{displayNameSelected}</Typography.Text>
                              <Typography.Text type="secondary" style={{ fontSize: 10, display: 'block', textAlign: 'right' }}>{selectedInquiry.createdAt ? new Date(selectedInquiry.createdAt).toLocaleString() : ''}</Typography.Text>
                            </>
                          );
                        })()}
                      </div>
                      {(() => {
                          const displayNameSelected = selectedInquiry.residentName || selectedInquiry.username || (selectedInquiry.createdBy && (selectedInquiry.createdBy.fullName || selectedInquiry.createdBy.username)) || selectedInquiry.subject || 'Unknown';
                        const initial = (displayNameSelected && displayNameSelected !== 'Unknown') ? getInitial(displayNameSelected, '?') : '?';
                        return <AppAvatar size={40} style={{ background: '#1890ff' }}>{initial}</AppAvatar>;
                      })()}
                    </div>

                    <Divider />

                    <div style={{ overflowY: 'auto', flex: 1, display: 'flex', flexDirection: 'column', gap: 10, paddingRight: 6, minHeight: 0, maxHeight: 220 }}>
                      {/* Resident original message */}
                      <div style={{ alignSelf: 'flex-end', maxWidth: '100%' }}>
                        {(() => {
                          const originalSenderName = selectedInquiry.residentName || selectedInquiry.username || (selectedInquiry.createdBy && (selectedInquiry.createdBy.fullName || selectedInquiry.createdBy.username)) || 'Resident';
                          const originalInitial = (originalSenderName && originalSenderName !== 'Resident') ? getInitial(originalSenderName, 'U') : 'U';
                          return (
                            <MessageComment
                              author={originalSenderName}
                              avatar={<AppAvatar style={{ background: '#1890ff' }}>{originalInitial}</AppAvatar>}
                              content={<div style={{ background: '#e6f7ff', padding: 10, borderRadius: 8 }}>
                                <Typography.Paragraph style={{ margin: 0 }}>{selectedInquiry.message}</Typography.Paragraph>
                                {selectedInquiry.attachments && selectedInquiry.attachments.length > 0 && (
                                  <div style={{ marginTop: 8, display: 'flex', flexDirection: 'column', gap: 8 }}>
                                    {selectedInquiry.attachments.map((a: any, i: number) => {
                                      const url = a.url || a.path || '#';
                                      const filename = a.name || a.filename || 'attachment';
                                      const isImage = (a.contentType && a.contentType.startsWith('image/')) || /\.(jpe?g|png|gif|webp)$/i.test(filename);
                                      return (
                                            <div key={i}>
                                              {isImage ? (
                                                <a href={typeof url === 'string' && !url.startsWith('http') ? getAbsoluteApiUrl(url) : url} target="_blank" rel="noreferrer">
                                                  <img src={typeof url === 'string' && !url.startsWith('http') ? getAbsoluteApiUrl(url) : url} alt={filename} className={styles.chatImage} />
                                                </a>
                                              ) : (
                                                <div>
                                                  <a href={url} target="_blank" rel="noreferrer">{filename}</a>
                                                  {a.size ? <span style={{ marginLeft: 8, color: '#888', fontSize: 12 }}>({Math.round(a.size/1024)} KB)</span> : null}
                                                </div>
                                              )}
                                            </div>
                                      );
                                    })}
                                  </div>
                                )}
                              </div>}
                              datetime={<span style={{ fontSize: 12 }}>{selectedInquiry.createdAt ? new Date(selectedInquiry.createdAt).toLocaleString() : ''}</span>}
                              align="right"
                            />
                          );
                        })()}
                      </div>

                      {/* Staff responses */}
                      {Array.isArray(selectedInquiry.responses) && selectedInquiry.responses.map((r: any, idx: number) => {
                        // Support multiple possible field shapes from server/client.
                        // Primary: trust an explicit role field if present. Fallback: try to detect resident replies
                        // by comparing createdBy id or author name to the selected inquiry's resident info.
                        const roleRaw = r.role || r.authorRole || r.author_role || '';
                        let role = (roleRaw || '').toString().toLowerCase();

                        // Prefer explicit author name provided by the server (authorName).
                        // Also accept several common alias fields that servers sometimes use.
                        // Only fall back to 'You' when the response was created by the current user.
                        const authorRaw = r.author || r.authorName || r.author_name || r.createdByName || r.createdByFullName || r.userName || r.user || '';
                        const createdById = r.createdBy ? String(r.createdBy) : undefined;
                        const currentUserId = storedProfile && (storedProfile._id || storedProfile.id) ? String(storedProfile._id || storedProfile.id) : undefined;

                        // If role is missing, try a few heuristics to detect resident replies
                        if (!role) {
                          try {
                            // resident id may be stored in selectedInquiry.createdBy (object or id) or selectedInquiry.residentId
                            let residentId: string | undefined;
                            if (selectedInquiry) {
                              if (selectedInquiry.createdBy && typeof selectedInquiry.createdBy === 'string') residentId = String(selectedInquiry.createdBy);
                              else if (selectedInquiry.createdBy && typeof selectedInquiry.createdBy === 'object' && (selectedInquiry.createdBy._id || selectedInquiry.createdBy.id)) residentId = String(selectedInquiry.createdBy._id || selectedInquiry.createdBy.id);
                              else if (selectedInquiry.residentId) residentId = String(selectedInquiry.residentId);
                            }

                            const residentName = (selectedInquiry && (selectedInquiry.residentName || selectedInquiry.username || '')) ? String(selectedInquiry.residentName || selectedInquiry.username).toLowerCase() : '';
                            const authorName = (authorRaw || r.authorName || '').toString().toLowerCase();

                            if (residentId && createdById && residentId === createdById) {
                              role = 'resident';
                            } else if (residentName && authorName && residentName === authorName) {
                              role = 'resident';
                            }
                          } catch (e) {
                            // ignore heuristic errors and fall back to existing logic
                          }
                        }

                        let authorDisplay: string;
                        if (authorRaw && authorRaw.trim() !== '') {
                          authorDisplay = authorRaw;
                        } else if (currentUserId && createdById && currentUserId === createdById) {
                          authorDisplay = 'You';
                        } else if (r.authorName && r.authorName.trim() !== '') {
                          authorDisplay = r.authorName;
                        } else if (r.createdBy && typeof r.createdBy === 'object' && (r.createdBy.fullName || r.createdBy.username)) {
                          authorDisplay = r.createdBy.fullName || r.createdBy.username;
                        } else if (r.createdByName && typeof r.createdByName === 'string' && r.createdByName.trim() !== '') {
                          authorDisplay = r.createdByName;
                        } else if (r.user && typeof r.user === 'object' && (r.user.fullName || r.user.username)) {
                          authorDisplay = r.user.fullName || r.user.username;
                        } else {
                          // Reasonable fallback depending on role. If this response appears to be from the resident
                          // prefer the selected inquiry's resident name / username so the UI shows the sender's name.
                          if (role === 'resident') {
                            authorDisplay = (selectedInquiry && (selectedInquiry.residentName || selectedInquiry.username)) ? (selectedInquiry.residentName || selectedInquiry.username) : 'Resident';
                          } else {
                            // Try common places for staff name before falling back to 'Staff'
                            // If responses don't include authorName but the inquiry has assignedTo populated,
                            // try to match the response.createdBy id to an assigned staff to show their name.
                            let staffNameFromAssigned: string | undefined;
                            try {
                              if (createdById && selectedInquiry && Array.isArray(selectedInquiry.assignedTo)) {
                                const match = selectedInquiry.assignedTo.find((a: any) => String(a._id) === String(createdById));
                                if (match) staffNameFromAssigned = match.fullName || match.username;
                              }
                            } catch (e) {
                              // ignore
                            }

                            authorDisplay = r.staffName || r.staffFullName || (r.createdBy && typeof r.createdBy === 'object' && (r.createdBy.fullName || r.createdBy.username)) || staffNameFromAssigned || 'Staff';
                          }
                        }

                        // Determine alignment: replies from the resident (or the current user) are shown on the right; staff on the left.
                        const isResidentReply = role === 'resident' || role === 'user';
                        const isCurrentUser = currentUserId && createdById && currentUserId === createdById;
                        const align: 'left' | 'right' = (isResidentReply || isCurrentUser) ? 'right' : 'left';

                        const avatarNode = (isResidentReply || isCurrentUser) ? (
                          <AppAvatar style={{ background: '#1890ff' }}>{getInitial(authorDisplay, 'U')}</AppAvatar>
                        ) : (
                          <AppAvatar style={{ background: '#666' }}>{getInitial(authorDisplay, 'B')}</AppAvatar>
                        );

                        return (
                          <div key={idx} style={{ alignSelf: (isResidentReply || isCurrentUser) ? 'flex-end' : 'flex-start', maxWidth: '100%' }}>
                            <MessageComment
                              author={isCurrentUser ? 'You' : authorDisplay}
                              avatar={avatarNode}
                              content={<div style={{ background: (isResidentReply || isCurrentUser) ? '#e6f7ff' : '#f6f6f6', padding: 10, borderRadius: 8 }}>
                                <Typography.Paragraph style={{ margin: 0 }}>{r.text}</Typography.Paragraph>
                                {r.attachments && r.attachments.length > 0 && (
                                  <div style={{ marginTop: 8, display: 'flex', flexDirection: 'column', gap: 8 }}>
                                    {r.attachments.map((a: any, i: number) => {
                                      const url = a.url || a.path || '#';
                                      const filename = a.name || a.filename || 'attachment';
                                      const isImage = (a.contentType && a.contentType.startsWith('image/')) || /\.(jpe?g|png|gif|webp)$/i.test(filename);
                                      return (
                                        <div key={i}>
                                              {isImage ? (
                                                  <a href={typeof url === 'string' && !url.startsWith('http') ? getAbsoluteApiUrl(url) : url} target="_blank" rel="noreferrer">
                                                    <img src={typeof url === 'string' && !url.startsWith('http') ? getAbsoluteApiUrl(url) : url} alt={filename} className={styles.chatImage} />
                                                  </a>
                                              ) : (
                                            <div>
                                              <a href={url} target="_blank" rel="noreferrer">{filename}</a>
                                              {a.size ? <span style={{ marginLeft: 8, color: '#888', fontSize: 12 }}>({Math.round(a.size/1024)} KB)</span> : null}
                                            </div>
                                              )}
                                            </div>
                                      );
                                    })}
                                  </div>
                                )}
                              </div>}
                              datetime={<span style={{ fontSize: 12 }}>{r.createdAt ? new Date(r.createdAt).toLocaleString() : ''}</span>}
                              align={align}
                            />
                          </div>
                        );
                      })}
                      <div ref={messagesEndRef} />
                    </div>

                    <div style={{ marginTop: 0, flexShrink: 0 }} className={styles.replyBarWrap}>
                      {/* Reply bar fixed at bottom of the conversation card */}
                      <div className={styles.replyBar} style={{ background: '#fff', paddingTop: 4, paddingBottom: 4, borderTop: '1px solid #f0f0f0', display: 'flex', alignItems: 'flex-end', gap: 8 }}>
                        {/* Auto-resizing textarea implemented with a controlled textarea and a simple resize handler */}
                        <textarea
                          value={replyText}
                          onChange={e => {
                            setReplyText(e.target.value);
                            // auto-resize
                            const el = e.target as HTMLTextAreaElement;
                            el.style.height = 'auto';
                            el.style.height = Math.min(200, el.scrollHeight) + 'px';
                          }}
                          onKeyDown={async (e) => {
                            if (e.key === 'Enter' && !e.shiftKey) {
                              e.preventDefault();
                              // send
                              if (!replyText || replyText.trim() === '') return antdMessage.warning('Reply cannot be empty');
                              if (!selectedInquiry || !selectedInquiry._id) return antdMessage.error('No inquiry selected');
                              const sendingKey = selectedInquiry._id;
                              setReplyLoading(r => ({ ...r, [sendingKey]: true }));
                              try {
                                const resp = await contactAPI.respondToInquiry(selectedInquiry._id, { response: replyText.trim() });
                                const updatedInquiry = resp;
                                setInquiries(prev => prev.map(i => i._id === updatedInquiry._id ? updatedInquiry : i));
                                setSelectedInquiry(updatedInquiry);
                                setReplyText('');
                                antdMessage.success('Reply sent');
                              } catch (err) {
                                console.error('Failed to send reply', err);
                                antdMessage.error('Failed to send reply');
                              } finally {
                                setReplyLoading(r => ({ ...r, [sendingKey]: false }));
                              }
                            }
                          }}
                          placeholder="Type your reply..."
                          style={{
                            width: '100%',
                            minHeight: 40,
                            maxHeight: 200,
                            resize: 'none',
                            padding: '10px 12px',
                            borderRadius: 12,
                            border: '1px solid #e6edf3',
                            boxShadow: '0 1px 4px rgba(15, 23, 42, 0.04)',
                            fontSize: 14,
                            outline: 'none',
                            fontFamily: 'Poppins, Arial, sans-serif',
                            color: '#222'
                          }}
                        />

                        <div style={{ display: 'flex', alignItems: 'center' }}>
                          <button
                            onClick={async () => {
                              if (!replyText || replyText.trim() === '') return antdMessage.warning('Reply cannot be empty');
                              if (!selectedInquiry || !selectedInquiry._id) return antdMessage.error('No inquiry selected');
                              const sendingKey = selectedInquiry._id;
                              setReplyLoading(r => ({ ...r, [sendingKey]: true }));
                              try {
                                const resp = await contactAPI.respondToInquiry(selectedInquiry._id, { response: replyText.trim() });
                                const updatedInquiry = resp;
                                setInquiries(prev => prev.map(i => i._id === updatedInquiry._id ? updatedInquiry : i));
                                setSelectedInquiry(updatedInquiry);
                                setReplyText('');
                                antdMessage.success('Reply sent');
                              } catch (err) {
                                console.error('Failed to send reply', err);
                                antdMessage.error('Failed to send reply');
                              } finally {
                                setReplyLoading(r => ({ ...r, [sendingKey]: false }));
                              }
                            }}
                            className={styles.sendButton}
                            style={{
                              background: '#0ea5a3',
                              color: '#fff',
                              border: 'none',
                              padding: '8px 14px',
                              borderRadius: 10,
                              cursor: 'pointer',
                              fontSize: 18,
                              fontWeight: 600,
                              boxShadow: '0 4px 10px rgba(14,165,163,0.12)',
                              display: 'flex',
                              alignItems: 'center',
                              justifyContent: 'center'
                            }}
                            title="Send Reply"
                            onMouseOver={(e) => (e.currentTarget.style.opacity = '0.95')}
                            onMouseOut={(e) => (e.currentTarget.style.opacity = '1')}
                            disabled={!!(selectedInquiry && replyLoading[selectedInquiry._id])}
                          >
                            <SendOutlined style={{ fontSize: 22, verticalAlign: 'middle' }} />
                          </button>
                        </div>
                      </div>
                    </div>
                  </>
                ) : (
                  <div style={{ textAlign: 'center', padding: 32 }}>
                    <Typography.Text type="secondary">Select a message to view the conversation</Typography.Text>
                  </div>
                )}
              </Card>
            </div>)}
            </div>
          )}
        </div>
      </Card>
    </div>
  );
};

export default Inbox;

import React, { useEffect, useRef, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { Layout, Card, List, Typography, Spin, Result, Divider, Row, Col, Space, Badge, Select, Tabs, Input, Tag, Button, Modal, message as antdMessage } from 'antd';
import AppAvatar from '../components/AppAvatar';
import styles from './staffInbox.module.css';
import { InboxOutlined, SendOutlined, CheckOutlined, PlusOutlined } from '@ant-design/icons';
import { contactAPI, adminAPI, getAbsoluteApiUrl } from '../services/api';

type FilterState = {
  status: string;
  search: string;
};

const StaffInbox: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [inquiries, setInquiries] = useState<any[]>([]);
  const [filter, setFilter] = useState<FilterState>({ status: 'all', search: '' });
  const [category, setCategory] = useState<string>('All');
  const [selectedInquiry, setSelectedInquiry] = useState<any | null>(null);
  const [replyText, setReplyText] = useState<string>('');
  const [replyLoading, setReplyLoading] = useState<{ [id: string]: boolean }>({});
  const [residentsModalVisible, setResidentsModalVisible] = useState(false);
  const [residentsLoading, setResidentsLoading] = useState(false);
  const [residents, setResidents] = useState<Array<{ label: string; username?: string; barangayID?: string }>>([]);
  const location = useLocation();
  const messagesEndRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    fetchAll();
    // If navigation state contains an inquiry to open, store it for after fetch
    const state: any = (location && (location as any).state) || {};
    if (state && state.openInquiryId) {
      // We'll try to open after loading inquiries
      const openId = state.openInquiryId;
      // Wait for fetchAll to populate inquiries, then open
      const waiter = setInterval(() => {
        const found = inquiries.find(i => i._id === openId);
        if (found) {
          setSelectedInquiry(found);
          clearInterval(waiter);
        }
      }, 200);
      // As fallback, after 3s, try fetching single inquiry from API
      setTimeout(async () => {
        const found = inquiries.find(i => i._id === openId);
        if (!found) {
          try {
            const single = await contactAPI.getInquiryById(openId);
            if (single) setSelectedInquiry(single);
          } catch (e) {
            // ignore
          }
        }
      }, 3000);
    }
  }, []);

  const fetchAll = async () => {
    setLoading(true);
    try {
      const res = await contactAPI.getAllInquiries();
      setInquiries(res || []);
    } catch (e) {
      // eslint-disable-next-line no-console
      console.error('Failed to load inquiries', e);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (messagesEndRef.current) messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
  }, [selectedInquiry, selectedInquiry && selectedInquiry.responses && selectedInquiry.responses.length]);

  const timeAgo = (iso?: string) => {
    if (!iso) return '';
    const diff = Date.now() - new Date(iso).getTime();
    const mins = Math.floor(diff / 60000);
    if (mins < 1) return 'just now';
    if (mins < 60) return `${mins}m`;
    const hrs = Math.floor(mins / 60);
    if (hrs < 24) return `${hrs}h`;
    const days = Math.floor(hrs / 24);
    return `${days}d`;
  };

  const getInitial = (val: any, fallback = '?') => {
    if (!val) return fallback;
    try { const s = String(val).trim(); return s.length ? s.charAt(0).toUpperCase() : fallback; } catch (e) { return fallback; }
  };

  // Lightweight message/comment renderer (keeps layout consistent with resident inbox)
  const MessageComment: React.FC<{ author?: string; avatar?: React.ReactNode; content?: React.ReactNode; datetime?: React.ReactNode; align?: 'left'|'right' }> = ({ author, avatar, content, datetime, align = 'left' }) => (
    <div style={{ display: 'flex', justifyContent: align === 'left' ? 'flex-start' : 'flex-end' }}>
      <div style={{ display: 'flex', gap: 8, alignItems: 'flex-start', maxWidth: '80%' }}>
        {align === 'left' && avatar}
        <div>
          <div style={{ fontSize: 13, fontWeight: 600 }}>{author}</div>
          <div style={{ marginTop: 6 }}>{content}</div>
          {datetime && <div style={{ marginTop: 6, fontSize: 12, color: '#888' }}>{datetime}</div>}
        </div>
        {align === 'right' && avatar}
      </div>
    </div>
  );

  const openThread = (inquiry: any) => {
    setSelectedInquiry(inquiry);
  };

  const handleSendReply = async () => {
    if (!replyText || !selectedInquiry || !selectedInquiry._id) return;
    const id = selectedInquiry._id;
    setReplyLoading(s => ({ ...s, [id]: true }));
    try {
      if (selectedInquiry.isNewThread) {
  // Determine recipient username/barangayID from several possible places to avoid falling
  // back to the current staff account (which would make the inquiry show up only on staff)
  let recipientUsername = selectedInquiry.residentUsername || selectedInquiry.username || selectedInquiry.userName || selectedInquiry.author || selectedInquiry.createdBy && (selectedInquiry.createdBy.username || selectedInquiry.createdBy.userName);
  let recipientBarangayID = (selectedInquiry as any).residentBarangayID || selectedInquiry.barangayID || (selectedInquiry.createdBy && selectedInquiry.createdBy.barangayID);

        // If createdBy info exists, try its username/barangayID
        if ((!recipientUsername || recipientUsername === '') && selectedInquiry.createdBy) {
          if (typeof selectedInquiry.createdBy === 'string') {
            recipientUsername = selectedInquiry.createdBy;
          } else if (selectedInquiry.createdBy.username || selectedInquiry.createdBy.userName) {
            recipientUsername = selectedInquiry.createdBy.username || selectedInquiry.createdBy.userName;
          }
          if (!recipientBarangayID && selectedInquiry.createdBy && (selectedInquiry.createdBy.barangayID)) {
            recipientBarangayID = selectedInquiry.createdBy.barangayID;
          }
        }

        // Try to resolve from the cached residents list by matching the display label if still missing
        if ((!recipientUsername || recipientUsername === '') && Array.isArray(residents) && selectedInquiry.residentName) {
          const match = residents.find(r => r.label && r.label.toString().toLowerCase() === (selectedInquiry.residentName || '').toString().toLowerCase());
          if (match) {
            recipientUsername = (match as any).username || recipientUsername;
            recipientBarangayID = (match as any).barangayID || recipientBarangayID;
          }
        }

        const payload: any = {
          type: 'staff_message',
          subject: `Message to ${selectedInquiry.residentName}`,
          message: replyText.trim(),
          // Include explicit recipient identifiers so resident's getMyInquiries will find it
          ...(recipientUsername ? { username: recipientUsername } : {}),
          ...(recipientBarangayID ? { barangayID: recipientBarangayID } : {}),
          // optional: server may accept assignedTo / assignedRole depending on API
        };

        // If we couldn't resolve a recipient for a staff-created thread, block sending and show an error
        if (!payload.username) {
          antdMessage.error('Please select a resident to send this message to.');
          // Also log for debugging
          // eslint-disable-next-line no-console
          console.warn('Blocked staff-created inquiry without recipient', { selectedInquiry, residents });
          return;
        }

        // Log payload right before submitting so we can inspect what is being sent
        // eslint-disable-next-line no-console
        console.debug('Submitting staff-created inquiry payload', payload, { selectedInquirySummary: { residentName: selectedInquiry.residentName, residentUsername: selectedInquiry.residentUsername, residentBarangayID: (selectedInquiry as any).residentBarangayID } });

        const created = await contactAPI.submitInquiry(payload);
        antdMessage.success(`Message sent to ${selectedInquiry.residentName || payload.username}`);
        // add to list and open created thread
        setInquiries(prev => [created].concat(prev));
        setSelectedInquiry(created);
        setReplyText('');
      } else {
        const resp = await contactAPI.respondToInquiry(id, { response: replyText.trim() });
        setInquiries(prev => prev.map(i => i._id === resp._id ? resp : i));
        setSelectedInquiry(resp);
        setReplyText('');
      }
    } catch (e) {
      // eslint-disable-next-line no-console
      console.error('reply failed', e);
    } finally {
      setReplyLoading(s => ({ ...s, [id]: false }));
    }
  };

  const handleResolve = async () => {
    if (!selectedInquiry || !selectedInquiry._id) return;
    const id = selectedInquiry._id;
    try {
      const resp = await contactAPI.resolveInquiry(id);
      setInquiries(prev => prev.map(i => i._id === resp._id ? resp : i));
      setSelectedInquiry(resp);
    } catch (e) {
      // eslint-disable-next-line no-console
      console.error('resolve failed', e);
    }
  };

  const filtered = inquiries.filter((inquiry) => {
    let statusMatch = true;
    if (filter.status === 'pending') statusMatch = !inquiry.responses || inquiry.responses.length === 0;
    if (filter.status === 'responded') statusMatch = inquiry.status === 'resolved' || (inquiry.responses && inquiry.responses.length > 0);
    if (filter.status === 'closed') statusMatch = inquiry.status === 'closed';
    let categoryMatch = true;
    if (category && category !== 'All') categoryMatch = inquiry.category === category;
    const search = (filter.search || '').toLowerCase();
    const searchMatch = !search || (inquiry.residentName && inquiry.residentName.toLowerCase().includes(search)) || (inquiry.subject && inquiry.subject.toLowerCase().includes(search));
    return statusMatch && categoryMatch && searchMatch;
  });

  return (
    <div style={{ minHeight: '100vh', background: '#f7f9fb', padding: 24 }}>
      <Card style={{ maxWidth: 1200, margin: '0 auto', borderRadius: 12 }} styles={{ body: { padding: 0 } }}>
        <div style={{ padding: 20 }}>
          <Row gutter={[12, 12]} align="middle">
            <Col xs={24} sm={6}>
              <Select value={category} onChange={(v) => setCategory(v)} style={{ width: '100%' }}>
                <Select.Option value="All">All</Select.Option>
                <Select.Option value="Complaints">Complaints</Select.Option>
                <Select.Option value="Requests">Requests</Select.Option>
                <Select.Option value="Announcements">Announcements</Select.Option>
              </Select>
            </Col>
            <Col xs={24} sm={12}>
              <Input.Search
                placeholder="Search by name or subject"
                allowClear
                value={filter.search}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
                  setFilter((s: FilterState) => ({ ...s, search: e.target.value }))
                }
                onSearch={(v: string) =>
                  setFilter((s: FilterState) => ({ ...s, search: v }))
                }
              />
            </Col>
            <Col xs={24} sm={6}>
              <Tabs activeKey={filter.status || 'all'} onChange={(k) => setFilter(s => ({ ...s, status: k }))}>
                <Tabs.TabPane tab="All" key="all" />
                <Tabs.TabPane tab="Pending" key="pending" />
                <Tabs.TabPane
                  tab={
                    <span>
                      Responded{' '}
                      <Button
                        type="link"
                        size="small"
                        style={{ marginLeft: 20 }}
                        icon={<PlusOutlined />}
                        onClick={(e) => {
                          // prevent tab change when clicking the icon
                          e.stopPropagation();
                          (async () => {
                            setResidentsModalVisible(true);
                            setResidentsLoading(true);
                            try {
                              const users: any[] = await adminAPI.getUsers();
                              // Heuristic: users that appear to be residents
                              const list = (users || []).filter(u => {
                                const role = (u.role || u.type || '').toString().toLowerCase();
                                return role.includes('resident') || (!role.includes('admin') && !role.includes('staff'));
                              }).map(u => ({
                                label: u.fullName || (u.firstName && u.lastName ? `${u.firstName} ${u.lastName}` : (u.username || u.email || u.name)),
                                // Be liberal in picking a username-like identifier: username, userName, email, or _id
                                username: u.username || u.userName || u.email || u._id,
                                // Barangay ID may be stored in several fields depending on user record shape
                                barangayID: u.barangayID || u.brgy || (u.barangay && (u.barangay.id || u.barangayID))
                              }));
                              setResidents(list.filter(r => r && r.label));
                            } catch (err) {
                              // fallback: empty list
                              setResidents([]);
                            } finally {
                              setResidentsLoading(false);
                            }
                          })();
                        }}
                      />
                    </span>
                  }
                  key="responded"
                />
              </Tabs>
            </Col>
          </Row>
        </div>
        <Divider style={{ margin: 0 }} />
        <Modal
          title="Residents"
          open={residentsModalVisible}
          onCancel={() => setResidentsModalVisible(false)}
          footer={null}
        >
          {residentsLoading ? <div style={{ textAlign: 'center' }}><Spin /></div> : (
            residents.length === 0 ? (
              <Typography.Text type="secondary">No residents found.</Typography.Text>
            ) : (
              <List
                dataSource={residents}
                renderItem={resident => (
                    <List.Item style={{ cursor: 'pointer' }} onClick={() => {
                    const name = resident.label;
                    const username = resident.username;
                    const barangayID = (resident as any).barangayID;
                    // Try to find an existing inquiry for this resident by username or display name
                    const found = inquiries.find(i => {
                      const rn = (i.username || i.residentName || i.createdBy && (i.createdBy.username || '') || '').toString().toLowerCase();
                      return rn && (username ? rn === (username || '').toString().toLowerCase() : rn === (name || '').toString().toLowerCase());
                    });
                    if (found) {
                      setSelectedInquiry(found);
                      setResidentsModalVisible(false);
                    } else {
                      // create a temporary thread object so staff can send a message
                      const temp: any = {
                        _id: `new-${Date.now()}`,
                        residentName: name,
                        residentUsername: username,
                        residentBarangayID: barangayID,
                        message: '',
                        responses: [],
                        isNewThread: true,
                      };
                      setSelectedInquiry(temp);
                      // debug log the temp selection to ensure username/barangayID are present
                      // eslint-disable-next-line no-console
                      console.debug('Resident selected for new thread', temp);
                      setResidentsModalVisible(false);
                    }
                  }}>
                    <List.Item.Meta
                      avatar={<AppAvatar style={{ background: '#1890ff' }}>{getInitial(resident.label)}</AppAvatar>}
                      title={resident.label}
                    />
                  </List.Item>
                )}
              />
            )
          )}
        </Modal>
        <div style={{ padding: 16 }}>
          {loading ? <Spin /> : filtered.length === 0 ? (
            <Result icon={<InboxOutlined />} title="No inquiries" subTitle="There are no inquiries to show." />
          ) : (
            <div className={styles.container}>
              <div className={styles.listPanel}>
                <List
                  dataSource={filtered}
                  renderItem={inquiry => (
                    <List.Item onClick={() => openThread(inquiry)} style={{ cursor: 'pointer', marginBottom: 8, borderRadius: 8, padding: 8, border: selectedInquiry && selectedInquiry._id === inquiry._id ? '1px solid #1890ff' : '1px solid #eee' }}>
                      <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
                        <AppAvatar style={{ background: '#1890ff' }}>{getInitial(inquiry.residentName || inquiry.username || inquiry.subject, '?')}</AppAvatar>
                        <div style={{ flex: 1, minWidth: 0 }}>
                          <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                            <Typography.Text strong style={{ overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>{inquiry.residentName || inquiry.username || 'Unknown'}</Typography.Text>
                            <Typography.Text type="secondary" style={{ fontSize: 12 }}>ΓÇö {inquiry.subject || 'No subject'}</Typography.Text>
                            <span style={{ marginLeft: 'auto', fontSize: 11 }}>{inquiry.createdAt ? timeAgo(inquiry.createdAt) : ''}</span>
                          </div>
                          <Typography.Text type="secondary" style={{ display: 'block', fontSize: 13, overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>{inquiry.message}</Typography.Text>
                        </div>
                      </div>
                    </List.Item>
                  )}
                />
              </div>

              <div className={styles.threadPanel}>
                <Card style={{ borderRadius: 8 }} styles={{ body: { display: 'flex', flexDirection: 'column', gap: 12, padding: 12 } }}>
                  {selectedInquiry ? (
                    <>
                      <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
                        <div style={{ flex: 1 }}>
                          <Typography.Text strong style={{ display: 'block' }}>{selectedInquiry.residentName || selectedInquiry.username || 'Unknown'}</Typography.Text>
                          <Typography.Text type="secondary">{selectedInquiry.createdAt ? new Date(selectedInquiry.createdAt).toLocaleString() : ''}</Typography.Text>
                        </div>
                        <AppAvatar size={48} style={{ background: '#1890ff' }}>{getInitial(selectedInquiry.residentName || selectedInquiry.username)}</AppAvatar>
                      </div>
                      <Divider />
                      <div style={{ overflowY: 'auto', maxHeight: 340 }}>
                        <div style={{ marginBottom: 8 }}>
                          <Typography.Paragraph style={{ margin: 0 }}>{selectedInquiry.message}</Typography.Paragraph>
                          {selectedInquiry.attachments && selectedInquiry.attachments.length > 0 && (
                            <div style={{ marginTop: 8, display: 'flex', flexDirection: 'column', gap: 8 }}>
                              {selectedInquiry.attachments.map((a: any, i: number) => {
                                const url = a.url || a.path || '#';
                                const filename = a.name || a.filename || 'attachment';
                                const isImage = (a.contentType && a.contentType.startsWith('image/')) || /\.(jpe?g|png|gif|webp)$/i.test(filename);
                                return (
                                  <div key={i}>
                                      {isImage ? (
                                      <a href={typeof url === 'string' && !url.startsWith('http') ? getAbsoluteApiUrl(url) : url} target="_blank" rel="noreferrer">
                                        <img src={typeof url === 'string' && !url.startsWith('http') ? getAbsoluteApiUrl(url) : url} alt={filename} style={{ maxWidth: 220, maxHeight: 140, borderRadius: 8, objectFit: 'cover', border: '1px solid #f0f0f0' }} />
                                      </a>
                                    ) : (
                                      <div>
                                        <a href={url} target="_blank" rel="noreferrer">{filename}</a>
                                        {a.size ? <span style={{ marginLeft: 8, color: '#888', fontSize: 12 }}>({Math.round(a.size/1024)} KB)</span> : null}
                                      </div>
                                    )}
                                  </div>
                                );
                              })}
                            </div>
                          )}
                        </div>
                        {Array.isArray(selectedInquiry.responses) && selectedInquiry.responses.map((r: any, idx: number) => {
                          // Determine role heuristics similar to resident inbox to calculate alignment
                          const roleRaw = r.role || r.authorRole || r.author_role || '';
                          let role = (roleRaw || '').toString().toLowerCase();
                          const authorRaw = r.author || r.authorName || r.author_name || r.createdByName || r.createdByFullName || r.userName || r.user || '';
                          const createdById = r.createdBy ? String(r.createdBy) : undefined;
                          const currentUserId = undefined; // staff view does not mark current user specially here

                          // Heuristic: if role missing, try to detect resident replies by matching selectedInquiry createdBy/residentId
                          if (!role) {
                            try {
                              let residentId: string | undefined;
                              if (selectedInquiry) {
                                if (selectedInquiry.createdBy && typeof selectedInquiry.createdBy === 'string') residentId = String(selectedInquiry.createdBy);
                                else if (selectedInquiry.createdBy && typeof selectedInquiry.createdBy === 'object' && (selectedInquiry.createdBy._id || selectedInquiry.createdBy.id)) residentId = String(selectedInquiry.createdBy._id || selectedInquiry.createdBy.id);
                                else if (selectedInquiry.residentId) residentId = String(selectedInquiry.residentId);
                              }
                              const residentName = (selectedInquiry && (selectedInquiry.residentName || selectedInquiry.username || '')) ? String(selectedInquiry.residentName || selectedInquiry.username).toLowerCase() : '';
                              const authorName = (authorRaw || r.authorName || '').toString().toLowerCase();
                              if (residentId && createdById && residentId === createdById) {
                                role = 'resident';
                              } else if (residentName && authorName && residentName === authorName) {
                                role = 'resident';
                              }
                            } catch (e) {
                              // ignore
                            }
                          }

                          let authorDisplay: string;
                          if (authorRaw && authorRaw.trim() !== '') authorDisplay = authorRaw;
                          else if (r.authorName && r.authorName.trim() !== '') authorDisplay = r.authorName;
                          else if (r.createdBy && typeof r.createdBy === 'object' && (r.createdBy.fullName || r.createdBy.username)) authorDisplay = r.createdBy.fullName || r.createdBy.username;
                          else authorDisplay = role === 'resident' ? (selectedInquiry && (selectedInquiry.residentName || selectedInquiry.username) ? (selectedInquiry.residentName || selectedInquiry.username) : 'Resident') : 'Staff';

                          const isResidentReply = role === 'resident' || role === 'user';
                          const align: 'left' | 'right' = isResidentReply ? 'right' : 'left';

                          const avatarNode = isResidentReply ? (
                            <AppAvatar style={{ background: '#1890ff' }}>{getInitial(authorDisplay, 'U')}</AppAvatar>
                          ) : (
                            <AppAvatar style={{ background: '#666' }}>{getInitial(authorDisplay, 'B')}</AppAvatar>
                          );

                          return (
                            <div key={idx} style={{ alignSelf: isResidentReply ? 'flex-end' : 'flex-start', maxWidth: '100%' }}>
                              <MessageComment
                                author={authorDisplay}
                                avatar={avatarNode}
                                content={<div style={{ background: isResidentReply ? '#e6f7ff' : '#f6f6f6', padding: 10, borderRadius: 8 }}>
                                  <Typography.Paragraph style={{ margin: 0 }}>{r.text}</Typography.Paragraph>
                                  {r.attachments && r.attachments.length > 0 && (
                                    <div style={{ marginTop: 8, display: 'flex', flexDirection: 'column', gap: 8 }}>
                                      {r.attachments.map((a: any, j: number) => {
                                        const url = a.url || a.path || '#';
                                        const filename = a.name || a.filename || 'attachment';
                                        const isImage = (a.contentType && a.contentType.startsWith('image/')) || /\.(jpe?g|png|gif|webp)$/i.test(filename);
                                        return (
                                          <div key={j}>
                                              {isImage ? (
                                              <a href={typeof url === 'string' && !url.startsWith('http') ? getAbsoluteApiUrl(url) : url} target="_blank" rel="noreferrer">
                                                <img src={typeof url === 'string' && !url.startsWith('http') ? getAbsoluteApiUrl(url) : url} alt={filename} style={{ maxWidth: 220, maxHeight: 140, borderRadius: 8, objectFit: 'cover', border: '1px solid #f0f0f0' }} />
                                              </a>
                                            ) : (
                                              <div>
                                                <a href={url} target="_blank" rel="noreferrer">{filename}</a>
                                                {a.size ? <span style={{ marginLeft: 8, color: '#888', fontSize: 12 }}>({Math.round(a.size/1024)} KB)</span> : null}
                                              </div>
                                            )}
                                          </div>
                                        );
                                      })}
                                    </div>
                                  )}
                                </div>}
                                datetime={<span style={{ fontSize: 12 }}>{r.createdAt ? new Date(r.createdAt).toLocaleString() : ''}</span>}
                                align={align}
                              />
                            </div>
                          );
                        })}
                        <div ref={messagesEndRef} />
                      </div>

                      <div className={styles.replyBar}>
                        <textarea
                          value={replyText}
                          onChange={e => {
                            setReplyText(e.target.value);
                            const el = e.target as HTMLTextAreaElement;
                            el.style.height = 'auto';
                            el.style.height = Math.min(200, el.scrollHeight) + 'px';
                          }}
                          onKeyDown={async (e) => {
                            if (e.key === 'Enter' && !e.shiftKey) {
                              e.preventDefault();
                              await handleSendReply();
                            }
                          }}
                          placeholder="Type your reply..."
                          className={styles.replyTextarea}
                        />
                        <Button type="primary" icon={<SendOutlined />} loading={!!(selectedInquiry && replyLoading[selectedInquiry._id])} onClick={handleSendReply} />
                        <Button icon={<CheckOutlined />} onClick={handleResolve}>Resolve</Button>
                      </div>
                    </>
                  ) : (
                    <div style={{ textAlign: 'center', padding: 32 }}>
                      <Typography.Text type="secondary">Select an inquiry to view details</Typography.Text>
                    </div>
                  )}
                </Card>
              </div>
            </div>
          )}
        </div>
      </Card>
    </div>
  );
};

